import React from 'react';
import { toPercentage } from '../../../helper/formatter';

function StatusCellStyling({ value }) {
  switch (value) {
    case 'Rejected':
      return (
        <span>
          <i className="fa fa-ban fa-lg" aria-hidden="true" style={{ color: '#D84039' }}></i>{' '}
          {value} &nbsp; &nbsp;{' '}
          <i
            className="fa fa-commenting fa-lg"
            aria-hidden="true"
            style={{ cursor: 'pointer' }}></i>
        </span>
      );
    case 'Pending':
      return (
        <span>
          <i
            className="fa fa-exclamation-circle fa-lg"
            style={{ color: '#E0D337' }}
            aria-hidden="true"></i>{' '}
          {value}
        </span>
      );
    case 'Approved':
      return (
        <span>
          <i
            className="fa fa-check-circle fa-lg"
            aria-hidden="true"
            style={{ color: '#59C770' }}></i>{' '}
          {value}
        </span>
      );

    default:
      return null;
  }
}

export default () => {
  const colDef = [
    {
      Header: 'MD Plan',
      accessor: 'md_plan',
      disableGroupBy: false,
      width: 50,
    },
    {
      Header: 'Scenario',
      accessor: 'scenario',
      disableGroupBy: true,
      width: 50,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} scenario`,
    },
    //Inventory
    {
      Header: 'Sell through%',
      accessor: 'sell_through_perc',
      disableGroupBy: true,
      width: 50,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      // aggregate: 'sum',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'Inventory On Hand',
      accessor: 'ioh',
      disableGroupBy: true,
      width: 70,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'Inventory On Order',
      accessor: 'ioo',
      disableGroupBy: true,
      width: 70,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    //Margin
    {
      Header: 'Margin%',
      accessor: 'margin_perc',
      disableGroupBy: true,
      width: 70,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'AUM',
      accessor: 'aum',
      disableGroupBy: true,
      width: 55,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    //Product Information
    {
      Header: 'SKU ID',
      accessor: 'sku_id',
      disableGroupBy: true,
      width: 50,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} SKU ID's`,
    },
    {
      Header: 'SKU DESC',
      accessor: 'sku_desc',
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'Department',
      accessor: 'department',
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'Category',
      accessor: 'category',
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'Brand',
      accessor: 'brand',
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `-`,
    },
    //Sales
    {
      Header: 'Store ID',
      accessor: 'store_id',
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} Store ID's`,
    },
    {
      Header: 'Actual Sales',
      accessor: 'actual_sales',
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} Store ID's`,
    },
    {
      Header: 'Predicted Sales',
      accessor: 'predicted_sales',
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} Store ID's`,
    },
    {
      Header: 'Sales Contribution',
      accessor: 'sales_contribution',
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} Store ID's`,
    },
    {
      Header: 'Revenue',
      accessor: 'revenue',
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} Store ID's`,
    },
    // Store Information
    {
      Header: 'Store Description',
      accessor: 'store_description',
      disableGroupBy: true,
      width: 55,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'Region',
      accessor: 'region',
      disableGroupBy: true,
      width: 55,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'State',
      accessor: 'state',
      disableGroupBy: true,
      width: 55,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'Store Address',
      accessor: 'store_address',
      disableGroupBy: true,
      width: 55,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: 'MD Status',
      accessor: 'status',
      disableGroupBy: true,
      width: 70,
      style: {
        textAlign: 'center',
      },
    },
    {
      Header: 'Status',
      accessor: 'status',
      disableGroupBy: true,
      width: 70,
      Cell: StatusCellStyling,
    },
  ];
  return colDef;
};
