import React, { Component } from 'react';
import { connect } from 'react-redux';
import Filters from '../../services/filters';
import DataTable from '../../services/reactTable';
import styled from 'styled-components';
import plannedReportsAction from '../../actions/plannedReportsAction';
import _ from 'lodash';
import LoadingOverlay from '../../utils/Loader';
import NoData from '../noTableData';
// import card1Image from '../../images/card1Image.png';
// import card2Image from '../../images/card2Image.png';
// import card3Image from '../../images/card3Image.png';
// import card4Image from '../../images/card4Image.png';
import { toUnit } from '../../helper/formatter';
import { toast } from 'react-toastify';

class planedReport extends Component {
  constructor(props) {
    super(props);
    this.state = {
      textStr: 'Please select filters',
      showTable: false,
      // filters: [
      //   {
      //     type: 'hierarchy1',
      //     isMandatory: true,
      //     isMultiple: false,
      //   },
      //   {
      //     type: 'hierarchy2',
      //     isMandatory: true,
      //     isMultiple: true,
      //   },
      //   {
      //     type: 'hierarchy3',
      //     isMandatory: true,
      //     isMultiple: true,
      //   },
      //   {
      //     type: 'hierarchy4',
      //     isMandatory: true,
      //     isMultiple: true,
      //   },
      //   {
      //     type: 'hierarchy5',
      //     isMandatory: true,
      //     isMultiple: true,
      //   },
      //   {
      //     type: 'hierarchy6',
      //     isMandatory: true,
      //     isMultiple: true,
      //   },
      // ],

      selectedItems: [],
      firstCardText: 'Avg Markdown',
      secondCardText: 'Sell through %',
      thirdCardText: 'Margin %',
      fourthCardText: 'Quantity Sold',
      firstCardFirstValue: '12%',
      firstCardSecondValue: '24%',
      secondCardFirstValue: '16%',
      secondCardSecondValue: '20%',
      thirdCardFirstValue: '6%',
      thirdCardSecondValue: '10%',
      fourthCardFirstValue: '345',
      fourthCardSecondValue: '3,457',
      cardWeekText: 'this Week',
      cardMonthText: 'this Month',
    };
  }
  onHandleSelect = (selectValues) => this.setState({ selectValues });
  validateFilters = () => {
    return (
      this.props.selectedHierarchy1 &&
      this.props.selectedHierarchy2.length &&
      // this.props.selectedHierarchy3.length &&
      // this.props.selectedHierarchy4.length &&
      // this.props.selectedHierarchy5.length &&
      // this.props.selectedHierarchy6.length &&
      // this.props.selectedStores.length &&
      this.props.startDate &&
      this.props.endDate
    );
  };
  onFilter = (filters) => {
    if (!this.validateFilters()) {
      toast.error(`Please select mandatory filters`, {
        autoClose: 15000,
        hideProgressBar: true,
      });
      return;
    }
    this.setState({
      showTable: true,
    });
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        hierarchy7: this.props.selectedHierarchy7,
        hierarchy8: this.props.selectedHierarchy8,
        hierarchy9: this.props.selectedHierarchy9,
        hierarchy10: this.props.selectedHierarchy10,
      },
      startDate: this.props.startDate,
      endDate: this.props.endDate,
      week: this.props.weeks,
    };
    // req = {
    //   filters: {
    //     hierarchy1: ['MENS'],
    //     hierarchy2: ['SPORTSWEAR MENS TEES'],
    //     hierarchy3: ['SPW MENS TEES'],
    //     hierarchy4: ['LS TEE'],
    //     hierarchy5: ['seasonal key items'],
    //     hierarchy6: ['Prior to Jan20'],
    //   },
    //   startDate: '06-01-2020',
    //   endDate: '08-02-2020',
    //   week: [23, 24, 25, 26, 27, 28, 29, 30, 31],
    // };
    // req = {
    //   filters: {
    //     hierarchy1: ['MENS'],
    //     hierarchy2: ['SPORTSWEAR MENS TEES'],
    //     hierarchy3: ['SPW MENS TEES'],
    //     hierarchy4: ['LS TEE'],
    //   },
    //   hierarchyType: 'HIERARCHY5',
    // };
    this.props.fetchPlannedReports(req).then((res) => {
      if (_.isEmpty(res)) {
        this.setState({
          textStr: 'No data found. Please try with different filters.',
        });
      }
    });
  };

  coldef = () => {
    if (this.props.getPlannedTableData.length) {
      let columns = [
        // {
        //   Header: 'Plan ID',

        //   accessor: 'promo_code',
        //   disableGroupBy: true,
        //   //   width: 50,

        //   align: 'left',
        //   enableRowSpan: true,
        //   // cells:
        // },

        {
          Header: 'Plan',
          accessor: 'promo_id',
          disableGroupBy: true,
          Cell: ({ row }) => (
            <div>
              <span>{row.original.plan ? row.original.plan : null}</span>
              <div>
                <button
                  className="btn btn-secondary"
                  onClick={() => {
                    this.props.updateSidebar('/vis/Markdown/create-markdown');
                    this.props.history.push({
                      pathname: `/vis/Markdown/create-markdown`,
                      state: { isEdit: true, planId: row.original.promo_id },
                    });
                  }}>
                  <span>
                    <i className="fa fa-pencil color-blue">Edit</i>
                  </span>
                </button>
              </div>
            </div>
          ),
          align: 'center',
          enableRowSpan: true,
          downloadValue: 'plan',
        },
        {
          Header: 'Scenario',
          accessor: (d) => d.promo_id + d.promo_id,
          disableGroupBy: true,
          Cell: ({ row, rows }) => (row.original.scenario ? row.original.scenario : null),
          enableRowSpan: true,
          //   width: 50,
          align: 'left',
          downloadValue: 'scenario',
        },
        {
          Header: 'Start Date',
          // accessor: 'startDate',
          disableGroupBy: true,
          accessor: 'promo_code',
          Cell: ({ row, rows }) => (row.original.startDate ? row.original.startDate : null),
          enableRowSpan: true,
          //   width: 50,
          align: 'left',
          downloadValue: 'startDate',
        },
        {
          Header: 'End Date',
          accessor: 'promotion_id',
          disableGroupBy: true,
          // accessor: 'promo_code',
          Cell: ({ row, rows }) => (row.original.endDate ? row.original.endDate : null),
          enableRowSpan: true,
          // enableRowSpan: true,
          //   width: 50,
          align: 'left',
          downloadValue: 'endDate',
        },
        {
          Header: 'Metrics',
