import React, { Component } from 'react';
import Filters from '../../services/filters';
import MarkdownAnalysis from '../MarkdownAnalysis';
import InventoryInfo from './InventoryInfo';
import Constraints from './Constraints';
import CreateCadence from './CreateCadence';
import Modal from 'react-awesome-modal';
import { withRouter } from 'react-router-dom';
import Select from 'react-select';
import { connect } from 'react-redux';
import manuallyCreateMdActions from '../../actions/manuallyCreateMdActions.js';
import './index.css';
import moment from 'moment';
import { Button, Tabs, Tab } from 'react-bootstrap';
import MarkdownDetailedTable from './MardownDetailedTable';
import ColumnSelection from '../columnSelection';
import cloneDeep from 'clone-deep';
import ComparisonTable from './ComparisonTable';
import ComparisonMetricTable from './ComparisonMetricTable';
import classNames from 'classnames';
import ExcludeStoreTable from './excludeStoreTable';
import PlanSavePopup from './planSavePopup';
class ManuallyCreateMD extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSkuId: { label: null, value: null },
      selectedSkuGroup: { label: null, value: null },
      targetSellThroughVal: null,
      markdownTypeVal: false,
      cadenceConstraintsVal: 'overall',
      marginVal: null,
      markdownDetailedData: [
        {
          sku_id: '1234',
          w1sellThrough: '1234',
          w1margin: '4321',
          w2sellThrough: '1234',
          w2margin: '4321',
          w3sellThrough: '1234',
          w3margin: '4321',
          w4sellThrough: '1234',
          w4margin: '4321',
          style_desc: 'def',
        },
        {
          sku_id: '1234',
          w1sellThrough: '1234',
          w1margin: '4321',
          w2sellThrough: '1234',
          w2margin: '4321',
          w3sellThrough: '1234',
          w3margin: '4321',
          w4sellThrough: '1234',
          w4margin: '4321',
          style_desc: 'def',
        },
        {
          sku_id: '1234',
          w1sellThrough: '1234',
          w1margin: '4321',
          w2sellThrough: '1234',
          w2margin: '4321',
          w3sellThrough: '1234',
          w3margin: '4321',
          w4sellThrough: '1234',
          w4margin: '4321',
          style_desc: 'def',
        },
      ],
      overallCadenceData: [
        // [
        //   {
        //     w1: 20,
        //     w2: 30,
        //     w3: 40,
        //     w4: 50,
        //     w5: 60,
        //   },
        // ],
      ],
      skuLabelCadenceData: [
        // [
        //   {
        //     'SKU ID': 1000234,
        //     'MD Status': 'High',
        //     w1: 20,
        //     w2: 30,
        //     w3: 40,
        //     w4: 50,
        //     w5: 60,
        //   },
        //   {
        //     'SKU ID': 1000234,
        //     'MD Status': 'Medium',
        //     w1: 20,
        //     w2: 30,
        //     w3: 40,
        //     w4: 50,
        //     w5: 60,
        //   },
        // ],
      ],
      filters: [
        'season',
        'hierarchy1',
        'hierarchy2',
        'hierarchy3',
        'hierarchy4',
        'hierarchy5',
        'hierarchy6',
        'hierarchy7',
      ],
      totalInventory: 0,
      currentInventory: 0,
      totalStores: 0,
      selectedStores: 0,
      selectedView: 'Overview',
      selectedSku: [],
      showExcludeStorePopup: false,
      showPlanSavePopup: false,
      TabTitles: ['Optimal'],
      defaultActiveKey: 'Optimal',
      attributes: ['Sales', 'Inventory', 'Margin'],
      value: 1,
      dataOfALlTheTabs: [],
      data: [
        {
          skuId: 1000234,
          mdStatus: 'High',
          w1: 20,
          w2: 30,
          w3: 40,
          w4: 50,
          w5: 60,
        },
        {
          skuId: 1000234,
          mdStatus: 'Medium',
          w1: 20,
          w2: 30,
          w3: 40,
          w4: 50,
          w5: 60,
        },
      ],
      comparsionHeader: [
        {
          Header: 'Scenarios',
          accessor: 'scenarios',
        },
        {
          Header: 'W1',
          accessor: 'w1',
        },
        {
          Header: 'W2',
          accessor: 'w2',
        },
        {
          Header: 'W3',
          accessor: 'w3',
        },
        {
          Header: 'W4',
          accessor: 'w4',
        },
        {
          Header: 'W5',
          accessor: 'w5',
        },
        {
          Header: 'W6',
          accessor: 'w6',
        },
      ],
      comparisonData: [],
      showCadence: false,
      simulatedData: false,
    };
  }
  selectedSkuHandler = (val) => {
    this.setState({ selectedSkuId: val });
  };
  onUpdateMarkdownPercentage = (data, index) => {
    if (this.state.cadenceConstraintsVal === 'overall') {
      let data1 = cloneDeep(this.state.overallCadenceData);
      data1[index] = data;
      this.setState({
        overallCadenceData: data1,
      });
    } else {
      let data1 = cloneDeep(this.state.skuLabelCadenceData);
      data1[index] = data;
      this.setState({
        skuLabelCadenceData: data1,
      });
    }
  };
  getTab() {
    let comparsionData = [];
    if (this.state.TabTitles.indexOf('Comparison') !== -1) {
      for (let i = 0; i < this.state.TabTitles.length - 1; i++) {
        if (this.state.cadenceConstraintsVal === 'overall') {
          comparsionData.push({
            scenarios: this.state.TabTitles[i],
            ...this.state.overallCadenceData[i][0],
          });
        } else {
          let rows = cloneDeep(this.state.skuLabelCadenceData[i][0]);
          delete rows['SKU ID'];
          delete rows['MD Status'];
          comparsionData.push({
            scenarios: this.state.TabTitles[i],
            ...rows,
          });
        }
      }
    }
    // else {
    //   if (this.state.cadenceConstraintsVal === "overall") {
    //     let tempArr = cloneDeep(this.state.overallCadenceData)
    //     tempArr.push(this.state.overallCadenceData[0])
    //     this.setState({
    //       overallCadenceData: tempArr
    //     })
    //   }

    // : this.state.skuLabelCadenceData[index]
    // }

    return this.state.TabTitles.map((tabTitle, index) => {
      console.log('cadenceData:', this.state.overallCadenceData[index]);
      return (
        <Tab eventKey={tabTitle} title={tabTitle} key={tabTitle}>
          <div className="markdownperstyle">
            {this.state.cadenceConstraintsVal === 'sku' && tabTitle === 'Comparison' ? (
              <div className="row skuLabelStyle">
                <label className="skuLabel">SKU/SKU Group</label>
                <div className="col-md-2">
                  <Select name="category" value={this.state.selectedSkuGroup} />
                </div>
              </div>
            ) : (
              <label className="font-size-14">Markdown Percentages</label>
            )}
            {/* <label className="font-size-14">Markdown Percentages</label> */}
          </div>
          {tabTitle === 'Comparison' ? (
            <div className="cadencetable">
              {this.state.cadenceConstraintsVal === 'sku' ? (
                <ComparisonMetricTable showCheckBoxComparsionTable={true} />
              ) : (
                <ComparisonTable data={comparsionData} />
              )}
            </div>
          ) : (
            <div className="cadencetable">
              {/* {this.state.overallCadenceData[index] && this.state.overallCadenceData[index].length && ( */}
              <CreateCadence
                overallCadenceData={
                  this.state.cadenceConstraintsVal === 'overall'
                    ? this.state.overallCadenceData[index]
                    : this.state.skuLabelCadenceData[index]
                }
                onUpdateMarkdownPercentage={this.onUpdateMarkdownPercentage}
                addScenario={this.addScenario}
                selectedSku={this.state.selectedSku}
                key={tabTitle}
                index={index}
                activeTab={this.state.defaultActiveKey}
                // dafaultData={}
              />
              {/* )} */}
            </div>
          )}
        </Tab>
      );
    });
    // setTimeout(() => {
    //   this.setState({
    //     defaultActiveKey: "Scenario1",
    //   });
    // }, 5000);
  }
  getComparisonData = () => {
    let comparisonData = cloneDeep(this.state.comparisonData);
    for (let i = 0; i < this.state.TabTitles.length - 2; i++) {
      comparisonData.push({
        scenarios: this.state.TabTitles[i],
        w1: 123,
        w2: 324,
        w3: 432,
        w4: 3456,
        w5: 324,
        w6: 3333,
      });
    }
  };
  onSelectTabs = (key) => {
    if (this.state.dataOfALlTheTabs.length === 0) {
      let data = cloneDeep(this.state.dataOfALlTheTabs);
      data[0] = this.state.data;
      this.setState({
        dataOfALlTheTabs: data,
      });
    } else {
      this.setState({
        simulatedData: false,
      });
    }

    this.setState({
      defaultActiveKey: key,
    });
  };
  // componentDidUpdate(props){
  //   debugger
  //   if (this.state.cadenceConstraintsVal === 'overall') {
  //     this.setState({
  //       overallCadenceData: props.cadenceData
  //     })
  //   }
  // }
  addScenario = () => {
    let TabTitles = this.state.TabTitles;
    let str = 'Scenario';
    let tempData = [];
    if (this.state.TabTitles.length === 1) {
      str += TabTitles.length;
      TabTitles[TabTitles.length] = str;
    } else {
      str += TabTitles.length;
      TabTitles[TabTitles.length] = str;
    }
    let currentIndex = TabTitles.length - 1;
    if (TabTitles.indexOf('Comparison') !== -1) {
      let indexComparison = TabTitles.indexOf('Comparison');
      TabTitles[indexComparison] = TabTitles[indexComparison + 1];
      TabTitles[indexComparison + 1] = 'Comparison';
      currentIndex = TabTitles.length - 2;
    }
    if (this.state.cadenceConstraintsVal === 'overall') {
      tempData = cloneDeep(this.state.overallCadenceData);
      tempData[currentIndex] = tempData[0];
      console.log('tempdata:', tempData);
      // this.setState(
      //   {
      //     overallCadenceData: tempData,
      //   },
      //   () => {
      //     console.log('overallCadenceData dTA callback:', this.state.overallCadenceData, [
      //       ...this.state.overallCadenceData,
      //       cloneDeep(this.state.overallCadenceData[0]),
      //     ]);
      //   }
      // );
      console.log('this.state.overallCadenceData:', this.state.overallCadenceData);
      this.setState(
        {
          overallCadenceData: [...this.state.overallCadenceData, this.state.overallCadenceData[0]],
        },
        () => {
          console.log(
            'this.state.overallCadenceData inside callback:',
            this.state.overallCadenceData
          );
        }
      );
      // this.setState(
      //   {
      //     overallCadenceData:
      //     overallCadenceData: [...this.state.overallCadenceData, this.state.overallCadenceData[0]],
      //   },
      //   () => {
      //     console.log(
      //       'this.state.overallCadenceData inside callback:',
      //       this.state.overallCadenceData
      //     );
      //   }
      // );
    } else {
      tempData = cloneDeep(this.state.skuLabelCadenceData);
      tempData[currentIndex] = tempData[0];
      this.setState(
        {
          skuLabelCadenceData: tempData,
        },
        () => {
          console.log('skylabelcadence dTA:', this.state.skuLabelCadenceData);
        }
      );
    }

    TabTitles[TabTitles.length] = 'Comparison';
    this.setState({
      TabTitles,
      defaultActiveKey: str,
    });
  };

  generateCadence = () => {
    this.setState({
      showCadence: true,
    });

    let payload = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        hierarchy7: this.props.selectedHierarchy7,
        startDate: '',
        endDate: '',
      },
      inventorySelected: '',
      storeSelected: '',
      excludeStore: '',
      sellThrough: this.state.targetSellThroughVal || '',
      margin: this.state.marginVal || '',
      markdownType: this.state.markdownTypeVal || '',
      cadenceType: this.state.cadenceConstraintsVal || '',
    };

    console.log(payload, 'payyloaaddd');
    this.props.fetchCadence(payload);
  };
  onSimulate = () => {
    if (this.state.TabTitles.indexOf('Comparison') === -1) {
      let array = [...this.state.TabTitles, 'Comparison'];
      this.setState({
        TabTitles: array,
      });
    }

    if (this.state.defaultActiveKey === 'Comparison') {
      this.setState({
        simulatedData: false,
      });
    } else {
      this.setState({
        simulatedData: true,
      });
    }
  };
  changedView = (view) => {
    this.setState({ selectedView: view });
  };
  selectedSkuHandler = (val) => {
    this.setState({ selectedSkuId: val });
  };
  onExcludeStores = () => {
    this.setState({ showExcludeStorePopup: true });
  };
  showPlanSavePopup = () => {
    this.setState({ showPlanSavePopup: true });
  };
  showTablePopup = () => {
    this.setState({ showExcludeStorePopup: false });
  };
  getComparisonCadence = (tabTitle = 'comparison') => {
    return (
      <Tab eventKey={tabTitle} title={tabTitle} key={tabTitle}>
        <ComparisonTable />
      </Tab>

      // <Tab eventKey={tabTitle} title={tabTitle} key={tabTitle}>
      //   <CreateCadence
      //     addScenario={this.addScenario}
      //     selectedSku={this.state.selectedSku}
      //     key={tabTitle}
      //   />
      // </Tab>
    );
  };
  onFilter = () => {
    this.props.fetchInventoryInfo({
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        // hierarchy7: this.props.selectedHierarchy7,
      },
      startDate: '2-25-1995',
      endDate: '12-25-1995',
      startWeek: moment('2-25-1995', 'MM-DD-YYYY').week(),
      endWeek: moment('12-25-1995', 'MM-DD-YYYY').week(),
    });
  };

  // static getDerivedStateFromProps = (props, state) => {
  //   if (state.cadenceConstraintsVal === 'overall') {
  //     state.overallCadenceData = props.cadenceData;
  //     return state;
  //   }
  //   console.log('state:', state);
  //   return null;
  //   // console.log('cadenceData:', props.cadenceData)
  // };
  constraintsCadenceHandler = (value) => {
    this.setState({
      cadenceConstraintsVal: value,
      showCadence: false,
      TabTitles: ['Optimal'],
      defaultActiveKey: 'Optimal',
    });
  };
  markdownTypeHandler = (val) => {
    this.setState({
      markdownTypeVal: val,
    });
  };
  marginValueHandler = (e) => {
    this.setState({
      marginVal: e.target.value,
    });
  };
  targetSellThrough = (e) => {
    this.setState({
      targetSellThroughVal: e.target.value,
    });
  };
  componentDidUpdate(prevProps, precState) {
    if (this.props.cadenceData !== prevProps.cadenceData) {
      this.state.cadenceConstraintsVal === 'overall' &&
        this.setState({
          overallCadenceData: cloneDeep(this.props.cadenceData),
        });
      this.state.cadenceConstraintsVal === 'sku' &&
        this.setState({
          skuLabelCadenceData: cloneDeep(this.props.cadenceData),
        });
    }

    // return state;
    // }
    // console.log('state:', state);
    // return null;
    console.log(precState.overallCadenceData, this.state.overallCadenceData);
    console.log(this.state.overallCadenceData === precState.overallCadenceData);
  }
  onSend = () => {
    this.setState({ showPlanSavePopup: true });
  };
  // componentWillReceiveProps(props){
  //   debugger
  //   if (this.state.cadenceConstraintsVal === 'overall') {
  //     this.setState({
  //       overallCadenceData: [props.cadenceData]
  //     })
  //   }
  //   // console.log('cadenceData:', props.cadenceData)
  // }
  render() {
    const optionSku = [
      { value: 'chocolate', label: 'Chocolate' },
      { value: 'strawberry', label: 'Strawberry' },
      { value: 'vanilla', label: 'Vanilla' },
    ];
    console.log('this.state.overallCadenceData:', this.state.overallCadenceData);
    return (
      <div className="createMd pl-2 pr-2">
        <div className="card mb-4">
          <div>
            <h4 className="font-size-14 markdown-heading">
              Markdown Optimization /
              <span className="font-bold color-blue">Manually Create MD</span>
            </h4>
          </div>
          <hr
            className="mb-2"
            style={{
              marginTop: '-4px',
            }}
          />
          <Filters filters={this.state.filters} onFilter={() => this.onFilter()} />
          <hr className="dotted-line"></hr>
          <InventoryInfo
            onExcludeStores={this.onExcludeStores}
            totalInventory={this.state.totalInventory}
            currentInventory={this.state.currentInventory}
            totalStores={this.state.totalStores}
            selectedStores={this.state.selectedStores}
          />
          <hr className="dotted-line"></hr>
          <Constraints
            markdownTypeHandler={this.markdownTypeHandler}
            marginValueHandler={this.marginValueHandler}
            markdownTypeVal={this.state.markdownTypeVal}
            targetSellThrough={this.targetSellThrough}
            constraintsCadenceHandler={this.constraintsCadenceHandler}
            cadenceConstraintsVal={this.state.cadenceConstraintsVal}
          />
          <hr className="dotted-line"></hr>
          <div className="cadencebtn">
            <Button onClick={this.generateCadence}>Generate Cadence</Button>
          </div>
        </div>

        {this.state.showCadence &&
          (this.state.overallCadenceData.length || this.state.skuLabelCadenceData.length) && (
            <div className="card mb-4">
              {/* {this.state.defaultActiveKey} */}
              <Tabs
                activeKey={this.state.defaultActiveKey}
                transition={false}
                id="noanim-tab-example"
                onSelect={this.onSelectTabs}>
                {this.getTab()}
              </Tabs>
              <div className="addScenarioBtn">
                <Button className="btn btn-secondary pull-right mt-2" onClick={this.addScenario}>
                  + Add Scenario
                </Button>
              </div>
              <div className="simulatebtn">
                {this.state.defaultActiveKey !== 'Optimal' && (
                  <button
                    className="btn btn-primary"
                    onClick={this.onSimulate}
                    disabled={this.state.defaultActiveKey !== 'Comparison' ? false : true}>
                    Simulate
                  </button>
                )}
                <button className="btn btn-primary approvalBtn" onClick={this.onSend}>
                  Send for approval
                </button>
                <button className="btn btn-primary resetButton">Send to drafts</button>
              </div>
            </div>
          )}
        {this.state.cadenceConstraintsVal === 'overall' &&
          this.state.defaultActiveKey === 'Comparison' && (
            <div className="card mb-4">
              <div className="optimalheading pl-3 pt-2">{this.state.defaultActiveKey}</div>
              <hr />
              <div className="cadencetable">
                <ComparisonMetricTable />
              </div>
            </div>
          )}

        {this.state.simulatedData && (
          <div className="card mb-4">
            <div className="optimalheading pl-3 pt-2">{this.state.defaultActiveKey}</div>
            <hr />
            {this.state.defaultActiveKey !== 'Comparison' && (
              <div className="overviewtoggle">
                <button
                  type="button"
                  className={classNames({
                    active: this.state.selectedView == 'Overview',
                    ' btn': true,
                    'btn-outline-primary cal-btn1': true,
                    btnCalendr: true,
                    calPromo: true,
                  })}
                  onClick={() => this.changedView('Overview')}
                  style={{
                    width: '90px',
                  }}>
                  Overview
                </button>
                <button
                  type="button"
                  className={classNames({
                    active: this.state.selectedView == 'Detailed',
                    ' btn': true,
                    'btn-outline-primary cal-btn1': true,
                    btnCalendr: true,
                    calPromo: true,
                  })}
                  onClick={() => this.changedView('Detailed')}
                  style={{
                    width: '90px',
                    borderColor: '#507a91',
                  }}>
                  Detailed
                </button>
              </div>
            )}
            {this.state.selectedView === 'Overview' &&
              this.state.defaultActiveKey !== 'Comparison' && (
                <div>
                  <MarkdownAnalysis
                    selectedSku={this.state.selectedSkuId}
                    optionSku={optionSku}
                    value={this.props.selectedSkuId}
                    selectedSkuHandler={(val) => this.selectedSkuHandler(val)}
                  />
                </div>
              )}
            {this.state.selectedView === 'Detailed' &&
              this.state.defaultActiveKey !== 'Comparison' && (
                <div>
                  <ColumnSelection attributes={this.state.attributes} />
                  <div className="cadencetable" style={{ paddingBottom: '2rem' }}>
                    <MarkdownDetailedTable data={this.state.markdownDetailedData} />
                  </div>
                </div>
              )}{' '}
          </div>
        )}

        <Modal
          visible={this.state.showPlanSavePopup}
          effect="fadeInDown"
          onClickAway={() => this.showPlanSavePopup()}>
          <div className="card" style={{ width: '130%' }}>
            <div className="card-body">
              <h6 className="card-title">
                Enter MD Plan Details
                <span
                  onClick={() => this.showPlanSavePopup()}
                  className="pull-right select-store-span">
                  <i
                    className="fa fa-times"
                    onClick={() => this.showPlanSavePopup()}
                    aria-hidden="true"
                  />
                </span>
              </h6>
              <hr />
            </div>

            <div className="ai-pre-grid-table" style={{ textAlign: 'right' }}>
              <PlanSavePopup labels={this.state.TabTitles} />
            </div>
          </div>
        </Modal>
        <Modal
          visible={this.state.showExcludeStorePopup}
          effect="fadeInDown"
          onClickAway={() => this.showTablePopup()}>
          <div className="card">
            <div className="card-body">
              <h6 className="card-title">
                Select stores
                <span
                  onClick={() => this.showTablePopup()}
                  className="pull-right select-store-span">
                  <i
                    className="fa fa-times"
                    onClick={() => this.showTablePopup()}
                    aria-hidden="true"
                  />
                </span>
              </h6>
              <hr />
            </div>

            <div className="ai-pre-grid-table">
              <ExcludeStoreTable storeData={this.props.inventoryInfoData} />
            </div>
          </div>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    inventoryInfoData: state.manuallyCreateMdData.inventoryInfoData,
    cadenceData: state.manuallyCreateMdData.cadenceData,
    selectedHierarchy1: state.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: state.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: state.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: state.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: state.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: state.filtersReducer.selectedHierarchy6,
    selectedHierarchy7: state.filtersReducer.selectedHierarchy7,
  };
};
const mapActionsToProps = {
  fetchInventoryInfo: manuallyCreateMdActions.fetchInventoryInfo,
  fetchCadence: manuallyCreateMdActions.fetchCadence,
  simulate: manuallyCreateMdActions.simulate,
  sendApproval: manuallyCreateMdActions.sendApproval,
  sendDrafts: manuallyCreateMdActions.sendDrafts,
};

export default connect(mapStateToProps, mapActionsToProps)(withRouter(ManuallyCreateMD));
