import React, { useState, useEffect } from "react";
import "./markdowncss.css";
import cloneDeep from "clone-deep";

const PromoTable = (props) => {
  const [skuList, setSkuList] = useState([]);
  const [skuStatusList, setSkuStatusList] = useState([]);

  useEffect(() => {
    setSkuList(cloneDeep(props.setData.skuList));
    setSkuStatusList(cloneDeep(props.setData.skuStatusList));
  }, []);

  const onDeleteClickHandler = (idx) => {
    setSkuList((prevSkuList) => {
      const newSkuList = cloneDeep(prevSkuList);
      const removedSku = newSkuList.splice(idx, 1);
      props.deletedSkuListHandler(removedSku);
      return newSkuList;
    });
    setSkuStatusList((prevSkuStatusList) => {
      const newSkuStatusList = cloneDeep(prevSkuStatusList);
      newSkuStatusList.splice(idx, 1);
      return newSkuStatusList;
    });
  };

  const getRows = (params) => {
    return skuList.map((d, i) => {
      return (
        <tr className="tr-style-sku-table" key={i}>
          <td className="td-color">
            <a
              className="font-size-13 font-base-color text-deco"
              onClick={(e) => props.onselectPromo(d)}
            >
              {d}
            </a>
          </td>
          <td className="text-center">
            <button
              className={`btn btn-sm
                ${
                  skuStatusList[i] === "urgent"
                    ? "btn-red"
                    : skuStatusList[i] === "medium"
                    ? "btn-yellow"
                    : "btn-green"
                }`}
              style={{ width: "4rem" }}
            >
              {skuStatusList[i]}
            </button>
            {props.showDelete && (
              <span style={{ display: "inline-block", marginLeft: "1rem" }}>
                <button
                  className="btn btn-close-set"
                  onClick={() => onDeleteClickHandler(i)}
                >
                  <i class="fa fa-times-circle" aria-hidden="true" />
                </button>
              </span>
            )}
          </td>
        </tr>
      );
    });
  };
  return (
    <table className="promoTable skutable-style">
      <thead>
        <tr className="skutable-tr">
          <th className="font-size-13">Sub Category ID</th>
          <th className="font-size-13">Status</th>
        </tr>
      </thead>
      <tbody>
        {getRows()}
        <tr />
      </tbody>
    </table>
  );
};

export default PromoTable;
