import React, { Component } from 'react';
import DataTable from '../../services/reactTable';
import cloneDeep from 'clone-deep';
// import { toPercentage } from '../../helper/formatter';
import ColumnSelection from '../../components/columnSelection';
import styled from 'styled-components';
import _ from 'lodash';
import moment from 'moment';

import NoData from '../noTableData';
import {
  toPercentage,
  toDollarWithlDraft,
  toUnitDraft,
  toDollarWithDecimal,
  toPercWithOneDecimal,
} from '../../helper/formatter';

function FormatterStyling(props, value, val) {
  let val1 = val || val == 0 ? val : props.value;
  let props1 = val || val == 0 ? { value: val } : props;
  if (value == 'planned_units' || value == 'units')
    return val1 || val1 == 0 ? toUnitDraft(props1) : '-';
  if (value == 'revenue' || value == 'margin')
    return val1 || val1 == 0 ? toDollarWithlDraft(props1) : '-';

  if (value == 'aur' || value == 'aum')
    return val1 || val1 == 0 ? toDollarWithDecimal(props1) : '-';
  //case downloadFormatter
  if (props === 'planned_units' || props === 'units') return toUnitDraft;
  if (props === 'revenue' || props == 'margin') return toDollarWithlDraft;

  if (props === 'aur' || props == 'aum') return toDollarWithDecimal;

  return '-';
}
let combinedData = [];
class MarkdownDetailedTable extends Component {
  constructor() {
    super();
    this.state = {
      columnOptions: [],
      selLabels: [],
      selectedItems: [],
      column: {
        Header: '',
        columns: [
          {
            Header: 'ST %',
            accessor: 'sellThrough',
            // minWidth: 600,
            width: 500,
            Cell: (props) => <div> {toPercentage(props)} </div>,
            // downloadFormatter: toPercentage,
          },
          {
            Header: 'Margin %',
            accessor: 'margin_percentage',
            // minWidth: 600,
            width: 500,
            Cell: (props) => <div> {toPercWithOneDecimal(props)} </div>,
            // downloadFormatter: toPercentage,
          },
          {
            Header: 'MD',
            accessor: 'scenario',
            // mi nWidth: 600,
            width: 500,
          },
        ],
      },
      headers: [],
      tableData: [],
      editedData: [],
    };
  }
  componentDidMount() {
    let { detailedTableData } = this.props;
    if (!_.isEmpty(detailedTableData)) {
      this.setState(
        {
          columnOptions: detailedTableData.columnOptions,
          selLabels: detailedTableData.selLabels,
          tableData: this.props.data && this.props.data.data,
        },
        () => {
          if (!_.isEmpty(detailedTableData.selectedItems))
            this.onHandleDropdownSelect(detailedTableData.selectedItems);
          else this.updateHeader();
        }
      );
      return;
    }
    this.updateHeader();
  }
  componentDidUpdate(prevProps) {
    if (
      this.props.data &&
      prevProps.data &&
      !_.isEqual(prevProps.data.weeks, this.props.data.weeks)
    ) {
      this.updateHeader();
    }
    if (!_.isEqual(this.props.data && this.props.data.data, this.state.tableData)) {
      combinedData = this.props.data && this.props.data.data;
      this.setState({
        tableData: this.props.data && this.props.data.data,
      });
    }
    if (
      !_.isEmpty(this.props.editData) &&
      !_.isEmpty(this.props.data) &&
      !_.isEqual(this.props.editData.data, this.state.editedData)
    ) {
      let Data = [...this.props.data.data, ...this.props.editData.data];
      combinedData = _.sortBy(Data, [
        function (o) {
          return o.SKU_Id;
        },
      ]);
      let find = _.find(this.state.headers, { accessor: 'metric' });
      if (!find) this.updateHeader();

      this.setState({
        editedData: this.props.editData.data,
      });
    }
  }
  updateHeader = (newColumns) => {
    let headers = [
      {
        Header: 'Bin',
        accessor: 'bins',
        width: 300,
        disableGroupBy: true,
        enableRowSpan: true,
      },
      {
        Header: 'Style/Style Group',
        accessor: 'SKU_Id',
        width: 300,
        disableGroupBy: true,
        enableRowSpan: true,
      },
      {
        Header: 'Style Description',
        accessor: 'style_description',
        width: 300,
      },
    ];

    _.map(this.props.data && this.props.data.weeks, (week) => {
      let weeksDate = {
        w1: {
          week_num: '1',
          start_of_week_date: '2017-01-2',
          end_of_week_date: '2017-02-2',
        },
        w2: {
          week_num: '2',
          start_of_week_date: '2017-01-2',
          end_of_week_date: '2017-02-2',
        },
        w3: {
          week_num: '3',
          start_of_week_date: '2017-01-2',
          end_of_week_date: '2017-02-2',
        },
        w4: {
          week_num: '4',
          start_of_week_date: '2017-01-2',
          end_of_week_date: '2017-02-2',
        },
      };
      console.log(week, 'weeee', weeksDate);

      let sMonth = moment(weeksDate && weeksDate.week && weeksDate.week.start_of_week_date).format(
        'MMM'
      );
      let eMonth = moment(weeksDate && weeksDate.week && weeksDate.week.end_of_week_date).format(
        'MMM'
      );
      let sDay = moment(weeksDate && weeksDate.week && weeksDate.week.start_of_week_date).format(
        'DD'
      );
      let eDay = moment(weeksDate && weeksDate.week && weeksDate.end_of_week_date).format('DD');
      let header = sMonth + ' ' + sDay + ' - ' + eMonth + ' ' + eDay;
      let column = newColumns ? cloneDeep(newColumns) : cloneDeep(this.state.column);
      column.Header = header;
      console.log(column.Header, 'hhhhhhh');
      _.map(column.columns, (item, index) => {
        if (item.accessor == 'scenario') {
          item.Cell = (props) => {
            return props.row.original.offer_value == '%off' ||
              props.row.original.offer_value == 'percent_off'
              ? `${props.value} %`
              : `${props.value}  PP`;
          };
        }
        item.accessor = `${week}${item.accessor}`;
        item.downloadHeader = `${week} ${item.Header}`;
        return item;
      });
      headers.push(column);
    });

    if (!_.isEmpty(this.props.editData) && this.props.isEdit) {
      headers.splice(1, 0, {
        Header: '',
        disableSort: true,
        accessor: 'metric',
        disableGroupBy: true,
      });
    }
    this.setState({ headers });
  };
  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = _.cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });

        let selectedItemsClone = _.cloneDeep(this.state.selectedItems);
        let newSelectedItems = [];
        let labelArr = Object.keys(this.props.columnHeaders[label]);
        selectedItemsClone.forEach((item) => {
          if (!labelArr.includes(item.label)) {
            newSelectedItems.push(item);
          }
        });

        this.onHandleDropdownSelect(newSelectedItems);
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
          selectedItems: newSelectedItems,
        });
      }
    }
  };
  onHandleDropdownSelect = (options) => {
    let headers = cloneDeep(this.state.headers);
    let column = cloneDeep(this.state.column);
    _.remove(headers, (item) => item.metric);

    _.map(options, (item) => {
      // let downloadFormatter = FormatterStyling(item.value);
      column.columns.push({
        Header: item.label,
        accessor: item.value,
        metric: true,
        Cell: (props) => FormatterStyling(props, item.value),
        // downloadFormatter,
      });
    });
    this.updateHeader(column);
    this.setState({
      selectedItems: options,
    });
  };
  componentWillUnmount() {
    this.props.detailedTableDataHandler({
      columnOptions: this.state.columnOptions,
      selLabels: this.state.selLabels,
      selectedItems: this.state.selectedItems,
      headers: this.state.headers,
    });
  }
  render() {
    console.log(this.props.data, 'dadadadadadda');
    return (
      <div>
        {combinedData && combinedData.length ? (
          <div style={{ overflowX: 'auto', width: '100%', paddingBottom: '28px' }}>
            <ColSelBtn>
              <ColumnSelection
                onHandleCheckBox={this.onHandleCheckBox}
                columnHeaders={this.props.columnHeaders}
                onHandleSelect={this.onHandleDropdownSelect}
                columnOptions={this.state.columnOptions}
                selectedItems={this.state.selectedItems}
                selLabels={this.state.selLabels}
              />
            </ColSelBtn>
            <DataTable
              isSort={true}
              fileName="Simulated details"
              columns={this.state.headers}
              data={combinedData ? combinedData : ''}
              isPagination={true}
              showExport={true}
            />{' '}
          </div>
        ) : (
          <NoData description="No Data" />
        )}
      </div>
    );
  }
}

export default MarkdownDetailedTable;

const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin-bottom: -3rem;
`;
