import React, { Component } from 'react';
import styled from 'styled-components';
import moment from 'moment';
import './index.css';
import _ from 'lodash';
import { toast } from 'react-toastify';

class CreateCadence extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      selectedData: [],
    };
  }
  getHeader() {
    console.log(this.props.overallCadenceData, 'this.props.overallCadenceData ');
    if (this.props.overallCadenceData && this.props.overallCadenceData.length) {
      let headers = [];
      _.map(Object.keys(this.props.overallCadenceData[0]), (o) => {
        if (o !== 'bins' && o !== 'SKU_Id') {
          if (this.props.daterangeOrWeek === 'date_range') {
            let obj = this.props.overallCadenceData[0] && this.props.overallCadenceData[0][o];
            let sMonth = moment(obj && obj.start_of_week_date).format('MMM');
            let eMonth = moment(obj && obj.end_of_week_date).format('MMM');
            let sDay = moment(obj && obj.start_of_week_date).format('DD');
            let eDay = moment(obj && obj.end_of_week_date).format('DD');
            let header = sMonth + ' ' + sDay + ' - ' + eMonth + ' ' + eDay;
            headers.push(header);
          } else {
            headers.push(o);
          }
        }
      });
      if (this.props.cadenceConstraintsVal === 'overall') {
        //overall case
        headers.unshift('bins'); //add bins col at first
      }
      if (this.props.cadenceConstraintsVal === 'sku') {
        //sku
        headers.unshift('SKU ID'); //add sku_id col at first
      }

      return headers.map((header, index) => {
        return <th key={index}>{header[0].toUpperCase() + header.slice(1)}</th>;
      });
    }
  }

  onCadenceValue(event, col, row, rowNumber) {
    let data = this.props.overallCadenceData;
    if (this.props.daterangeOrWeek === 'date_range') row[col].val = event.target.value;
    else row[col] = event.target.value;
    data[rowNumber] = row;
    this.props.onUpdateMarkdownPercentage(data, this.props.index, event.target.value);
  }
  getInputWithPercentageTag(col, row, rowNumber, i) {
    let disableInput =
      this.props.weekInfo &&
      this.props.weekInfo.pastWeeks &&
      this.props.weekInfo.pastWeeks.includes(col)
        ? true
        : false;
    let newValue =
      this.props.daterangeOrWeek === 'date_range' ? row[col] && row[col].val : row[col];
    return (
      <div className={`col ${this.props.offerType ? 'ppWidth' : 'percenWidth'}`} key={i + 1}>
        <div className="input-group">
          {this.props.offerType == 'pp' ? (
            <div class="input-group-prepend">
              <span class="input-group-text">$</span>
            </div>
          ) : null}

          <input
            type="number"
            step="5"
            className="form-control border-style"
            onChange={(e) => this.onCadenceValue(e, col, row, rowNumber, i)}
            // onBlur={(e) => this.validateCadenceValue(e, col, row, rowNumber, i)}
            value={newValue}
            min={this.props.validateDiscount.min}
            max={this.props.validateDiscount.max}
            step={this.props.validateDiscount.interval}
            disabled={
              this.props.isEdit
                ? this.props.activeTab === 'Optimal' || disableInput
                : this.props.activeTab === 'Optimal'
            }
          />
          <div className="input-group-append">
            {this.props.offerType == 'pp' ? (
              <button
                className="btn btn-side-percent"
                type="button"
                style={{ zIndex: 'unset', fontSize: '15px' }}>
                PP
              </button>
            ) : (
              <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
                <i className="fa fa-percent" />
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  getTags(col, row, rowNumber, i) {
    if (col.toLowerCase().startsWith('w')) {
      return this.getInputWithPercentageTag(col, row, rowNumber, i);
    } else if (col.toLowerCase().startsWith('m')) {
      return (
        <div
          style={{
            backgroundColor: row[col] === 'Medium' ? '#D6B93E' : '#E59393',
            color: 'white',
            borderRadius: '6px',
          }}>
          {row[col]}
        </div>
      );
    } else {
      return row[col];
    }
  }

  render() {
    return (
      <div
        className={
          this.props.cadenceConstraintsVal == 'overall'
            ? 'createCadenceTable'
            : this.props.overallCadenceData && this.props.overallCadenceData.length >= 10
            ? 'createCadenceTableComparison'
            : 'createCadenceTable'
        }>
        <Styles>
          <tbody>
            <tr>{this.getHeader()}</tr>
            {this.props.overallCadenceData &&
              this.props.overallCadenceData.length &&
              this.props.overallCadenceData.map((row, rowNumber) => {
                let rows = [];
                if (this.props.cadenceConstraintsVal === 'overall') {
                  //overall case
                  rows = _.filter(Object.keys(row), (o) => o !== 'bins' && o !== 'SKU_Id');
                  rows.unshift('bins'); //add bins col at first
                }
                if (this.props.cadenceConstraintsVal === 'sku') {
                  //sku case
                  rows = _.filter(Object.keys(row), (o) => o !== 'SKU_Id' && o !== 'bins');
                  rows.unshift('SKU_Id'); //add sku_id col at first
                }
                return (
                  <tr>
                    {rows.map((col, i) => {
                      return (
                        <td style={{ width: '1%' }}>{this.getTags(col, row, rowNumber, i)}</td>
                      );
                    })}
                  </tr>
                );
              })}
          </tbody>
        </Styles>
      </div>
    );
  }
}

export default CreateCadence;

const Styles = styled.table`
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;

  td,
  th {
    border: 1px solid #dddddd;
    text-align: center;
    padding: 8px;
  }
`;
