import React, { Component } from 'react';
import styled from 'styled-components';
import cloneDeep from 'clone-deep';
import InputWithPercentageTag from './InputWithPercentageTag';
import _ from 'lodash';
class ComparisonTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      selectedRow: [],
      editedRows: {},
    };
  }
  // componentWillReceiveProps(props) {
  //   this.setState({
  //     data: this.props.data,
  //   });
  // }
  // componentDidMount() {
  //   this.setState({
  //     data: this.props.data,
  //   });
  // }
  onCadenceValue(event, col, row, rowNumber) {
    let data = cloneDeep(this.props.data);
    row[col] = event.target.value;
    data[rowNumber] = row;
    this.setState({
      data: data,
    });
    this.props.onComparisonInputChange(data, this.props.index);
  }
  getHeader() {
    if (this.state.data && this.state.data.length) {
      let headers = Object.keys(this.state.data[0]);
      if (this.props.cadenceConstraintsVal === 'overall') {
        //overall case
        headers = _.filter(Object.keys(this.state.data[0]), (o) => o !== 'bins');
        headers.splice(1, 0, 'bins'); //add bins col at first
      }
      if (this.props.cadenceConstraintsVal === 'sku') {
        //sku case
        headers = _.filter(Object.keys(this.state.data[0]), (o) => o !== 'SKU_Id');
        headers.splice(1, 0, 'SKU ID'); //add sku_id col at first
      }
      headers.unshift('');
      return headers.map((header, index) => {
        return <th key={index}>{header ? header[0].toUpperCase() + header.slice(1) : ''}</th>;
      });
    }
  }
  onCheckboxClick = (e, col, row, index) => {
    let selectedRows = cloneDeep(this.state.selectedRow);
    if (e.target.checked) {
      selectedRows.push(row);
    } else {
      selectedRows.forEach((rows, index) => {
        if (rows.scenarios === row.scenarios) {
          selectedRows.splice(index, 1);
        }
      });
    }
    this.setState({
      selectedRow: selectedRows,
    });
    // let comparisonRowVal = row;
    this.props.comparisonCheckbox(selectedRows, index);
  };
  // getInputWithPercentageTag(col, row, i, rowNumber, index) {
  //   return (
  //     <div className="col" key={i + 1} style={{ width: '140px' }}>
  //       <div className="input-group" style={{ width: '90%' }}>
  //         <input
  //           type="text"
  //           onChange={(event) => this.onCadenceValue(event, col, row, rowNumber, index, i)}
  //           // onChange={(e) => this.onComparisonInputChange(e, col, row, rowNumber, index, i)}
  //           className="form-control border-style"
  //           value={row[col]}
  //           disabled={row.scenarios === 'Optimal' ? true : false}
  //         />
  //         <div className="input-group-append">
  //           <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
  //             <i className="fa fa-percent" />
  //           </button>
  //         </div>
  //       </div>
  //     </div>
  //   );
  // }
  changedRow = (obj) => {
    let data = this.state.data;
    data.splice(obj.rowNumber, 1, obj.row);
    // this.setState({
    //   data: data,
    // });
  };

  componentDidUpdate(preprops) {
    if (this.props.data !== preprops.data) {
      this.setState({
        data: this.props.data,
      });
    }
  }

  componentDidMount(preprops) {
    // if (this.props.data !== preprops.data) {
    this.setState({
      data: this.props.data,
    });
    // }
  }

  getTags(col, row, rowNumber) {
    console.log(col, 'col');
    if (col.toLowerCase().startsWith('w')) {
      // return this.getInputWithPercentageTag(col, row, rowNumber);
      return (
        <InputWithPercentageTag
          row={row}
          col={col}
          value={row[col]}
          rowNumber={rowNumber}
          data={this.state.data}
          changedRow={(obj) => {
            let data = cloneDeep(this.state.data);
            data[obj.rowNumber][obj.col] = obj.value;
            let editedRow = cloneDeep(this.state.editedRows);
            editedRow[obj.rowNumber] = data[obj.rowNumber];
            this.setState({ data: data, editedRows: editedRow });
            this.props.updatedData(data, this.state.editedRows);
          }}
        />
      );
    } else if (col.toLowerCase().startsWith('scenarios')) {
      return row[col];
    } else if (col.toLowerCase().startsWith('bins')) {
      return row[col];
    } else if (col.startsWith('SKU ID')) {
      return row[col];
    } else {
      return (
        <input
          type="checkbox"
          onChange={(e) => this.onCheckboxClick(e, col, row, rowNumber)}></input>
      );
    }
  }

  render() {
    return (
      <>
        <div className="cadencetable">
          <div className="createCadenceTable">
            <Styles>
              <tbody>
                <tr>{this.getHeader()}</tr>
                {this.state.data &&
                  this.state.data.length > 0 &&
                  this.state.data.map((row, rowNumber) => {
                    let rows = Object.keys(row);
                    if (this.props.cadenceConstraintsVal === 'overall') {
                      //overall case
                      rows = _.filter(Object.keys(row), (o) => o !== 'bins');
                      rows.splice(1, 0, 'bins'); //add bins col at first
                    }
                    if (this.props.cadenceConstraintsVal === 'sku') {
                      //sku case
                      rows = _.filter(Object.keys(row), (o) => o !== 'SKU_Id');
                      rows.splice(1, 0, 'SKU_Id'); //add sku_id col at first
                    }
                    rows.unshift('');
                    return (
                      <tr>
                        {rows.map((col, index) => {
                          return <td>{this.getTags(col, row, rowNumber)}</td>;
                        })}
                      </tr>
                    );
                  })}
              </tbody>
            </Styles>
          </div>
        </div>
      </>
    );
  }
}

export default ComparisonTable;

const Styles = styled.table`
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;

  td,
  th {
    border: 1px solid #dddddd;
    text-align: center;
    padding: 8px;
  }
`;
