import axios from 'axios';
import { config } from '../utils/config';
import { toast } from 'react-toastify';

const manuallyCreateMdActions = {
  onResetInventoryInfo: () => {
    return (dispatch) => {
      dispatch({
        type: 'RESET_INVENTORY_INFO_DATA',
      });
    };
  },
  fetchInventoryInfo: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/getInventoryInfo/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          if (resp.data && resp.data.data && resp.data.data.store_data.length <= 0) {
            toast.error(
              `Current filter selection doesn't have inventory please retry with different selection`,
              {
                autoClose: 15000,
                hideProgressBar: true,
              }
            );
          }
          dispatch({
            type: 'GET_INVENTORY_INFO',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          toast.error(err.message, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  fetchCadence: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        // url: `${config.createMarkdownURL}/generateCadence/`,
        url: `${config.cadenceUrl}/MarkdownOptimization`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          if (resp.data && resp.data.status !== 'false') {
            let skus = [];
            if (resp.data.skus && resp.data.skus.length) {
              skus = resp.data.skus.map((sku) => {
                return {
                  label: sku,
                  value: sku,
                };
              });
            }
            dispatch({
              type: 'GET_CADENCE_DATA',
              payload: [resp.data.cadenceData],
              simulation_id: resp.data.simulation_id,
              skus: skus,
              // savedData: resp.data.saveData,
            });
            // dispatch({
            //   type:'ANAYSIS_DATA',

            // })
            dispatch({
              type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
              payload: resp.data.simulateData,
            });
            dispatch({
              type: 'SET_ANALYSIS_TABLE_DATA',
              payload: resp.data.simulateData,
              data: resp.data,
              cadenceType: 'Optimal',
              comparisonData: [],
            });
          } else {
            toast.error(resp.data.message, {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          toast.error(err.message, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },

  updateAnalysisData: (req, cadenceType) => {
    return (dispatch) => {
      dispatch({
        type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
        payload: cadenceType === 'Optimal' ? req.simulateData : req,
      });
      // if (this.state.defaultActiveKey === 'Optimal') {
      //   obj.analysisData[key] = this.props.analysisData[this.state.defaultActiveKey]
      //     ? this.props.analysisData[this.state.defaultActiveKey].simulateData
      //     : [];
      // } else {
      //   const { analysisTabledata, detailed, overview } = this.props.analysisData[
      //     this.state.defaultActiveKey
      //   ];
      //   obj.analysisData[key] = { analysisTabledata, detailed, overview };
      // }
      dispatch({
        type: 'SET_ANALYSIS_TABLE_DATA',
        payload: cadenceType === 'Optimal' ? req.simulateData : req,
        data: req,
        cadenceType,
      });
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: false,
        },
      });
    };
  },

  simulate: (reqObj, cadenceType) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.cadenceUrl}/CalculationData`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          let returnStatus = true;
          if (resp.data.status === 'false') {
            toast.error(resp.data.message, {
              autoClose: 15000,
              hideProgressBar: true,
            });
            dispatch({
              type: 'LOADER_STATE',
              payload: {
                status: false,
              },
            });
            returnStatus = false;
          }
          let uniqueSellThroughValue = new Set(resp.data.overview.sellThrough);
          if (
            resp.data &&
            resp.data.overview &&
            uniqueSellThroughValue.size === 1 &&
            uniqueSellThroughValue.has('0.0')
          ) {
            toast.error('Data not found', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
          if (reqObj.skus === 'all') {
            dispatch({
              type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
              payload: resp.data,
            });
            dispatch({
              type: 'SET_ANALYSIS_TABLE_DATA',
              payload: resp.data,
              data: resp.data,
              cadenceType,
            });
          } else {
            dispatch({
              type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA_BY_SKUS',
              payload: resp.data,
            });
            dispatch({
              type: 'SET_ANALYSIS_TABLE_DATA_BY_SKUS',
              payload: resp.data,
              data: resp.data,
              cadenceType,
            });
          }
          dispatch({
            type: 'GET_COLUMNHEADERS',
          });
          // dispatch({
          //   type: 'SET_ANALYSIS_TABLE_DATA',
          //   payload: resp.data,
          //   data: resp.data,
          //   cadenceType,
          // });
          // dispatch({
          //   type: 'LOADER_STATE',
          //   payload: {
          //     status: false,
          //   },
          // });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return { status: returnStatus };
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });

          return { status: false };
        });
    };
  },
  savedStatusFalse: (status) => {
    return (dispatch) => {
      dispatch({
        type: 'MARKDOWN_SAVED_STATUS',
        status,
      });
    };
  },
  saveData: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/saveMarkDown/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Data Saved Sucessfully', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          dispatch({
            type: 'MARKDOWN_SAVED_STATUS',
            status: true,
          });
          // dispatch({
          //   type: 'GET_INVENTORY_INFO',
          //   payload: resp.data,
          // });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
};
export default manuallyCreateMdActions;
