import axios from '../utils/axios';
import { config } from '../utils/config';

const plannedReportsActions = {
  onReset: () => {
    return (dispatch) => {
      dispatch({
        type: 'RESET_PLANNED_REPORTS_DATA',
      });
    };
  },
  fetchPlannedReports: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/getActualAndPlanned/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'GET_PLANNED_REPORTS_DATA',
            payload: resp.data.data.length > 0 ? resp.data.data.map((d) => d.data) : [],
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return resp.data.data.length > 0 ? resp.data.data.map((d) => d.data) : [];
        })
        .catch((err) => {
          dispatch({
            type: 'GET_PLANNED_REPORTS_ERROR',
            payload: err,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return [];
        });
    };
  },
  updateSidebar: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'UPDATE_SIDEBAR',
        payload: reqObj,
      });
    };
  },
};

export default plannedReportsActions;
