import React from "react";
import { Line } from "react-chartjs-2";

const CadenceChart = (props) => {
  const data = {
    labels: ["Jun 24", "Jun 25", "Jun 26", "Jun 27", "Jun 28", "Jun 29"],
    datasets: [
      {
        data: [10, 18],
        borderColor: "#7b8ec9",
        backgroundColor: "#eaecf6",
      },
      {
        data: [10, 18, 25, 39],
        borderColor: "#c099b7",
        backgroundColor: "#f1e8ef",
      },
      {
        data: [10, 18, 25, 39, 50, 58],
        borderColor: "#e5ce6b",
        backgroundColor: "#faf4de",
      },
    ],
  };
  const options = {
    annotation: {
      annotations: props.annotations
        ? props.annotations
        : [
            {
              drawTime: "afterDatasetsDraw",
              type: "line",
              mode: "vertical",
              scaleID: "x-axis-0",
              value: "Jun 25",
              borderColor: "#c6bcbc",
              borderWidth: 2,
            },
            {
              drawTime: "afterDatasetsDraw",
              type: "line",
              mode: "vertical",
              scaleID: "x-axis-0",
              value: "Jun 27",
              borderColor: "#c6bcbc",
              borderWidth: 2,
            },
          ],
    },
    plugins: {
      datalabels: {
        display: false,
      },
    },
    legend: {
      display: false,
      position: "bottom",
      labels: {
        boxWidth: 12,
      },
      fontFamily: "Helvetica",
      fontSize: "12px",
    },
    maintainAspectRatio: false,
    scales: {
      yAxes: [
        {
          gridLines: {
            display: false,
          },
          ticks: {
            beginAtZero: true,
          },
          scaleLabel: {
            display: true,
            labelString: "Units",
            fontWeight: 900,
            fontStyle: "bold",
          },
        },
      ],
      xAxes: [
        {
          id: "x-axis-0",
          gridLines: {
            // color: '#aeaeae5e',
            display: false,
          },
          ticks: {
            autoSkip: true,
            autoSkipPadding: 40,
            maxRotation: 0,
          },
          scaleLabel: {
            display: true,
            labelString: "Time (week)",
            fontWeight: 900,
            fontStyle: "bold",
          },
        },
      ],
    },
  };
  return (
    <div>
      <Line data={props.chartData ? props.chartData : data} options={options} />
    </div>
  );
};
export default CadenceChart;
