import axios from 'axios';
import { config } from '../utils/config';
import { toast } from 'react-toastify';

const styleConfigActions = {
  fetchStyleGroups: () => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        // url: `${config.styleAndStoreConfigURL}/get-style-family-group-dropdown/`,
        method: 'GET',
      })
        .then((resp) => {
          console.log(resp.data.data, 'storesgroups');
          dispatch({
            type: 'SET_STYLE_GROUPS',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  fetchStyle: () => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        // url: `${config.styleAndStoreConfigURL}/get-storesgroups`,
        method: 'GET',
      })
        .then((resp) => {
          console.log(resp.data.data, 'storesgroups');
          dispatch({
            type: 'SET_STYLE',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  getStyleConfigTableData: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.styleAndStoreConfigURL}/get-style-family-group/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          console.log(resp.data.data, 'storesgroups');
          let tableData = [
            {
              style_group: reqObj.style_config_id,
              subRows: resp.data.data,
            },
          ];
          dispatch({
            type: 'STYLE_CONFIG_TABLE_DATA',
            payload: tableData,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  createStyleGroup: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.styleAndStoreConfigURL}/style-config-create/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Style Group Created Successfully', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(resp, 'get-stores-create');
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err);
        });
    };
  },
  editStyleGroup: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.styleAndStoreConfigURL}/style-config-edit/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Style Group Updated Successfully', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          console.log(resp, 'stpreresp');
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err);
        });
    };
  },
  mapStyle: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.styleAndStoreConfigURL}/style-create/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Style mapping is Successful', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(resp, 'get-stores-create');
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err);
        });
    };
  },
  editStyle: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.styleAndStoreConfigURL}/style-edit/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Style Updated Successfully', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          console.log(resp, 'stpreresp');
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err);
        });
    };
  },
};
export default styleConfigActions;
