import React from 'react';
import { toUnit, toDollar, toPercentage } from '../../helper/formatter';

const CellRenderer = ({ row, accessorValue }) => {
  return (
    <span
      {...row.getToggleRowExpandedProps({
        style: {
          paddingLeft: `${row.depth * 2}rem`,
        },
      })}>
      {accessorValue}{' '}
      {row.isExpanded ? (
        <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
      ) : (
        <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
      )}
    </span>
  );
};
export default () => {
  const header = [
    {
      Header: 'Style Group',
      columns: [
        {
          id: 'expander',
          Header: '',
          width: 15,
          Cell: ({ row }) => {
            return row.id.includes('.') ? null : (
              <span
                {...row.getToggleRowExpandedProps({
                  style: {
                    paddingLeft: `${row.depth * 2}rem`,
                  },
                })}>
                {row.original.style_group}&nbsp;&nbsp;&nbsp;
                {row.isExpanded ? (
                  <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
                ) : (
                  <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
                )}
              </span>
            );
          },
        },
      ],
    },
    {
      Header: 'Custom Style Group',
      columns: [
        {
          id: 'expander1',
          Header: '',
          width: 15,
          Cell: ({ row }) => {
            let checkForDot = row.id.includes('.') ? row.id.split('.') : [];

            return checkForDot.length === 2 ? (
              <span
                {...row.getToggleRowExpandedProps({
                  style: {
                    paddingLeft: `${row.depth * 2}rem`,
                  },
                })}>
                {row.original.custom_style_group}&nbsp;&nbsp;&nbsp;
                {row.isExpanded ? (
                  <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
                ) : (
                  <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
                )}
              </span>
            ) : null;
          },
        },
      ],
    },
    {
      Header: 'Style Color',
      columns: [
        {
          // Cell: ({ row }) => <CellRenderer row={row} accessorValue={row.original.style_number} />,
          downloadHeader: 'Style Color',
          accessor: 'style_color',
          groupByID: 'style_color',
          disableGroupBy: true,
          align: 'left',
          //showFilter: true,
          disableSort: true,
        },
      ],
    },

    {
      Header: 'Sell through %',
      columns: [
        {
          Header: 'TY',
          downloadHeader: 'Sell through TY %',
          downloadFormatter: toPercentage,
          accessor: 'sell_through_ty',
          aggregate: 'sum',
          disableGroupBy: true,
          align: 'left',
          Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
        },
        {
          Header: 'LY',
          downloadHeader: 'Sell through LY %',
          downloadFormatter: toPercentage,
          accessor: 'sell_through_ly',
          disableGroupBy: true,
          align: 'left',
          Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
        },
      ],
    },
    {
