import axios from 'axios';
import { config } from '../utils/config';
import {
  GET_STORE_INVENTORY,
  GET_ERRORS,
  SIMULATE_CADENCE_SKU,
  FLUSH_STORE_DATA,
  SIMULATE_CADENCE_SKU_LOADING,
  SIMULATE_PRE_SEASON_MARKDOWN_DATA,
  SAVE_OVERALL_MARKDOWN,
  RESET_MARKDOWN_SAVED,
  SAVE_SKU_MARKDOWN,
  SIMULATE_DATA_FOR_SKU,
} from './types';

export const fetchStoreData = (payload) => async (dispatch) => {
  let url = config.markDownUrl + '/markdown/getStoreInventory';
  try {
    const res = await axios.post(url, payload);
    if (res.data.status) {
      dispatch({ type: GET_STORE_INVENTORY, payload: res.data.data });
    } else {
      console.log('msg data', res.data.msg);
      dispatch({ type: GET_STORE_INVENTORY, payload: [], message: res.data.msg });
      /* let payload = {
        message: res.data.msg
      };
      dispatch({
        type: GET_ERRORS,
        payload: payload
      }); */
    }
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err,
    });
  }
};

export const simulateMarkdownCadenceAndSkuList = (payload) => async (dispatch) => {
  let url = config.markDownUrl + '/markdown/simulate';
  try {
    dispatch({ type: SIMULATE_CADENCE_SKU_LOADING });

    const res = await axios.post(url, payload);

    if (res.data.status) {
      dispatch({ type: SIMULATE_CADENCE_SKU, payload: res.data.data });
    } else {
      dispatch({ type: SIMULATE_CADENCE_SKU, payload: [] });
    }
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err,
    });
  }
};

export const simulatePreSeasonMarkDown = (payload) => async (dispatch) => {
  let url = config.markDownUrl + '/markdown/preseason';
  try {
    dispatch({ type: SIMULATE_CADENCE_SKU_LOADING });
    const res = await axios.post(url, payload);
    if (res.data.status) {
      dispatch({ type: SIMULATE_PRE_SEASON_MARKDOWN_DATA, payload: res.data.data });
    } else {
      dispatch({ type: SIMULATE_PRE_SEASON_MARKDOWN_DATA, payload: {} });
    }
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err,
    });
  }
};

export const simulateMarkdownCadenceAndSkuListMultiple = (payloads) => async (dispatch) => {
  let url = config.markDownUrl + '/markdown/simulate';
  try {
    dispatch({ type: SIMULATE_CADENCE_SKU_LOADING });
    let apis = payloads.markdowns.map((pld) => {
      payloads.markdown = pld.map((pd) => pd / 100).join(',');
      console.log(payloads.markdown);
      return axios.post(url, { ...payloads });
    });

    let responsePayload = [];

    Promise.all(apis).then(function (res) {
      if (res && res.length) {
        if (payloads.skus) {
          res.forEach((re, idx) => {
            if (re.data.data && re.data.data.set0) {
              responsePayload.push({
                overall: re.data.data.set0[payloads.skus],
              });
            }
          });
          dispatch({
            type: SIMULATE_DATA_FOR_SKU,
            payload: res,
          });
        } else {
          res.forEach((re, idx) => {
            if (re.data.status) {
              responsePayload.push(re.data.data[0]);
            } else {
            }
          });
        }

        responsePayload &&
          responsePayload.length &&
          dispatch({ type: SIMULATE_CADENCE_SKU, payload: responsePayload });
        responsePayload &&
          !responsePayload.length &&
          dispatch({ type: SIMULATE_CADENCE_SKU, payload: [] });
      }
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err,
    });
  }
};

export const flushStoreData = () => (dispatch) => {
  dispatch({ type: FLUSH_STORE_DATA });
};

export const saveOverAllMarkdown = (payload) => async (dispatch) => {
  let url = config.markDownUrl + '/markdown/save';
  try {
    const res = await axios.post(url, payload);
    dispatch({ type: SAVE_OVERALL_MARKDOWN, payload: res.data.status });
  } catch (err) {
    dispatch({ type: GET_ERRORS, payload: err });
  }
};

export const resetMakrdownSave = () => (dispatch) => {
  dispatch({ type: RESET_MARKDOWN_SAVED });
};

export const saveSkuMarkdown = (payload) => async (dispatch) => {
  let url = config.markDownUrl + '/markdown/saveSku';
  try {
    const res = await axios.post(url, payload);
    dispatch({ type: SAVE_SKU_MARKDOWN, payload: res.data.status });
  } catch (err) {
    dispatch({ type: GET_ERRORS, payload: err });
  }
};
