import React, { Component } from 'react';
import Select from 'react-select';
import SelectMulti from '../../../services/select';
import '../../../index.css';
import { connect } from 'react-redux';
import Filters from '../../../services/filters';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import './StyleConfigFilter.css';
import StyleConfigActions from '../../../actions/styleConfigActions';
import _ from 'lodash';
import MultiSelect from '../../../components/MultiSelect';
import { toast } from 'react-toastify';

const styleGroupData = [
  { label: 'HOODIE GROUP', value: 'HOODIE GROUP' },
  { label: 'SHORTS GROUP', value: 'SHORTS GROUP' },
  { label: 'FLEECE GROUP', value: 'FLEECE GROUP' },
];
const styleData = [
  {
    label: 'M NK BRTHE CHLLGR TOP SS',
    value: 'M NK BRTHE CHLLGR TOP SS',
  },
  {
    label: 'M NK DRY MILER TOP SS SSNL GX',
    value: 'M NK DRY MILER TOP SS SSNL GX',
  },
  {
    label: 'M NK TOP SL HOODIE SSNL GX',
    value: 'M NK TOP SL HOODIE SSNL GX',
  },
  {
    label: 'JORDAN BELL CAP',
    value: 'JORDAN BELL CAP',
  },
  {
    label: 'JORDAN CARBON FIBER FITTED',
    value: 'JORDAN CARBON FIBER FITTED',
  },
];
export class StyleConfigFilter extends Component {
  state = {
    styleGroupOptions: [],
    selectedStyleGroup: '',
    selectedStyle: '',
    filterReset: false,
    setHierarchy8: false,
  };
  setHierarchy8Handler = () => this.setState({ setHierarchy8: false });
  componentDidMount() {
    // this.props.fetchStyleGroups();
  }
  componentDidUpdate(prevProps, prevState) {
    if (
      this.props.selectedHierarchy7 &&
      !_.isEqual(prevProps.selectedHierarchy7, this.props.selectedHierarchy7)
    ) {
      let req = {
        filters: {
          hierarchy1: this.props.selectedHierarchy1,
          hierarchy2: this.props.selectedHierarchy2,
          hierarchy3: this.props.selectedHierarchy3,
          hierarchy4: this.props.selectedHierarchy4,
          hierarchy5: this.props.selectedHierarchy5,
          hierarchy6: this.props.selectedHierarchy6,
          hierarchy7: this.props.selectedHierarchy7,
        },
      };
      this.props.fetchStyleGroups(req);
    }

    if (
      !_.isEmpty(this.props.styleGroups) &&
      !_.isEmpty(this.props.selectedHierarchy7) &&
      !_.isEqual(this.state.styleGroupOptions, this.props.styleGroups.StyleGroups)
    ) {
      this.setState({ styleGroupOptions: this.props.styleGroups.StyleGroups });
    }

    if (!_.isEmpty(this.state.styleGroupOptions) && _.isEmpty(this.props.selectedHierarchy6)) {
      this.setState({ styleGroupOptions: [], selectedStyleGroup: '' });
    }
  }
  onStyleGroupChangeHandler = (val) => {
    this.setState({
      selectedStyleGroup: val,
    });
  };
  onStyleChangeHandler = (val) => {
    this.setState({
      selectedStyle: val,
    });
  };

  onFilter = () => {
    this.props
      .getStyleConfigTableData({
        // style_type: 'group',
        style_config_id: this.state.selectedStyleGroup.value,
      })
      .then((res) => {
        this.props.onFilterHandler();
        this.setState({ setHierarchy8: true });
      });
  };
  filterResetHandler = (val) => {
    this.setState({
      filterReset: val,
    });
  };

  render() {
    let styleGrp = this.state.selectedStyleGroup
      ? [
          {
            label: this.state.selectedStyleGroup.parent_style_group,
            value: this.state.selectedStyleGroup.parent_style_group,
          },
        ]
      : [];
    return (
      <>
        <div
        // className={this.props.compType === 'createStyleGroup'}
        >
          <Filters
            customStyleGroups
            // hideStoreClusterFilter
            hidefilterButton
            hierarchy={[
              {
                type: 'hierarchy1',
                isMandatory: true,
                isMultiple: false,
              },
              {
                type: 'hierarchy2',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy3',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy4',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy5',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy6',
                isMandatory: true,
                isMultiple: true,
              },
              {
                type: 'hierarchy7',
                isMandatory: true,
                isMultiple: true,
              },
              // {
              //   type: 'hierarchy8',
              //   isMandatory: false,
              //   isMultiple: true,
              // },
            ]}
            hideStoreCluster={true}
            onFilter={this.onFilter}
            from={this.props.from}
            filterReset={this.state.filterReset}
            onReset={this.filterResetHandler}
            setHierarchy8={this.state.setHierarchy8}
            setHierarchy8Handler={this.setHierarchy8Handler}
            hierarchy8Val={styleGrp}
          />
          {/* {this.props.compType === 'createStyleGroup' ? (
            <div className="col-md-2  mt-5">
              <button className="col-md-5 btn btn-primary btn-filter">
                <span>
                  <i className="fa fa-filter"></i>&nbsp; Filter
                </span>
              </button>
              <button
                className="col-md-6 offset-1 btn btn-primary btn-filter btn-filter-reset"
                // onClick={() => this.dropDownChangeHandler('reset')}
              >
                <span>
                  <i className="fa fa-repeat"></i>&nbsp; Reset
                </span>
              </button>
            </div>
          ) : null} */}
        </div>

        {/* {this.props.compType !== 'createStyleGroup' ? ( */}
        <div className="row add-sku-row">
          <div className="col-md-3">
            {/* <label className="mb-1 mt-3">Style Color Group</label>{' '} */}
            <div className="row">
              <div className="col-md-1 d-none">
                <input
                  type="radio"
                  name="selectStyleType"
                  value="styleGroup"
                  onChange={(e) =>
                    this.props.onStyleTypeChangeHandler(e.target.value, 'styleGroup')
                  }
                  checked={'styleGroup' === this.props.styleType}
                />
              </div>
              {/* <div className="col-md-10">
                <Select
                  className="style-config-filter"
                  name="styleGroup"
                  placeholder="Select Style Color Group"
                  isDisabled={this.props.styleType !== 'styleGroup'}
                  options={this.state.styleGroupOptions}
                  value={this.state.selectedStyleGroup}
                  onChange={this.onStyleGroupChangeHandler}
                />
              </div> */}
            </div>
          </div>
          {/* <div className="col-md-3">
            <label className="mb-1 mt-3">Style</label>
            <div className="row">
              <div className="col-md-1">
                <input
                  type="radio"
                  name="selectStyleType"
                  value="style"
                  onChange={(e) => this.props.onStyleTypeChangeHandler(e.target.value, 'style')}
                  checked={'style' === this.props.styleType}
                />
              </div>
              <div
                className={
                  this.props.styleType !== 'style' ? 'col-md-10 style-disabled' : 'col-md-10'
                }>
                <SelectMulti
                  className="style-config-filter"
                  name="style"
                  isMulti
                  allowSelectAll
                  placeHolder="Select Style"
                  isDisabled={this.props.styleType !== 'style'}
                  options={styleData}
                  value={this.state.selectedStyle}
                  onChange={this.onStyleChangeHandler}
                />
              </div>
            </div>
          </div> */}
          <div className="col-md-3  mt-5">
            <button
              // onClick={this.onFilter}
              className="col-md-4 btn btn-primary btn-filter"
              disabled={this.state.selectedStyleGroup ? false : true}>
              <span>
                <i className="fa fa-filter"></i>&nbsp; Filter
              </span>
            </button>
            {/* <button
              className="col-md-4 offset-1 btn btn-primary btn-filter btn-filter-reset"
              onClick={() => this.filterResetHandler(true)}>
              <span>
                <i className="fa fa-repeat"></i>&nbsp; Reset
              </span>
            </button> */}
          </div>
        </div>
        {/* ) : null} */}
      </>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    selectedHierarchy7: store.filtersReducer.selectedHierarchy7,
    selectedHierarchy8: store.filtersReducer.selectedHierarchy8,
    styleGroups: store.styleReducer.styleGroups,
    selectedCustomStyleGroup: store.filtersReducer.selectedCustomStyleGroup,
    style: store.styleReducer.style,
  };
};
const mapActionsToProps = {
  getStyleConfigTableData: StyleConfigActions.getStyleConfigTableData,
  fetchStyleGroups: StyleConfigActions.fetchStyleGroups,
};
export default connect(mapStateToProps, mapActionsToProps)(StyleConfigFilter);
