import React, { Component } from "react";
import DataTable from "../../services/reactTable";
import cloneDeep from 'clone-deep';

class MarkdownDetailedTable extends Component {
constructor(){
    super();
    this.state = {
        column: {
            Header: '',
            columns: [{
              Header: 'Sell through %',
              accessor: 'sellThrough'
            }, {
              Header: 'Margin %',
              accessor: 'margin'
            }]
        },
        weeks: ['w1', 'w2','w3','w4'],
        headers:[{
            Header: "SKU ID",
            accessor: "sku_id"
        }],


    }
}

componentDidMount() {    
    let counter = 0;
    let headers = cloneDeep(this.state.headers)
    this.state.weeks.forEach(week => {
        let column = cloneDeep(this.state.column);
        column.Header = week
        column.columns[0].accessor = `${week}${column.columns[0].accessor}`
        column.columns[1].accessor = `${week}${column.columns[1].accessor}`
        headers.push(column)
        counter++;
    });
    if (counter === this.state.weeks.length) {
        this.setState({
            headers
        })
    }

}

    render() {
        return <DataTable columns={this.state.headers} data={this.props.data} />;
    } 
}


export default MarkdownDetailedTable;
