import axios from 'axios';
import { config } from '../utils/config';
import { toast } from 'react-toastify';

const manuallyCreateMdActions = {
  fetchInventoryInfo: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/getInventoryInfo/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          console.log(resp.data.data, 'resp');
          if (resp.data && resp.data.data && resp.data.data.store_data.length <= 0) {
            toast.error(
              `Current filter selection doesn't have inventory please retry with different selection`,
              {
                autoClose: 15000,
                hideProgressBar: true,
              }
            );
            return;
            // dispatch({
            //   type: 'LOADER_STATE',
            //   payload: {
            //     status: false,
            //   },
            // });
          }
          dispatch({
            type: 'GET_INVENTORY_INFO',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          toast.error(err.message, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err.message, 'message');
        });
    };
  },
  fetchCadence: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        // url: `${config.createMarkdownURL}/generateCadence/`,
        url: `${config.cadenceUrl}/MarkdownOptimization`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          console.log(resp, 'resppppp');
          console.log(resp.status, 'resppppp');
          if (resp.data && resp.data.status !== 'false') {
            let skus = [];
            if (resp.data.skus && resp.data.skus.length) {
              skus = resp.data.skus.map((sku) => {
                return {
                  label: sku,
                  value: sku,
                };
              });
            }
            dispatch({
              type: 'GET_CADENCE_DATA',
              payload: [resp.data.cadenceData],
              csv_names: resp.data.csv_names,
              skus: skus,
              savedData: resp.data.saveData,
            });
            // dispatch({
            //   type:'ANAYSIS_DATA',

            // })
            dispatch({
              type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
              payload: resp.data.simulateData,
            });
            dispatch({
              type: 'SET_ANALYSIS_TABLE_DATA',
              payload: resp.data.simulateData,
              data: resp.data,
              cadenceType: 'Optimal',
            });
          } else {
            toast.error(resp.data.message, {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          toast.error(err.message, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },

  simulate: (reqObj, cadenceType) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.cadenceUrl}/CalculationData`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
            payload: resp.data,
          });
          dispatch({
            type: 'SET_ANALYSIS_TABLE_DATA',
            payload: resp.data,
            data: resp.data,
            cadenceType,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err.message, 'message');
        });
    };
  },
  saveData: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/saveMarkDown/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Data Saved Sucessfully', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          // dispatch({
          //   type: 'GET_INVENTORY_INFO',
          //   payload: resp.data,
          // });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err);
        });
    };
  },
};
export default manuallyCreateMdActions;
