import React, { Component } from 'react';
import StyleFilters from '../StyleFilters/StyleFilters';
// import TableComponent from '../../../components/TableComponent/TableComponent';
// import TableContextProvider from '../../../components/TableComponent/context';
// import CreateStyleFamilySchema from '../../../utils/columnDefs/CreateStyleFamilySchema';
import { cloneDeep, differenceBy } from 'lodash';
import excelIcon from '../../../images/leftPanel/status.svg';

export class CreateStyleFamily extends Component {
  state = {
    familyName: '',
    // allStyles: CreateStyleFamilySchema.data,
    currentStyles: [],
    selectedAllStyles: [],
    selectedCurrentStyles: [],
    showEDLP: true,
    showGeo: false,
  };

  onFamilyNameChange = (e) => {
    this.setState({
      familyName: e.target.value,
    });
  };

  onAddClickHandler = () => {
    const allStyles = cloneDeep(this.state.allStyles);
    const currentStyles = cloneDeep(this.state.currentStyles);
    currentStyles.push(...this.state.selectedAllStyles);
    const newAllStyles = differenceBy(allStyles, this.state.selectedAllStyles, 'style_id');
    console.log('asdflkasmdfa', newAllStyles, this.state.allStyles, this.state.selectedAllStyles);

    this.setState({
      allStyles: newAllStyles,
      currentStyles,
      selectedAllStyles: [],
    });
  };

  onRemoveClickHandler = () => {
    const currentStyles = cloneDeep(this.state.currentStyles);
    const allStyles = cloneDeep(this.state.allStyles);
    const newCurrentStyles = differenceBy(
      currentStyles,
      this.state.selectedCurrentStyles,
      'style_id'
    );
    allStyles.push(...this.state.selectedCurrentStyles);
    this.setState({
      allStyles,
      currentStyles: newCurrentStyles,
      selectedCurrentStyles: [],
    });
  };

  onAllStyleSelectionChangeHandler = (rowData) => {
    console.log('asdfmasdf', rowData);
    this.setState({
      selectedAllStyles: rowData,
    });
  };

  onCurrentStylesSelectionChangeHandler = (rowData) => {
    console.log('adfsasdf', rowData);
    this.setState({
      selectedCurrentStyles: rowData,
    });
  };

  render() {
    const {
      __Style_family_name,
      __All_styles,
      __Current,
      __No_of_styles,
      __Create,
    } = this.props.translation;
    return (
      <div className="card p-2">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">
            Store Configuration/
            <span className="font-bold color-blue">
              {' '}
              {this.props.isEditStyle ? 'Edit Style' : 'Create Style'} Family
            </span>
          </h4>

          <div>
            <button className="btn btn-primary" onClick={this.props.onCancelButtonHandler}>
              Back
            </button>
          </div>
        </div>
        <hr
          className="mb-2"
          style={{
            marginTop: '-4px',
          }}
        />
        {/* <div className="row p-2">
          <label> *Style Family Name : </label>

        </div> */}
        <div className="row justify-content-between">
          <div className="col-md-6">
            <label className="mr-1">
              <span style={{ color: 'red' }}>*</span>
              {__Style_family_name}:
            </label>
            <input type="text" value={this.state.familyName} onChange={this.onFamilyNameChange} />
          </div>
          <div className="col-md-2 text-right">
            <button type="button" className="btn btn-primary" onClick={() => {}}>
              Upload &nbsp;&nbsp;
              <span>
                <img className="img-icons-create-promo" src={excelIcon} alt="" />
              </span>
            </button>
          </div>
        </div>
        <hr />
        <StyleFilters showEDLP={this.state.showEDLP} showGeo={this.state.showGeo} fromStyle />
        <hr />
        <div className="row p-2" style={{ justifyContent: 'center' }}>
          <div className="col-md-4">
            {__All_styles}
            <div style={{ height: '372px' }}>
              {/* <TableContextProvider
                value={{
                  tableComponent: {
                    colDefs: CreateStyleFamilySchema.colDefs,
                    data: this.state.allStyles,
                    autoSizeCol: true,
                    suppressRowTransform: true,
                    suppressRowClickSelection: true,
                    suppressCellSelection: true,
                    rowSelection: 'multiple',
                    onSelectionChanged: this.onAllStyleSelectionChangeHandler,
                  },
                }}>
                <TableComponent />
              </TableContextProvider> */}
            </div>
            <div>
              <button className="btn btn-primary" onClick={this.onAddClickHandler}>
                Add
              </button>
            </div>
          </div>
          <div className="col-md-4">
            <div>
              <span className="mr-2">{__Current}</span>
              <span>{__No_of_styles}: 23</span>
            </div>
            <div style={{ height: '372px' }}>
              {/* <TableContextProvider
                value={{
                  tableComponent: {
                    colDefs: CreateStyleFamilySchema.colDefs,
                    data: this.state.currentStyles,
                    autoSizeCol: true,
                    suppressRowTransform: true,
                    suppressRowClickSelection: true,
                    suppressCellSelection: true,
                    rowSelection: 'multiple',
                    onSelectionChanged: this.onCurrentStylesSelectionChangeHandler,
                  },
                }}>
                <TableComponent />
              </TableContextProvider> */}
            </div>
            <div>
              <button className="btn btn-primary" onClick={this.onRemoveClickHandler}>
                Remove
              </button>
            </div>
          </div>
        </div>
        <div style={{ textAlign: 'center' }}>
          <button
            className="btn btn-primary"
            onClick={() => this.props.onCreateStyleHandler(this.state)}>
            {__Create}
          </button>
          <button className="btn ml-1 cancel-button" onClick={this.props.onCancelButtonHandler}>
            Cancel
          </button>
        </div>
      </div>
    );
  }
}

export default CreateStyleFamily;
