import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { labels } from './config';
import Select from '../select';
import './index.css';
import { connect } from 'react-redux';
import DateRangePicker from '../RetailCalendarDatePicker';
import FilterActions from '../../actions/filtersAction';
import _ from 'lodash';

class Filters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      filters: {},
      startDate: null,
      endDate: null,
      hierarchy1:
        props.retainFilters && props.selectedHierarchy1
          ? props.selectedHierarchy1.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy2:
        props.retainFilters && props.selectedHierarchy2
          ? props.selectedHierarchy2.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy3:
        props.retainFilters && props.selectedHierarchy3
          ? props.selectedHierarchy3.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy4:
        props.retainFilters && props.selectedHierarchy4
          ? props.selectedHierarchy4.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy5:
        props.retainFilters && props.selectedHierarchy5
          ? props.selectedHierarchy5.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy6:
        props.retainFilters && props.selectedHierarchy6
          ? props.selectedHierarchy6.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy7:
        props.retainFilters && props.selectedHierarchy7 && props.location.state
          ? props.location.state.styles.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy8:
        props.retainFilters && props.selectedHierarchy8
          ? props.selectedHierarchy8.map((e) => {
              return { value: e, label: e };
            })
          : null,
      stores:
        props.retainFilters && props.selectedStores
          ? props.selectedStores.map((e) => {
              return { value: e, label: e };
            })
          : null,
    };
    !props.isEdit &&
      props.getHierarchyOptions({
        filters: {},
        hierarchyType: 'HIERARCHY1',
        allowInactiveProducts: this.props.allowInactiveProducts || false,
      });
    props.getStores({
      filters: {
        hierarchy1: [],
        hierarchy2: [],
        hierarchy3: [],
        hierarchy4: [],
        hierarchy5: [],
        hierarchy6: [],
        hierarchy7: [],
        hierarchy8: [],
      },
    });
    if (props.retainFilters && props.location.state) {
      props.setSelectedHierarchy7(props.location.state.styles, true);
      props.setSelectedStores(this.state.stores.map((e) => e.label));
    }
    props.setRetainFilters(false);
  }
  componentDidUpdate(prevProps, prevState) {
    if (this.props.filterReset) {
      this.props.filterResetHandler(false);
      this.reset(); // reset filters from configFilters
    }
    if (
      this.props.isEdit &&
      _.isEmpty(this.state.hierarchy1) &&
      this.props.editPlanDetails[0] &&
      !_.isEmpty(this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj)
    ) {
      let {
        hierarchy1,
        hierarchy2,
        hierarchy3,
        hierarchy4,
        hierarchy5,
        hierarchy6,
        hierarchy7,
        hierarchy8,
        tiers,
      } = this.props.editPlanDetails[0].request_obj.filters;

      this.props.presetFilters([
        hierarchy1,
        hierarchy2,
        hierarchy3,
        hierarchy4,
        hierarchy5,
        hierarchy6,
        hierarchy7,
        hierarchy8,
        tiers,
      ]);
      this.setState({
        hierarchy1: _.map(hierarchy1, (item) => ({ value: item, label: item })),
        hierarchy2: _.map(hierarchy2, (item) => ({ value: item, label: item })),
        hierarchy3: _.map(hierarchy3, (item) => ({ value: item, label: item })),
        hierarchy4: _.map(hierarchy4, (item) => ({ value: item, label: item })),
        hierarchy5: _.map(hierarchy5, (item) => ({ value: item, label: item })),
        hierarchy6: _.map(hierarchy6, (item) => ({ value: item, label: item })),
        hierarchy7: _.map(hierarchy7, (item) => ({ value: item, label: item })),
        hierarchy8: _.map(hierarchy8, (item) => ({ value: item, label: item })),
        stores: _.map(tiers, (item) => ({ value: item, label: item })),
      });
    }
  }
  getExtraFiltersOptions = (val, extraFiltersOptions) => {
    !Array.isArray(val) && (val = [val]);
    let resetHierachies = { stores: null };
    // for (let i = extraFiltersOptions + 1;; i <= this.props.extraFiltersOptions.length; i++) {
    //   resetHierachies[`hierarchy${i}`] = null;
    // }
    // this.setState({ [`hierarchy${hierarchy}`]: val }, async () => {
    //   await this.props[`setSelectedHierarchy${hierarchy}`](val.map((e) => e.label));
    //   let func = hierarchy === 8 ? `getStores` : `getExtraFiltersOptions`;
    //   let req = {};

    //   for (let i = 1; i <= hierarchy; i++) {
    //     req[`hierarchy${i}`] = this.props[`selectedHierarchy${i}`];
    //   }
    //   if (this.props[func]) {
    //     if (Object.keys(req).length) {
    //       this.props[func]({
    //         filters: req,
    //         filterType: ('hierarchy' + (hierarchy + 1)).toUpperCase(),
    //         allowInactiveProducts: this.props.allowInactiveProducts || false,
    //       });
    //     } else {
    //       this.props[func]();
    //     }
    //   }
    //   this.setState({ filters: { ...req } });
    // });
  };
  getHierarchyOptions = (val, hierarchy) => {
    console.log(val, 'vallllllllllllllll', hierarchy);
    !Array.isArray(val) && (val = [val]);
    let resetHierachies = { stores: null };
    for (let i = hierarchy + 1; i <= this.props.hierarchy.length; i++) {
      resetHierachies[`hierarchy${i}`] = null;
    }
    this.setState({ [`hierarchy${hierarchy}`]: val, ...resetHierachies }, async () => {
      await this.props[`setSelectedHierarchy${hierarchy}`](val.map((e) => e.label));
      let func = hierarchy === 8 ? `getStores` : `getHierarchyOptions`;
      let req = {};

      for (let i = 1; i <= hierarchy; i++) {
        req[`hierarchy${i}`] = this.props[`selectedHierarchy${i}`];
      }
      if (this.props[func]) {
        if (Object.keys(req).length) {
          this.props[func]({
            filters: req,
            hierarchyType: ('hierarchy' + (hierarchy + 1)).toUpperCase(),
            allowInactiveProducts: this.props.allowInactiveProducts || false,
          });
        } else {
          this.props[func]();
        }
      }
      this.setState({ filters: { ...req } });
    });
  };
  setDrop = (val) => {
    let req = {};
    let selItems = val.map((item) => item.label);
    req['drop'] = selItems;
    this.setState({ filters: { ...req }, drops: val }, () => {
      this.props.setSelectedDrops(val.map((v) => v.value));
    });
  };
  setStores = (val) => {
    // let req = {};
    let selItems = val.map((item) => item.label);
    // req['stores'] = selItems;
    let newFilters = { ...this.state.filters, stores: selItems };
    this.setState({ filters: newFilters, stores: val }, () => {
      this.props.setSelectedStores(val.map((v) => v.value));
    });
  };
  reset = () => {
    this.setState(
      {
        drops: null,
        startDate: null,
        endDate: null,
        hierarchy1: null,
        hierarchy2: null,
        hierarchy3: null,
        hierarchy4: null,
        hierarchy5: null,
        hierarchy6: null,
        hierarchy7: null,
        hierarchy8: null,
        stores: null,
      },
      () => {
        if (this.props.from !== 'draft/status') this.props.resetFilters(); ////////// handling reset issues rising bcs of tabs, only reset this component state, not reducer
        this.props.onReset && this.props.onReset();
      }
    );
  };
  onDatesChange = ({ startDate, endDate }) => {
    this.setState(
      {
        startDate,
        endDate,
      },
      () => {
        let weeks = [];
        if (endDate) {
          for (let i = startDate.clone(); i <= endDate; i = i.add(7, 'd')) {
            weeks.push(i.week());
          }
        }

        this.props.setDateRange({
          startDate: startDate && startDate.format('YYYY-MM-DD'),
          endDate: endDate && endDate.format('YYYY-MM-DD'),
          weeks,
        });
      }
    );
  };
  render() {
    return (
      <React.Fragment>
        <div
          className={this.props.isEdit ? 'row ml-4 mr-4 edit-markdown-filters' : 'row ml-4 mr-4'}>
          {this.props.drop && labels.drop && (
            <div className="col-md-2">
              <label className="mb-1 mt-3">{labels.drop}</label>
              <Select
                options={this.props.drops}
                onChange={(option) => this.setDrop(option)}
                getOptionLabel={(option) => option.label + (option.year ? option.year : '')}
                value={this.state.drops}
                isMulti
                // isDisabled={!this.props.drops || !this.props.drops.length}
              />
            </div>
          )}
          {/* {this.props.hierarchy.map(
            (filter, i) =>
              labels[filter] && (
                <div className="col-md-2 " key={filter}>
                  <label className="mb-1 mt-3">{labels[filter]}</label>
                  <Select
                    options={this.props[filter]}
                    onChange={(option) => this.getHierarchyOptions(option, i + 1)}
                    value={this.state[`hierarchy${i + 1}`]}
                    isMulti
                    allowSelectAll
                    // isDisabled={!(this.state[`hierarchy${i}`] && this.state[`hierarchy${i}`].length)}
                  />
                </div>
              )
          )} */}
          {this.props.hierarchy.map(
            (filter, i) =>
              labels[filter.type] && (
                <div className="col-md-2 " key={filter.type}>
                  <label className="mb-1 mt-3">
                    {labels[filter.type]}
                    {filter.isMandatory ? <span style={{ color: 'red' }}> * </span> : null}
                  </label>
                  <Select
                    name={`hierarchy${i + 1}`}
                    options={this.props[filter.type]}
                    onChange={(option) => this.getHierarchyOptions(option, i + 1)}
                    value={this.state[`hierarchy${i + 1}`]}
                    isMulti={filter.isMultiple}
                    isDisabled={i === 0 && this.props.disableDivision ? true : false}
                    // isDisabled={!(this.state[`hierarchy${i}`] && this.state[`hierarchy${i}`].length)}
                  />
                </div>
              )
          )}
          {this.props.extraFiltersOptions &&
            this.props.extraFiltersOptions.map(
              (filter, i) =>
                labels[filter.type] && (
                  <div className="col-md-2 " key={filter.type}>
                    {console.log(filter, 'filllllllllll', [filter.type])}
                    <label className="mb-1 mt-3">
                      {labels[filter.type]}
                      {filter.isMandatory ? <span style={{ color: 'red' }}> * </span> : null}
                    </label>
                    <Select
                      name={`hierarchy${i + 1}`}
                      options={this.props[filter.type]}
                      onChange={(option) => this.getExtraFiltersOptions(option, i + 1)}
                      value={this.state[`hierarchy${i + 1}`]}
                      isMulti={filter.isMultiple}
                      // isDisabled={i === 0 && this.props.disableDivision ? true : false}
                      // isDisabled={!(this.state[`hierarchy${i}`] && this.state[`hierarchy${i}`].length)}
                    />
                  </div>
                )
            )}
          {this.props.stores && labels.store && !this.props.hideStoreCluster && (
            <div className="col-md-2 ">
              <label className="mb-1 mt-3">
                {labels.store}
                {!this.props.storeIsNotMandatory ? <span style={{ color: 'red' }}> * </span> : null}
              </label>
              <Select
                options={this.props.stores}
                onChange={(option) => this.setStores(option)}
                value={this.state.stores}
                isMulti
                // isDisabled={!(this.state[`hierarchy${i}`] && this.state[`hierarchy${i}`].length)}
              />
            </div>
          )}
          {this.props.dateRange && (
            <div className="col-md-3">
              <label className="mb-1 mt-3">
                Date range <span style={{ color: 'red' }}> * </span>
              </label>
              <DateRangePicker
                startDate={this.state.startDate}
                startDateId="startDate_11"
                endDate={this.state.endDate}
                endDateId="endDate_11"
                onDatesChange={this.onDatesChange}
              />
            </div>
          )}
          {!this.props.hidefilterButton && (
            <div
              className={
                this.props.from === 'styleGroupConfig'
                  ? 'col-md-2 mt-5 px-0 text-center'
                  : 'col-md-3 mt-5'
              }>
              <button
                disabled={this.props.isEdit}
                onClick={() =>
                  this.props.onFilter({
                    filters: this.state.filters,
                    startDate: this.state.startDate,
                    endDate: this.state.endDate,
                  })
                }
                className={
                  this.props.from === 'styleGroupConfig'
                    ? 'btn btn-primary btn-filter'
                    : 'col-md-4 btn btn-primary btn-filter'
                }>
                <span>
                  <i className="fa fa-filter"></i>&nbsp; Filter
                </span>
              </button>
              <button
                disabled={this.props.isEdit}
                className={
                  this.props.from === 'styleGroupConfig'
                    ? 'offset-1 btn btn-primary btn-filter btn-filter-reset'
                    : 'col-md-4 offset-1 btn btn-primary btn-filter btn-filter-reset'
                }
                onClick={this.reset}>
                <span>
                  <i className="fa fa-repeat"></i>&nbsp; Reset
                </span>
              </button>
            </div>
          )}
        </div>
      </React.Fragment>
    );
  }
}

