import React, { Component } from 'react';
import Select from 'react-select';
import { InputGroup, FormControl } from 'react-bootstrap';
import cloneDeep from 'clone-deep';
// import { toast } from 'react-toastify';
// import "./index.css"
class PlanSave extends Component {
  constructor(props) {
    super(props);
    this.state = {
      planName: '',
      scenarioName: '',
      showErrorMessage: false,
    };
  }

  validateInput = (inputs) => {
    let value = inputs.map((obj) => obj.value);
    return new Set(value).size === value.length;
  };
  render() {
    let tabsData = cloneDeep(this.props.data);
    let tempArr = [];
    if (this.props.activeTab === 'Comparison') {
      tempArr = this.props.comparsionSelectedData.map((row) => {
        return Object.assign({}, row, {
          label: row.scenarios,
          value: '',
        });
      });
    } else {
      let data = tabsData[this.state.activeIndex] ? tabsData[this.state.activeIndex][0] : {};
      data.label = this.props.activeTab;
      data.value = '';
      tempArr = [data];
      // data.forEach((label) => {
      //   if (label !== 'Optimal' && label !== 'Comparison') {
      //     tempArr.push({
      //       label: label,
      //       value: '',
      //     });
      //   }
      // });
    }

    return (
      <div>
        <div className="row mt-3">
          <div className="col-md-4">
            {' '}
            <p className="font-size-14 mb-0 ml-3" style={{ fontWeight: 'bold' }}>
              <span className="required">*</span>Markdown Plan Name
            </p>
          </div>
          <div className="col-md-6">
            <InputGroup className="mb-3">
              <FormControl
                aria-label="planName"
                aria-describedby="basic-addon2"
                style={{ padding: '18px' }}
                value={this.state.planName}
                type="text"
                onChange={(e) => {
                  this.setState({ planName: e.target.value });
                }}
              />
            </InputGroup>
          </div>
        </div>
        {tempArr.map((obj, index) => {
          return (
            <div className="row" key={index}>
              <div className="col-md-4">
                {' '}
                <p className="font-size-14 mb-0 ml-3" style={{ fontWeight: 'bold' }}>
                  {obj.label}
                </p>
              </div>
              <div className="col-md-6">
                <InputGroup className="mb-3">
                  <FormControl
                    aria-label="scenarioName"
                    aria-describedby="basic-addon2"
                    style={{ padding: '18px' }}
                    // value={obj.value}
                    type="text"
                    onChange={(e) => {
                      obj.value = e.target.value;
                      // this.setState({ [obj.value]: e.target.value });
                    }}
                  />
                </InputGroup>
              </div>
            </div>
          );
        })}
        {this.state.showErrorMessage && (
          <div style={{ color: 'red', textAlign: 'center' }}>Names should be Unique</div>
        )}
        <div className="saveBtn">
          <button
            className="btn btn-primary biconbuttonFilter"
            style={{ width: '15%' }}
            disabled={!this.state.planName}
            onClick={() => {
              if (this.validateInput(tempArr)) {
                this.props.onSave(this.state.planName, tempArr);
              } else {
                this.setState({
                  showErrorMessage: true,
                });
              }
            }}>
            Save
          </button>
        </div>
      </div>
    );
  }
}

export default PlanSave;
