import React, { Component } from 'react';
import { labels } from './config';
import Select from 'react-select';
import './index.css';
import { connect } from 'react-redux';
import { DateRangePicker } from 'react-dates';
import FilterActions from '../../actions/filtersAction';

class Filters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startDate: null,
      endDate: null,
    };
    props.getHierarchyLevel1();
  }
  getHierarchyOptions = (val, hierarchy) => {
    let func = `getHierarchyLevel${hierarchy + 1}`;
    this.props[`selectedHierarchy${hierarchy}`].push(val.label);
    let req = {};
    for (let i = 1; i <= hierarchy; i++) {
      req[`hierarchy${i}`] = this.props[`selectedHierarchy${i}`];
    }
    if (this.props[func]) {
      if (Object.keys(req).length) {
        this.props[func](req);
      } else {
        this.props[func]();
      }
    }
  };
  render() {
    return (
      <React.Fragment>
        <div className="row ml-4">
          {this.props.filters.map((filter, i) => (
            <div className="col-md-2 filter-block">
              <label className="mb-1 mt-3">{labels[filter]}</label>
              <Select
                className=""
                options={this.props[filter]}
                onChange={(val) => this.getHierarchyOptions(val, i)}
              />
            </div>
          ))}
          <div className="col-md-2 filter-block">
            <label className="mb-1 mt-3">Date range</label>
            <DateRangePicker
              startDate={this.state.startDate}
              startDateId="startDate"
              endDate={this.state.endDate}
              endDateId="endDate"
              onDatesChange={({ startDate, endDate }) => this.setState({ startDate, endDate })}
              focusedInput={this.state.focusedInput}
              onFocusChange={(focusedInput) => this.setState({ focusedInput })}
            />
          </div>
          <div className="col-md-2 mt-5">
            <button
              className="col-md-5 btn btn-primary filterBtn"
              onClick={() => this.props.onFilter()}>
              <span>
                <i class="fa fa-filter"></i>&nbsp; Filter
              </span>
            </button>
            <button className="col-md-5 offset-1 btn btn-primary resetButton">
              <span>
                <i class="fa fa-repeat"></i>&nbsp; Reset
              </span>
            </button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    season: store.filtersReducer.season,
    hierarchy1: store.filtersReducer.hierarchy1,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    hierarchy2: store.filtersReducer.hierarchy2,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    hierarchy3: store.filtersReducer.hierarchy3,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    hierarchy4: store.filtersReducer.hierarchy4,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    hierarchy5: store.filtersReducer.hierarchy5,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    hierarchy6: store.filtersReducer.hierarchy6,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    hierarchy7: store.filtersReducer.hierarchy7,
    selectedHierarchy7: store.filtersReducer.selectedHierarchy7,
  };
};
const mapDispatchToProps = {
  getSeasons: FilterActions.getSeasons,
  getHierarchyLevel1: FilterActions.getHierarchyLevel1,
  getHierarchyLevel2: FilterActions.getHierarchyLevel2,
  getHierarchyLevel3: FilterActions.getHierarchyLevel3,
  getHierarchyLevel4: FilterActions.getHierarchyLevel4,
  getHierarchyLevel5: FilterActions.getHierarchyLevel5,
  getHierarchyLevel6: FilterActions.getHierarchyLevel6,
  getHierarchyLevel7: FilterActions.getHierarchyLevel7,
};
export default connect(mapStateToProps, mapDispatchToProps)(Filters);
