import axios from '../utils/axios';
import { config } from '../utils/config';
import { toast } from 'react-toastify';
import moment from 'moment';

function pastWeekFutureWeekHandler(startDate, endDate) {
  let pastWeeks = [];
  let futureWeeks = [];
  let crrntDate = moment().format('YYYY-MM-DD');

  for (let i = startDate; i <= endDate; i = moment(i).add(1, 'week').format('YYYY-MM-DD')) {
    if (moment(i).isSameOrBefore(crrntDate)) pastWeeks.push(`W${moment(i).week()}`);
    else futureWeeks.push(`W${moment(i).week()}`);
  }
  return {
    pastWeeks,
    futureWeeks,
  };
}

const manuallyCreateMdActions = {
  onResetInventoryInfo: () => {
