        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/getInventoryInfo/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          if (resp.data && resp.data.data && resp.data.data.store_data.length <= 0) {
            toast.error(
              `Current filter selection doesn't have inventory please retry with different selection`,
              {
                autoClose: 15000,
                hideProgressBar: true,
              }
            );
          }
          dispatch({
            type: 'GET_INVENTORY_INFO',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
