import React, { Component } from 'react'
import { Bar } from "react-chartjs-2";

let chartOptions = {
  plugins: {
    datalabels: {
       display: false
    }
 },
  hover: {
    mode: "nearest",
  },
  scales: {
    xAxes: [
      {
        gridLines: {
          display: false,

        },
        ticks: {
          fontSize: 10,
          opacity: 0.7,
         },

        barPercentage: 1.0,
      }
    ],
    yAxes: [
      {
        gridLines: {
          display: false,
          // drawBorder: false,
        },
      }
    ]
  },
  legend: {
    display: true,
    position: 'bottom',
    labels: {
      boxWidth: 12
  },
    fontFamily: "Helvetica",
    fontSize: "12px",

  },
  responsive: true,
  maintainAspectRatio: true,
}
const data = {
  labels: ["January", "February", "March", "April", "May", "June", "July"],
  datasets: [
    {
      label: "My First dataset",
      backgroundColor: "rgba(45,105,235,0.7)",
      borderColor: "rgba(45,105,235,0.8)",
      borderWidth: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [65, 59, 80, 81, 56, 55, 40]
    }
  ]
};

export class PerformanceQuantity extends Component {
  render() {
    return (
      <div className="quantityTab">

        <div className="row">
          <div className="col-md-6">
            <h4 className="promoHeading">Current Year</h4>
            <div className="cards quantityCharts">
            <div className="row">
            <div className="col-md-4">
            <p className="promoCal">AdGroup</p>
                <select id="inputSelect1" className="form-control">
                  <option selected>AdGroup_1</option>
                  <option>...</option>
                </select>
              </div>
              <div className="col-md-4">
                <p className="promoCal">Offer</p>
                <select id="inputSelect1" className="form-control">
                  <option selected>20% off</option>
                  <option>...</option>
                </select>
              </div>

            </div>
              <div className="chartDiv1">
                <Bar data={data} options={chartOptions}/>
              </div>
            </div>
          </div>
          <div className="col-md-6">
            <h4 className="promoHeading">Last Year</h4>
            <div className="cards quantityCharts">
            <div className="row">
            <div className="col-md-4">
            <p className="promoCal">AdGroup</p>
                <select id="inputSelect1" className="form-control">
                  <option selected>AdGroup_1</option>
                  <option>...</option>
                </select>
              </div>
              <div className="col-md-4">
                <p className="promoCal">Offer</p>
                <select id="inputSelect1" className="form-control">
                  <option selected>20% off</option>
                  <option>...</option>
                </select>
              </div>

            </div>
              <div>
                <Bar data={data} options={chartOptions}/>
              </div>
            </div>
          </div>
        </div>

      </div>
    )
  }
}

export default PerformanceQuantity
