import React, { Component } from 'react';
import Select from 'react-select';
import AnalysisTable from '../MarkdownAnalysis/AnalysisTable';
import moment from 'moment';
import LineChart from '../support/Charts/LineChart';
import './index.css';

const options2 = {
  plugins: {
    datalabels: {
      display: false,
    },
  },
  legend: {
    display: false,
    position: 'bottom',
    labels: {
      boxWidth: 12,
    },
    fontFamily: 'Helvetica',
    fontSize: '12px',
  },
  maintainAspectRatio: false,
  scales: {
    yAxes: [
      {
        gridLines: {
          display: true,
        },
        ticks: {
          fontSize: 13,
        },
        scaleLabel: {
          show: true,
          display: true,
          fontSize: 14,
          fontStyle: 'bold',
          fontColor: '#000',
          labelString: 'Margin %',
        },
      },
    ],
    xAxes: [
      {
        id: 'x-axis-0',
        gridLines: {
          // color: '#aeaeae5e',
          display: false,
        },
        ticks: {
          autoSkip: true,
          autoSkipPadding: 40,
          maxRotation: 0,
        },
        scaleLabel: {
          display: true,
          // labelString: "Time (week)",
          fontWeight: 900,
          fontStyle: 'bold',
        },
      },
    ],
  },
};
const options = {
  plugins: {
    datalabels: {
      display: false,
    },
  },
  legend: {
    display: false,
    position: 'bottom',
    labels: {
      boxWidth: 12,
    },
    fontFamily: 'Helvetica',
    fontSize: '12px',
  },
  maintainAspectRatio: false,
  scales: {
    yAxes: [
      {
        gridLines: {
          display: true,
        },
        ticks: {
          fontSize: 13,
        },
        scaleLabel: {
          show: true,
          display: true,
          fontSize: 14,
          fontStyle: 'bold',
          fontColor: '#000',
          labelString: 'Sell Through %',
        },
      },
    ],
    xAxes: [
      {
        id: 'x-axis-0',
        gridLines: {
          // color: '#aeaeae5e',
          display: false,
        },
        ticks: {
          autoSkip: true,
          autoSkipPadding: 40,
          maxRotation: 0,
        },
        scaleLabel: {
          display: true,
          // labelString: "Time (week)",
          fontWeight: 900,
          fontStyle: 'bold',
        },
      },
    ],
  },
};
class MarkdownAnalysis extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    const borderColors = ['#44677B', '#A6CEE3', '#83BE8C'];
    const backgroundColors = ['#eaecf6', '#f1e8ef', '#faf4de'];
    let legendPercentages = [...new Set(this.props.markdownAnalysisGraphData.weekly_markdown)];

    const datasets = legendPercentages.map(() => []);
    this.props.markdownAnalysisGraphData.weekly_markdown.forEach((markdown, index) => {
      if (datasets[legendPercentages.indexOf(markdown)].length == 0 && index !== 0) {
        datasets[legendPercentages.indexOf(markdown)] = [
          ...datasets[legendPercentages.indexOf(markdown) - 1],
        ];
      }
      datasets[legendPercentages.indexOf(markdown)].push(
        this.props.markdownAnalysisGraphData.sellThrough[index]
      );
    });
    const datasets1 = legendPercentages.map(() => []);
    this.props.markdownAnalysisGraphData.weekly_markdown.forEach((markdown, index) => {
      if (datasets1[legendPercentages.indexOf(markdown)].length == 0 && index !== 0) {
        datasets1[legendPercentages.indexOf(markdown)] = [
          ...datasets1[legendPercentages.indexOf(markdown) - 1],
        ];
      }
      datasets1[legendPercentages.indexOf(markdown)].push(
        this.props.markdownAnalysisGraphData.margin[index]
      );
    });
    const labels = this.props.markdownAnalysisGraphData.weekNum.map((date) =>
      moment(date).format('MMM DD')
    );
    let lineChartData = {
      labels: labels,
      datasets: datasets.map((item, index) => ({
        data: item,
        borderColor: borderColors[index],
        backgroundColor: backgroundColors[index],
      })),
    };
    let lineChartData2 = {
      labels: labels,
      datasets: datasets1.map((item, index) => ({
        data: item,
        borderColor: borderColors[index],
        backgroundColor: backgroundColors[index],
      })),
    };

    legendPercentages = legendPercentages.map(
      (percentage) => `${(Number(percentage) * 100).toFixed(2)}%`
    );
    return (
      <div>
        <div className="mb-4">
          <div className="row skuLabelStyle">
            <label className="skuLabel">SKU</label>
            <div className="col-md-2">
              <Select
                placeholder="SKU"
                value={this.props.selectedSkuId}
                options={this.props.optionSku}
                onChange={this.props.selectedSkuHandler}
              />
            </div>
          </div>

          <div className="row">
            <div className="col-md-8" style={{ paddingLeft: '4rem' }}>
              <div className="anu">
                <div className="chartContainer">
                  <div className="row j-c-c">
                    {legendPercentages.map((percentage, index) => (
                      <div className="col-md-3 d-flex" key={`${Math.random() + index}`}>
                        <p
                          className="colorBox"
                          style={{ backgroundColor: backgroundColors[index] }}
                        />
                        <span className="left-space">{percentage}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
              <LineChart data={lineChartData} options={options} />
              <label className="sellThroughLabel">Margin %</label>
              <div className="chartContainer">
                <div className="row j-c-c">
                  {legendPercentages.map((percentage, index) => (
                    <div className="col-md-3 d-flex" key={`${Math.random() + index}`}>
                      <p
                        className="colorBox"
                        style={{ backgroundColor: backgroundColors[index] }}
                      />
                      <span className="left-space">{percentage}</span>
                    </div>
                  ))}
                </div>
              </div>
              <LineChart data={lineChartData2} options={options2} />
            </div>
            <div className="col-md-4" style={{ paddingTop: '0rem' }}>
              <AnalysisTable tableData={this.props.analysisTable || []} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default MarkdownAnalysis;
