import React, { Component } from "react";
import { Bar } from "react-chartjs-2";

const options = {
  plugins: {
    datalabels: {
      display: false
    }
  },
  scales: {
    xAxes: [
      {
        stacked: true,
        gridLines: {
          display: false
        },
        scaleLabel: {
          display: true,
          labelString: "Year"
        },
        barPercentage: 0.3
      }
    ],
    yAxes: [
      {
        stacked: true,
        gridLines: {
          display: false
        },
        scaleLabel: {
          display: true,
          labelString: "COGS"
        }
      },
      {
        stacked: true,
        position: "right",
        gridLines: {
          display: false
        },
        scaleLabel: {
          display: true,
          labelString: "Ratio"
        }
      }
    ]
  },
  legend: {
    position: "bottom",
    labels: {
      boxWidth: 12
    }
  },
  maintainAspectRatio: false
};

class BarLine extends Component {
  render() {
    return (
      <div
        className="bar-line-chart"
        style={this.props.height && { height: this.props.height }}
      >
        <Bar data={this.props.data} options={this.props.options || options}  />
      </div>
    );
  }
}

export default BarLine;
