import React, { Component } from "react";
import SelectProducts from "./AdGroupSelectProducts";
export class CreateStoreCluster extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showSearchProducts: true,
      showSelectProducts: false
    };
    this.importFile = this.importFile.bind(this);
  }
  showSelectProducts = e => {
    this.setState({
      showSearchProducts: false,
      showSelectProducts: true
    });
  };
  importFile(e) {
    console.log("deii");
    document.getElementById("selectedFile").click();
  }
  render() {
    return (
      <div>
        {this.state.showSearchProducts ? (
          <div className="cards cardStyles childCompStyling">
            <div id="cardbox">
              <div className="createClusterDiv">
                <div className="importIconDiv">
                  <p>
                    <i
                      className="fa fa-download importIcon"
                      aria-hidden="true"
                    />
                  </p>
                  <div className="clusterStyling">
                    <p id="dragFileText">Drag and Drop File Here</p>
                    <input
                      type="file"
                      id="selectedFile"
                      style={{ display: "none" }}
                    />
                    <p id="textsub2">
                      Or{" "}
                      <a className="linkStyling" onClick={this.importFile}>
                        {" "}
                        Choose File
                      </a>{" "}
                      to upload{" "}
                    </p>
                  </div>
                </div>
                <div className="strike adGroupSpacing">
                  <span>Or</span>
                </div>
                <div className="inner-addon right-addon adGroupSpacing">
                  <i
                    className="fa fa-search searchIconPadding"
                    aria-hidden="true"
                  />
                  <input
                    type="text"
                    className="form-control searchTextBoxStyling"
                    placeholder="Search/ Copy paste Sku's"
                  />
                </div>
                <div className="strike adGroupSpacing">
                  <span>Or</span>
                </div>
                <div className="row adGroupStyling">
                  <div className="col-md-3">
                    <p className="adGroupSearchProdText">Shop Description</p>
                    <select
                      id="inputSelect1"
                      className="form-control adGroupSearchProdDropdown"
                    >
                      <option selected>Promo_1</option>
                      <option>...</option>
                    </select>
                  </div>
                  <div className="col-md-3">
                    <p className="adGroupSearchProdText">Merch Cat</p>
                    <select
                      id="inputSelect1"
                      className="form-control adGroupSearchProdDropdown"
                    >
                      <option selected>Choose AdGroup</option>
                      <option>...</option>
                    </select>
                  </div>
                  <div className="col-md-3">
                    <p className="adGroupSearchProdText">AdGroup</p>
                    <select
                      id="inputSelect1"
                      className="form-control adGroupSearchProdDropdown"
                    >
                      <option selected>Choose AdGroup</option>
                      <option>...</option>
                    </select>
                  </div>
                </div>
                <div className="searchProductsBtn text-center">
                  <button
                    type="button"
                    className="btn btn-outline-primary btnPromo"
                    onClick={e => this.showSelectProducts(e)}
                  >
                    Search
                  </button>
                </div>
              </div>
            </div>
          </div>
        ) : null}
        {this.state.showSelectProducts ? <SelectProducts /> : null}
      </div>
    );
  }
}
export default CreateStoreCluster;
