import axios from 'axios';
import { config } from '../utils/config';

const draftStausFinalizeAction = {
  getMarkdowns: (payload) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/getMarkDowns/`,
        method: 'POST',
        data: payload,
      })
        .then((resp) => {
          const { columnHeaders, markdown_data } = resp.data.data;
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          dispatch({
            type: 'SET_MARK_DOWNS',
            payload: resp.data.data.markdown_data.length
              ? resp.data.data
              : 'No data found. Please try with different filters.',
          });
          dispatch({
            type: 'SET_MD_COLUMN_HEADERS',
            value: columnHeaders,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return resp.data.data;
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  setMarkDowns: (payload) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_MARK_DOWNS',
        value: payload,
      });
    };
  },
  setMDColumnHeaders: (payload) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_MD_COLUMN_HEADERS',
        value: payload,
      });
    };
  },
  sendForApproval: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/updateMarkdownStatus/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return resp;
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return err;
        });
    };
  },
};

export default draftStausFinalizeAction;
