import React, { useState, useEffect } from 'react';

const InputWithPercentageTag = (rowObj) => {
  const [state, setState] = useState({
    col: rowObj.col,
    value: rowObj.value,
  });
  // useEffect(() => {
  //   rowObj.changedRow(state);
  // });
  useEffect(() => {
    if (rowObj.value !== state.value) {
      let obj = {
        col: rowObj.col,
        value: rowObj.value,
      };
      setState(obj);
    }
  });
  const handleChange = (event) => {
    let obj = {
      col: event.target.name,
      value: event.target.value,
    };
    setState(obj);
    obj.rowNumber = rowObj.rowNumber;
    rowObj.changedRow(obj);
  };
  return (
    <div
      className={`col ${props.offerType ? 'ppWidth' : 'percenWidth'}`}
      key={rowObj.rowNumber + 1}>
      <div className="input-group" style={{ width: '90%' }}>
        {this.props.offerType == 'pp' ? (
          <div class="input-group-prepend">
            <span class="input-group-text">$</span>
          </div>
        ) : null}

        <input
          type="number"
          onChange={(e) => handleChange(e)}
          name={state.col}
          step="5"
          // onChange={(e) => this.onComparisonInputChange(e, col, row, rowNumber, index, i)}
          className="form-control border-style"
          value={state.value}
          disabled={rowObj.row.scenarios == 'Optimal' ? true : false}
        />
        <div className="input-group-append">
          {this.props.offerType == 'pp' ? (
            <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
              PP
            </button>
          ) : (
            <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
              <i className="fa fa-percent" />
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default InputWithPercentageTag;
