import React, { Component } from 'react';
import { connect } from 'react-redux';
import MarkDownActions from '../../../actions/markdowns';
import Filters from '../../../services/filters';
import DataTable from '../../../services/reactTable';
import Modal from '../../../services/modal';
import ColumnSelection from '../../columnSelection';
import StatusListCount from './statusListCount';
import Select from '../../../utils/reactDropDownSelect';
import Coldef from './coldef';
import styled from 'styled-components';
import _ from 'lodash';
import { cloneDeep } from 'lodash';
import NoData from '../../noTableData';
import moment from 'moment';

class Status extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectStatusValues: [],
      comment: '',
      isComment: false,
      rowDetails: {},
      statusCounts: {},
      selectedLabels: [],
      selectedItems: [],
      columnOptions: [],
      selLabels: [],
      columns: [],
    };
  }

  onCellClick = (row) => {
    if (row.value === 'Rejected') {
      this.setState({
        isComment: true,
        rowDetails: row.row.original,
      });
    }
  };
  setComment = (e) => {
    this.setState({
      comment: e.target.value,
    });
  };

  onCloseComment = () => {
    this.setState({
      isComment: false,
      rowDetails: {},
    });
  };
  onSaveComment = () => {
    let tableData = cloneDeep(this.props.getMarkDowns);
    tableData.forEach((row) => {
      if (
        this.state.rowDetails.md_plan === row['md_plan'] &&
        this.state.rowDetails.scenario === row['scenario']
      ) {
        row.comment = this.state.comment;
      }
    });
    this.props.setStatusData(tableData);
    this.setState({
      isComment: false,
      rowDetails: {},
    });
  };

  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };
  onFilter = (selectedFilters) => {
    let payload = { ...selectedFilters };
    payload.startDate = moment(payload.startDate).format('MM-DD-YYYY');
    payload.endDate = moment(payload.endDate).format('MM-DD-YYYY');
    payload['week'] = '';
    payload['status'] = 'draft';
    this.props.runMarkDown(payload).then((resp) => {
      let tempStatusObj = {};
      this.props.getMarkDowns.forEach((row) => {
        if (tempStatusObj.hasOwnProperty(row.status)) {
          tempStatusObj[row.status] += 1;
        } else {
          tempStatusObj[`${row.status}`] = 1;
        }
      });
      this.setState({
        statusCounts: tempStatusObj,
      });
      if (resp.columnHeaders && Object.keys(resp.columnHeaders).length) {
        let setDefaultColDef = Coldef().filter((col) =>
          resp.columnHeaders.default.includes(col.Header)
        );
        let setDefaultLabel = [];
        let setDefaultColumnOptions = [];
        let setDefaultItems = [];
        let colHeaders = _.cloneDeep(resp.columnHeaders);
        let labels = Object.keys(colHeaders);
        labels.splice(labels.indexOf('fixed'), 1);
        labels.splice(labels.indexOf('default'), 1);
        for (const label of labels) {
          function diff(A = resp.columnHeaders[label], B = resp.columnHeaders.default) {
            return A.filter(function (a) {
              return B.indexOf(a) !== -1;
            });
          }

          if (diff().length) {
            setDefaultColumnOptions.push(...resp.columnHeaders[label]);
            setDefaultItems.push(...diff());
            setDefaultLabel.push(label);
          }
        }
        let options = setDefaultColumnOptions.map((label) => {
          return {
            label: label,
            value: label,
          };
        });
        let selectedItems = setDefaultItems.map((label) => {
          return {
            label: label,
            value: label,
          };
        });
        this.setState({
          columns: setDefaultColDef,
          selLabels: setDefaultLabel,
          selectedItems: selectedItems,
          columnOptions: options,
        });
      }
    });
  };

  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.getColumnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.getColumnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
        });
      }
    }
  };
  onHandleSelect = (selColOpt) => {
    //finding diff on adding option
    let onAddDiff = _.differenceWith(selColOpt, this.state.selectedItems, _.isEqual);
    //finding diff on removing option
    let onRemoveDiff = _.differenceWith(this.state.selectedItems, selColOpt, _.isEqual);

    //Adding selected option and its respective column to the table
    if (onAddDiff.length) {
      //selected item label
      let item = onAddDiff[0].label;
      //finding the selected column from the main column definitions
      let findSelColFromMain = Coldef().filter((col) => col.Header === item);

      let currentCols = _.cloneDeep(this.state.columns);
      //adding selected column before actions column
      currentCols.splice(currentCols.length - 1, 0, findSelColFromMain[0]);
      this.setState({
        selectedItems: [...selColOpt],
        columns: currentCols,
      });
    }
    //Removing selected option and its respective column from the table
    if (onRemoveDiff.length) {
      //selected item label
      let item = onRemoveDiff[0].label;
      let itemIndex = this.state.selectedItems.map((item) => item.label).indexOf(item);
      let colIndex = this.state.columns.map((col) => col.Header).indexOf(item);
      let currentItems = _.cloneDeep(this.state.selectedItems);
      currentItems.splice(itemIndex, 1);
      let currentCols = _.cloneDeep(this.state.columns);
      currentCols.splice(colIndex, 1);
      console.log(currentCols, 'removedCOls');
      this.setState({
        selectedItems: currentItems,
        columns: currentCols,
      });
    }
  };

  render() {
    return (
      <>
        <div className="card CalendarMain promoCalendarDiv1 mt-3">
          <h4 className="promoCal-brdcrm mb-10">
            Markdown Optimization /<span className="font-bold color-blue">Status</span>
          </h4>
          <hr style={{ marginTop: '1px' }} />
          <Filters
            drop
            onFilter={this.onFilter}
            hierarchy={[
              'hierarchy1',
              'hierarchy2',
              'hierarchy3',
              'hierarchy4',
              'hierarchy5',
              'hierarchy6',
              'hierarchy7',
            ]}
          />
        </div>
        {this.props.getMarkDowns && this.props.getMarkDowns.length ? (
          <div className="card CalendarMain promoCalendarDiv1 mt-3">
            <ColSelBtn>
              {this.props.getColumnHeaders && Object.keys(this.props.getColumnHeaders).length && (
                <ColumnSelection
                  onHandleCheckBox={this.onHandleCheckBox}
                  columnHeaders={this.props.getColumnHeaders}
                  onHandleSelect={this.onHandleSelect}
                  columnOptions={this.state.columnOptions}
                  selectedItems={this.state.selectedItems}
                  selLabels={this.state.selLabels}
                />
              )}
              <div style={{ padding: '1rem' }} className="flexRow">
                <label>Status</label> &nbsp;
                <Select
                  multi={true}
                  clearable={true}
                  onHandleSelect={this.onHandleStatusSelect}
                  options={this.props.getStatusOptions}
                  selectedItems={this.state.selectStatusValues}
                />
              </div>
            </ColSelBtn>
            {this.state.statusCounts && Object.keys(this.state.statusCounts).length ? (
              <StatusListCount statusCounts={this.state.statusCounts} />
            ) : (
              ''
            )}
            <DataTable
              columns={this.state.columns}
              data={this.props.getMarkDowns}
              visibleColumns="markDownStatus"
              getColumnProps={(column) => ({
                // onClick: () => this.onColumnClick(column),
              })}
              getCellProps={(cellInfo) => ({
                onClick: () => this.onCellClick(cellInfo),
                style: {
                  backgroundColor: this.bgmCol(cellInfo),
                  color: this.txtCol(cellInfo),
                  margin: '4px 4px',
                  borderRadius: '2px',
                },
              })}
            />
          </div>
        ) : (
          <NoData description="Please select filters to show Markdown plans." />
        )}
        <Modal
          size="sm"
          show={this.state.isComment}
          header="Add Comment"
          onHide={this.onCloseComment}
          onSaveComment={this.onSaveComment}>
          <textarea id="commentTextArea" rows="2" cols="20" onChange={this.setComment}></textarea>
        </Modal>
      </>
    );
  }
}
