import React, { Component } from "react";
import ReactDOM from "react-dom";
import "antd/dist/antd.css";
import { Table } from "antd";


class TableComponents extends Component {

    render() {


        var chartData = this.props.data;
        console.log('chartData in table', chartData);
        let data = chartData.data;
        let labels = [];
        let datasets = [];
        let dataColumns = chartData.labels;

        for (var key in dataColumns) {
            dataColumns[key].map((label) => {
                labels.push(
                    {
                        title: label,
                        dataIndex: label,
                        sorter: (a, b) => a[label].length - b[label].length,
                        sortDirections: ["descend"],
                        width: 500,
                    },
                );
            })
        }
        let numberOfRows ;
        for(var key in data){
            numberOfRows = Object.values(data[key]).length ;
            break;
        }
        console.log('rows number ----', numberOfRows);
        function TableData(key) {  //object creation by constructor method to avoid object by reference
            this.key = key;
          }

        for(let i=0; i<data.data.length;i++) {
            var tableData = new TableData(i+1);
            for(let j=0; j<dataColumns.header.length; j++) {
                var obj = dataColumns.header[j];
                tableData[obj] = data.data[i][j]
            }
            datasets.push(tableData);
        }

        return (
            <Table columns={labels} dataSource={datasets} scroll={{ y: 300 }} pagination={false} />
        )
    }
};


export default TableComponents;
