import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import RadioPriceRev from "./generateRecommendationRadios";
import Select from "react-select";
import pricingStyle from "../pricingStyle.css";
import BarChart from "../../components/support/Charts/Barchart";
import _ from "lodash";

export class GenerateRecommendation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedRegion: null,
      selectedRadio: null,
      sDate: null,
      eDate: null,
      showGraphs: false
    };
  }
  componentDidMount() {}
  callBackForDateRange = (sDate, eDate) => {
    this.setState({
      sDate: sDate,
      eDate: eDate
    });
  };
  showGraphsCallback = showGraph => {
    console.log("show graph",showGraph)
    this.setState({
      showGraphs: showGraph
    });
  };
  render() {
    let graphItemsQuantity = [];
    let graphItemsMargin = [];
    let graphItemsRevenue = [];
    let graphTopLabels = [];
    let totalUnits = [];

    //graph code-------------------------
    if (
      this.props.genarateRecommendationData.data &&
      this.props.genarateRecommendationData.data[0] != null
    ) {
      var graphArr = this.props.genarateRecommendationData.data[0];
      graphItemsQuantity.push(graphArr.total_cp_units, graphArr.total_rp_units);
      graphItemsMargin.push(graphArr.cp_margin, graphArr.rp_margin);
      graphItemsRevenue.push(graphArr.cp_rev, graphArr.rp_rev);
      if (
        graphArr.qualPre &&
        graphArr.marPre &&
        graphArr.revPre &&
        graphArr.rpc &&
        graphArr.cp_price != null
      ) {
        graphTopLabels.push(
          graphArr.qualPre.toFixed(2),
          graphArr.marPre.toFixed(2),
          graphArr.revPre.toFixed(2)
        );
        totalUnits.push(graphArr.rpc.toFixed(0), graphArr.cp_price.toFixed(0));
      }
    }

    const barOption = {
      tooltips: {
        callbacks: {
          label: function(tooltipItem, data) {
            return `${tooltipItem.yLabel
              .toFixed(0)
              .toString()
              .replace(/\B(?=(\d{3})+(?!\d))/g, ",")}`;
          }
        }
      },
      legend: { position: "bottom", display: false },
      plugins: {
        datalabels: {
          display: false
        }
      },
      scales: {
        xAxes: [
          {
            barPercentage: 0.3,
            ticks: {
              fontSize: 13,
              fontColor: "#44677b",
              fontStyle: "bold"
            },
            gridLines: {
              // display:false,
              drawOnChartArea: false
            }
          }
        ],
        yAxes: [
          {
            ticks: {
              display: true,
              beginAtZero: true
            },

            gridLines: {
              display: false,
              drawOnChartArea: false
            }
          }
        ]
      }
    };
    const data1 = {
      labels: ["CPrice", "RPrice"],
      datasets: [
        {
          label: "Quantity",
          data: graphItemsQuantity || [],
          borderWidth: 1,
          backgroundColor: ["rgb(67, 103, 124)", "rgb(208, 116, 68)"]
        }
      ]
    };
    const data2 = {
      labels: ["CPrice", "RPrice"],
      datasets: [
        {
          label: "Margin",
          data: graphItemsMargin || [],
          borderWidth: 1,
          backgroundColor: ["rgb(67, 103, 124)", "rgb(208, 116, 68)"]
        }
      ]
    };
    const data3 = {
      labels: ["CPrice", "RPrice"],
      datasets: [
        {
          label: "Revenue",
          data: graphItemsRevenue || [],
          borderWidth: 1,
          backgroundColor: ["rgb(67, 103, 124)", "rgb(208, 116, 68)"]
        }
      ]
    };
    return (
      <div className="mt-10 cntnt-padding">
        <RadioPriceRev
          totalUnits={totalUnits}
          CategoryId={this.props.CategoryId}
          CheckDateRange={this.callBackForDateRange}
          SubCategoryId={this.props.SubCategoryId}
          BrandId={this.props.BrandId}
          ClassId={this.props.ClassId}
          SubClassId={this.props.SubClassId}
          SkuId={this.state.skuId}
          CallbackForDate={this.props.CallbackForDate}
          showGraphsCallback={this.showGraphsCallback}
        />
        {this.state.showGraphs ? (
          <div className="row">
            <div className="col-md-4">
              <div className="blue-backgr">
                <p className="line-label1">Quantity
                <br/>
                <span className="grapTopLabels">{graphTopLabels[0]}%
                {graphTopLabels[0] > 0 ? (
                <i class="fa fa-caret-up upIconC" aria-hidden="true"></i>
              ) : (
                <i class="fa fa-caret-down caretRed" aria-hidden="true"></i>
              )}</span></p>
                <BarChart data={data1} options={barOption} />
              </div>
            </div>
            <div className="col-md-4">
              <div className="blue-backgr">
                <p className="line-label1">Margin
                <br/>
                <span className="grapTopLabels">{graphTopLabels[1]}%
                {graphTopLabels[1] > 0 ? (
                <i class="fa fa-caret-up upIconC" aria-hidden="true"></i>
              ) : (
                <i class="fa fa-caret-down caretRed" aria-hidden="true"></i>
              )}</span></p>

                <BarChart data={data2} options={barOption} />
              </div>
            </div>
            <div className="col-md-4">
              <div className="blue-backgr">
                <p className="line-label1">Revenue
                <br />
                <span className="grapTopLabels">{graphTopLabels[2]}%
                {graphTopLabels[2] > 0 ? (
                <i class="fa fa-caret-up upIconC" aria-hidden="true"></i>
              ) : (
                <i class="fa fa-caret-down caretRed" aria-hidden="true"></i>
              )}</span>
                </p>

                <BarChart data={data3} options={barOption} />
              </div>
            </div>
          </div>
        ) : (
          <div className="" style={{ height: "150px" }}></div>
        )}
        <hr />
      </div>
    );
  }
}

export default GenerateRecommendation;
