const initialState = {
  inventoryInfoData: '',
  cadenceData: [],
  cadenceData: [
    [
      { W42: '40', W43: '40', W44: '40', W45: '40', W46: '40', W47: '40' },
      { W42: '50', W43: '50', W44: '50', W45: '50', W46: '50', W47: '50' },
    ],
  ],
  csv_names: [],
  editPlanDetails: [],
  columnHeaders: {
    default: {
      'SKU ID': 'SKU_Id',
      'Style Description': 'style_desc',
      'Revenue $': 'revenue',
      'PVH Planned units': 'planned_units',
      'Markdown units': 'units',
      'AUR $': 'aur',
      'Margin %': 'margin_percentage',
      'Margin $': 'margin',
      'AUM $': 'aum',
      'ST %': 'sellThrough',
    },
    fixed: {
      'SKU ID': 'SKU_Id',
      'Style Description': 'style_desc',
      'ST %': 'sellThrough',
      'Margin %': 'margin_percentage',
    },
    Sales: {
      'Revenue $': 'revenue',
      'PVH Planned units': 'planned_units',
      'Markdown units': 'units',
      'AUR $': 'aur',
    },
    Margin: {
      'Margin $': 'margin',
      'AUM $': 'aum',
    },
  },
};

export default function (state = initialState, action) {
  switch (action.type) {
    case 'GET_INVENTORY_INFO':
      return {
        ...state,
        inventoryInfoData: action.payload,
      };
    case 'RESET_INVENTORY_INFO_DATA':
      return {
        ...state,
        inventoryInfoData: '',
        cadenceData: [],
        csv_names: [],
      };
    case 'GET_CADENCE_DATA':
      return {
        ...state,
        cadenceData: {
          ...state.cadenceData,
        },
        csv_names: action.csv_names,
        skus: action.skus,
        savedData: action.savedData,
      };
    case 'GET_SKUS_AND_CSV':
      return {
        ...state,
        csv_names: action.csv_names,
        skus: action.skus,
      };
    case 'MARKDOWN_SAVED_STATUS':
      return {
        ...state,
        status: action.status,
      };
    case 'GET_COLUMNHEADERS':
      return {
        ...state,
        columnHeaders: {
          ...state.columnHeaders,
        },
      };
    case 'GET_EDITPLAN_DETAILS':
      return {
        ...state,
        editPlanDetails: action.payload,
      };

    default:
      return {
        ...state,
      };
  }
}
