const storeConfigDefaultState = {
  styleGroups: [],
  style: [],
  styleConfigTableData: [],
};

export default (state = storeConfigDefaultState, action) => {
  switch (action.type) {
    case 'SET_STYLE_GROUPS':
      return {
        ...state,
        styleGroups: action.payload,
      };
    case 'SET_STYLE':
      return {
        ...state,
        style: action.payload,
      };
    case 'STYLE_CONFIG_TABLE_DATA':
      return {
        ...state,
        styleConfigTableData: action.payload,
      };
    default:
      return state;
  }
};
