import React, { Component } from "react";
import { connect } from "react-redux";
import Filters from "../../../services/filters";
import DataTable from "../../../services/reactTable";
import ColumnSelection from "../../columnSelection";
import Coldef from "./coldef";
import styled from "styled-components";
import RowData from "./data";

const options = [
  { label: "sell through", value: "1" },
  { label: "margin %", value: "2" },
  { label: "mark down %", value: "3" },
];

const attributes = ["Margin", "Sales", "Inventory"];

class Draft extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedItems: [],
      tableData: RowData,
    };
  }

  componentDidMount() {
    if (this.props.getTableData && this.props.getTableData.length) {
      this.setState({
        tableData: this.props.tableData,
      });
    }
  }

  onHandleSelect = (selectValues) => this.setState({ selectValues });
  onCellClick = (row) => {
    console.log(row, "cell clicked .....");
  };

  findStatusColor = (status, color) => {
    switch (status) {
      case "high":
        return color.high;
      case "medium":
        return color.medium;
      case "low":
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === "md_status")
      var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: "#F8E8E8",
      medium: "#FCF3D1",
      low: "#E8F8F5",
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === "md_status")
      var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: "#D38485",
      medium: "#F0C53A",
      low: "#66CFBA",
    };
    return this.findStatusColor(status, txtCol);
  };

  updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    this.setState((old) => {
      let val = old.tableData.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...row,
            [columnId]: value,
          };
        }
        return row;
      });
      return { tableData: val };
    });
  };

  render() {
    return (
      <>
        <div className="card CalendarMain promoCalendarDiv1 mt-3">
          <h4 className="promoCal-brdcrm mb-10">
            Markdown Optimization /
            <span className="font-bold color-blue">Draft</span>
          </h4>
          <hr style={{ marginTop: "1px" }} />
          <Filters
            filters={[
              "season",
              "hierarchy1",
              "hierarchy2",
              "hierarchy3",
              "hierarchy4",
              "hierarchy5",
              "hierarchy6",
              "hierarchy7",
            ]}
          />
        </div>
        <div className="card CalendarMain promoCalendarDiv1 mt-3">
          <ColSelBtn>
            <ColumnSelection
              tab="draft"
              options={this.props.getMetricOptions}
              onHandleSelect={this.onHandleSelect}
              selectedItems={this.state.selectedItems}
              attributes={attributes}
              contentRenderer={true}
            />
            <div style={{ padding: "1rem" }}>
              <button className="btn btn-primary">Send for Approval</button>
              &nbsp;
            </div>
          </ColSelBtn>
          <DataTable
            columns={Coldef()}
            data={this.props.getTableData}
            updateMyData={this.updateMyData}
            visibleColumns="markDownDrafts"
            isPagination={true}
            getColumnProps={(column) => ({
              // onClick: () => this.onColumnClick(column),
            })}
            getCellProps={(cellInfo) => ({
              onClick: () => this.onCellClick(cellInfo),
              style: {
                backgroundColor: this.bgmCol(cellInfo),
                color: this.txtCol(cellInfo),
                // textAlign: "center",
                margin: "4px 4px",
                borderRadius: "2px",
              },
            })}
          />
        </div>
      </>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    getTableData: store.draftStatusReducer.draftTableData,
    getMetricOptions: store.draftStatusReducer.metricOptions.draft,
  };
};

export default connect(mapStateToProps, null)(Draft);

const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;
