import React from 'react';
import Modal from 'react-awesome-modal';
import Select from 'react-select';

let mdTypeOptions = [
  { label: 'Hard MD', value: 'Hard MD' },
  { label: 'POS MD', value: 'POS MD' },
];
let mdLevelOptions = [
  { label: 'Overall', value: 'pp' },
  { label: 'SKU', value: 'percen' },
];
const GetComparisonHeader = (props) => {
  return (
    <div className="constraints-modal">
      <Modal
        visible={true}
        effect="fadeInDown"
        width="35%"
        height="100%"
        onClickAway={() => props.showConstaintsHandler(false)}>
        <div className="card-body pb-0 px-0">
          <h6 className="card-title mb-4 mt-2 mx-4" style={{ fontWeight: 'bold' }}>
            Add Additional Constraints
            <span
              onClick={() => props.showConstaintsHandler(false)}
              className="pull-right select-store-span">
              <i
                style={{ cursor: 'pointer' }}
                className="fa fa-times"
                onClick={() => props.showConstaintsHandler(false)}
                aria-hidden="true"
              />
            </span>
          </h6>
          <hr />
          <label className="input-label mt-4 mx-4">MD Level</label>
          <Select
            className="md-level-dropdown"
            placeholder="Select"
            value={props.mdLevel}
            options={mdLevelOptions}
            onChange={props.mdLevelHandler}
            //   isClearable
          />

          <hr />
          <div className="row mt-4 mx-4">
            <div className="col-5">
              <label className="input-label">Markdown type</label>
            </div>
            <div className="col-6">
              <Select
                placeholder="Select"
                value={props.markdownTypeVal}
                options={mdTypeOptions}
                onChange={props.markdownTypeHandler}
                //   isClearable
              />
            </div>
          </div>

          <div className="row mt-4 mx-4">
            <div className="col-5">
              {/* <input
                type="radio"
                name="selectedScenario"
                className="inverse-radio mr-2"
                // onChange={() => props.selectCadenceScenariosHandler(index)}
                // checked={false}
              /> */}
              <label className="input-label d-inline">MD Frequency</label>
            </div>
            <div className="col-6 pr-0 row">
              <div className="col-6 pr-0">
                <input
                  type="number"
                  placeholder="Min"
                  className="form-control border-style"
                  onChange={(e) =>
                    props.mdFrequencyHandler({ min: e.value, max: props.mdFrequency.max })
                  }
                  value={props.mdFrequency.min}
                />
              </div>
              <div className="col-6 pr-0">
                <input
                  type="number"
                  placeholder="Max"
                  className="form-control border-style"
                  onChange={(e) =>
                    props.mdFrequencyHandler({ min: props.mdFrequency.min, max: e.value })
                  }
                  value={props.mdFrequency.max}
                />
              </div>
            </div>
          </div>

          {/* <div className="row mt-4 mx-4">
            <div className="col-5">
              <input
                type="radio"
                name="selectedScenario"
                className="inverse-radio mr-2"
                // onChange={() => props.selectCadenceScenariosHandler(index)}
                checked={true}
              />
              <label className="input-label d-inline">No. of Distinct MD's</label>
            </div>
            <div className="col-7 row">
              <div className="col-6">
                <input
                  type="number"
                  placeholder="Min"
                  //   step="5"
                  className="form-control border-style"
                  //   onChange={(e) => this.onCadenceValue(e, col, row, rowNumber, index, i)}
                  //   value={row[col]}
                  //   disabled={this.props.activeTab === 'Optimal' || disableInput}
                />
              </div>
              <div className="col-6">
                <input
                  type="number"
                  placeholder="Max"
                  //   step="5"
                  className="form-control border-style"
                  //   onChange={(e) => this.onCadenceValue(e, col, row, rowNumber, index, i)}
                  //   value={row[col]}
                  //   disabled={this.props.activeTab === 'Optimal' || disableInput}
                />
              </div>
            </div>
          </div> */}

          <div className="row my-4 mx-4">
            <div className="col-5 pr-0">
              <label className="input-label">Min% - Max discount%</label>
            </div>
            <div className="col-6 pr-0 row">
              <div className="col-6 pr-0">
                <input
                  type="number"
                  placeholder="Min"
                  className="form-control border-style"
                  onChange={(e) =>
                    props.minMaxDiscountHandler({ min: e.value, max: props.minMaxDiscount.max })
                  }
                  value={props.minMaxDiscount.min}
                />
              </div>
              <div className="col-6 pr-0">
                <input
                  type="number"
                  placeholder="Max"
                  className="form-control border-style"
                  onChange={(e) =>
                    props.minMaxDiscountHandler({ min: props.minMaxDiscount.min, max: e.value })
                  }
                  value={props.minMaxDiscount.max}
                />
              </div>
            </div>
          </div>
          {/* <div className="row mt-4">
            <div className="col-5">
              <label className="input-label">Step Size %</label>
            </div>
            <div className="col-7 row">
              <div className="col-6">
                <input
                  type="number"
                  placeholder="Min"
                  //   step="5"
                  className="form-control border-style"
                  //   onChange={(e) => this.onCadenceValue(e, col, row, rowNumber, index, i)}
                  //   value={row[col]}
                  //   disabled={this.props.activeTab === 'Optimal' || disableInput}
                />
              </div>
              <div className="col-6">
                <input
                  type="number"
                  placeholder="Max"
                  //   step="5"
                  className="form-control border-style"
                  //   onChange={(e) => this.onCadenceValue(e, col, row, rowNumber, index, i)}
                  //   value={row[col]}
                  //   disabled={this.props.activeTab === 'Optimal' || disableInput}
                />
              </div>
            </div>
          </div>
        */}
          <div className="constraints-card-footer">
            <hr />
            <div className="text-center">
              <input
                type="checkbox"
                name="saveAsDefault"
                className="mr-2"
                // onChange={() => props.selectCadenceScenariosHandler(index)}
                // checked={false}
              />
              <label className="input-label d-inline">Save as default</label>
            </div>
            <div className="text-center mt-4">
              <button
                className="btn btn-primary"
                onClick={() => props.showConstaintsHandler(false)}>
                Add constraints
              </button>
            </div>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default GetComparisonHeader;
