import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { loginUser, hidrateUser } from '../../actions/auth';
// import {setAuthToken} from '../utils/setAuthToken' ;
import SideBarNew from '../../components/SideBar/SideBar';
import Header from '../../components/Header/Header';
import ContainerText from '../Content/containerText';
import { Route, Switch, Redirect } from 'react-router-dom';
import MarkdownCalender from '../CalenderMarkdown';
import ManuallyCreateMD from '../ManuallyCreateMD';
import Config from '../Config';
import DraftStatus from '../draftStatus';
import CreateInSeasonMarkdownFromHistorical from '../createInSeason/CreateInSeasonMarkdownFromHistorical';
import FinalizeMarkdown from '../Finalize/FinalizeMarkdown';
import CreateFromHistory from '../history/CreateFromHistory';
// import MarkdownConfiguration from '../configureMarkdown/markdownConfiguration';
import MarkdownConfiguration from '../Configuration/ConfigurationContainer';
import RecommendedMarkdown from '../recommendedMarkdown/RecommendedMain';
import InseasonHistory from '../createInSeason/CreateManuallyIn';
import GlobalPromos from '../CalenderMarkdown/GlobalPromos';

//-----------------Pricing Routes------------------------/

import MarkdownReporting from '../Reporting';
import PlannedReport from '../Planned Reports';
// import StyleConfig from "../../Markdown/components/StyleConfig"

// LicenseManager.setLicenseKey(
//   "Evaluation_License-_Not_For_Production_Valid_Until_9_October_2019__MTU3MDU3NTYwMDAwMA==6c2634c6b22d53d1bfccc435db170aee"
// );

//import ContainerText from './containerText'

class Layout extends Component {
  constructor(props) {
    super();
    this.state = {
      username: '',
      password: '',
      errors: {},
      errorMessage: false,
    };
  }

  render() {
    // const { errors, email, password} = this.state;
    const { path } = this.props.match;
    let token = this.props.authReducer ? this.props.authReducer.isAuthenticated : false;
    token = !token ? localStorage.getItem('token') : token;
    // <div className="d-md-flex h-md-100 align-items-center">

    return (
      <div className="wrapper">
        <SideBarNew />
        <div id="content">
          <Header />
          <ContainerText />
          <div id="sub-content">
            <Switch>
              <ProtectedRoute
                exact
                path={`${path}/create-manually-in-season`}
                auth={token}
                component={InseasonHistory}
              />
              <ProtectedRoute
                exact
                path={`${path}/Markdown/calender`}
                auth={token}
                component={MarkdownCalender}
              />
              <ProtectedRoute
                exact
                path={`${path}/Markdown/global-promo`}
                auth={token}
                component={() => <GlobalPromos />}
              />

              {/* <Route
              exact
              path={`${path}/create-markdown`}
              component={CreateMarkdown}
            /> */}
              <ProtectedRoute
                exact
                path={`${path}/create-markdown-in`}
                auth={token}
                component={CreateInSeasonMarkdownFromHistorical}
              />
              <ProtectedRoute
                exact
                path={`${path}/Markdown/reporting`}
                auth={token}
                component={MarkdownReporting}
              />
              <ProtectedRoute
                exact
                path={`${path}/Markdown/actual-planned-reports`}
                auth={token}
                component={PlannedReport}
              />
              <ProtectedRoute
                exact
                path={`${path}/Markdown/draftStatus`}
                auth={token}
                component={DraftStatus}
              />

              <ProtectedRoute
                exact
                path={`${path}/Markdown/styleConfig`}
                auth={token}
                component={Config}
              />
              <ProtectedRoute
                exact
                path={`${path}/Markdown/create-markdown`}
                auth={token}
                component={ManuallyCreateMD}
              />
              <ProtectedRoute
                exact
                auth={token}
                path={`${path}/markdown/create-in-season-md-from-historical`}
                component={CreateInSeasonMarkdownFromHistorical}
              />
              <ProtectedRoute
                exact
                auth={token}
                path={`${path}/Markdown/finalize`}
                component={FinalizeMarkdown}
              />
              <ProtectedRoute
                exact
                auth={token}
                path={`${path}/Markdown/history`}
                component={CreateFromHistory}
              />
              {/* <ProtectedRoute
              exact
              path={`${path}/Markdown/styleConfig`}
              auth={token}
              component={StyleConfig}
            /> */}
              <ProtectedRoute
                auth={token}
                path={`${path}/Markdown/configuration`}
                component={MarkdownConfiguration}
              />
              <ProtectedRoute
                exact
                auth={token}
                path={`${path}/recommended`}
                component={RecommendedMarkdown}
              />
            </Switch>
          </div>
        </div>
      </div>
    );
  }
}

Layout.propTypes = {
  loginUser: PropTypes.func.isRequired,
  hidrateUser: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired,
  success: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  authReducer: state.authReducer,
  errors: state.errors,
  success: state.success,
});

const ProtectedRoute = ({ component: Component, ...rest }) => {
  return (
    <Route
      {...rest}
      render={(props) => {
        if (rest.auth) {
          const allProps = { ...props, ...rest };
          return <Component {...allProps} />;
        } else {
          return (
            <Redirect
              to={{
                pathname: '/',
                state: {
                  from: props.location,
                },
              }}
            />
          );
        }
      }}
    />
  );
};

export default connect(mapStateToProps, { loginUser, hidrateUser })(withRouter(Layout));
