import React, { Component } from "react";
import Select from "react-select";
import { withRouter } from "react-router-dom";
import Tabs from "react-bootstrap/Tabs";
import Tab from "react-bootstrap/Tab";
import { connect } from "react-redux";
import "./markdownConfig.css";
import ConfigureModal from "./configModal";
import CreateSKUModal from "./createSkuModal";
import "../../../shared/css/settings.css";
import {
  getMarkdownConfigureData,
  actionMarkdownSkuCreate,
  actionDeleteMarkdownGroup,
  actionMarkdownSKugroupGraphData,
} from "../../actions/markdown";
import { getAllFilters } from "../../actions/markDownCommonActions";
import _ from "lodash";
import AlertDanger from "../../../utils/AlertMessage/alertDanger";
import AlertSuccess from "../../../utils/AlertMessage/alertSuccess";
var queryArray = [];

class MarkdownConfiguration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showModalFlag: false,
      showCreateModal: false,
      show: false,
      ind: 0,
      skuGroupName: "",
      createdSkuGroupName: "",
      key: "home",
      selectedskuid: [],
      selectedSkuGroup: [],
      selectedBrandId: [],
      skuselected: [],
      selectedbrands: [],
      selectedSkuLength: 0,
      disableFlag: true,
      diableCreateGroup: true,
      isSkuGroupFlag: false,
      isGroup: "no",
      filters: {
        category: null,
        subcategory: null,
        brand: null,
      },
      showSkuGroup: false,
      brandarr: [],
      showAlertDanger: false,
      diableDelete: true,
      showAlertSuccess: false,
      setMarkdownData: [],
    };
  }

  componentDidMount() {
    this.props.getAllFilters();
    this.props.getMarkdownConfigureData();
    let data1 = [];
    if (this.props.data) {
      this.props.data.map((item) => {
        data1.push(item);
      });
    }
  }
  // componentDidUpdate(prevProps) {
  // if(prevProps.markdownConfigData !== this.props.markdownConfigData){
  //     this.setState({
  //         setMarkdownData:this.props.markdownConfigData
  //     })
  // }
  // }
  onChange = (tag, value) => {
    var filters = this.state.filters;
    if (tag === "category") {
      filters.category = value;
      this.setState({
        filters: filters,
        disableFlag: false,
      });
    }
    if (tag === "subCategory") {
      this.setState({
        selectedSubCategory: value,
      });
    }
    if (tag === "brand") {
      this.setState({
        selectedBrand: value,
      });
    }
  };
  showMessageModal = (ind) => {
    this.setState({ showModalFlag: true, ind: ind ? ind : 0 });
  };
  onshowskuGroupModal = (ind) => {
    this.setState({ showModalFlag: true, ind: ind ? ind : 0 });
  };
  hideModalFlag = () => {
    this.setState({ showModalFlag: false });
    this.props.getMarkdownConfigureData();
  };

  onCreateSkuGroup = () => {
    let brandarr = [];
    // let found = [];
    // this.state.selectedskuid.map(items => {
    //   let found1 = _.find(this.props.markdownConfigData.skuData, {
    //     skuid: parseInt(items)
    //   });
    //   found.push(found1);
    // });
    // if (found) {
    //   found.map(item => this.state.brandarr.push(item.brand_id));
    // }
    // let checkUniq = _.uniq(this.state.brandarr);
    var selectedbrands = this.state.selectedbrands;
    var flag = true;
    selectedbrands.forEach((item, ind) => {
      var existing = selectedbrands[0];
      if (ind > 0) {
        if (item != existing) {
          flag = false;
        }
      }
    });
    brandarr.push(selectedbrands[0]);

    if (!flag) {
      this.setState({
        showAlertDanger: true,
      });
      setTimeout(() => {
        this.setState({
          showAlertDanger: false,
        });
      }, 2500);
    } else {
      this.setState({
        showCreateModal: true,
        brandarr: brandarr,
      });
    }
  };

  hideModalCreate = () => {
    this.props.getMarkdownConfigureData();
    this.setState({ showCreateModal: false });
  };

  onSearchSku(val) {
    if (val.length < 3 && val.length !== 0) {
      return;
    }

    this.props.getMarkdownConfigureData("?sku_id=" + val.toUpperCase());
  }
  onSearchSkuGroup(val) {
    if (val.length < 3 && val.length !== 0) {
      return;
    }

    this.props.getMarkdownConfigureData("?groupname=" + val);
  }

  onReset = () => {
    var filters = this.state.filters;
    filters.subcategory = null;
    filters.category = null;
    filters.brand = null;

    this.setState({
      filters,
    });
  };
  onDropdownchange(tag, value) {
    if (tag === "category") {
      this.setState({
        selectedCategory: value,
        disableFlag: false,
      });
    }

    if (tag === "subcategory") {
      this.setState({
        selectedSubCategory: value,
      });
    }
    if (tag === "brand") {
      this.setState({
        selectedBrand: value,
      });
    }
  }

  onSelectSkus = (e, val) => {
    let { selectedskuid, selectedbrands } = this.state;
    if (e.target.checked) {
      let value = val;
      selectedskuid.push(e.target.value);
      selectedbrands.push(value);
    } else {
      selectedbrands.forEach((item, index) => {
        if (item == val) {
          selectedbrands.splice(index, 1);
        }
      });
      selectedskuid.forEach((item, index) => {
        if (item == e.target.value) {
          selectedskuid.splice(index, 1);
        }
      });
    }

    this.setState({
      selectedskuid: selectedskuid,
      selectedbrands: selectedbrands,
      selectedSkuLength: selectedskuid.length,
    });
    if (this.state.selectedskuid.length > 0) {
      this.setState({
        diableCreateGroup: false,
      });
    }
  };

  onSelectSkuGroup = (value) => {
    let { selectedSkuGroup } = this.state;
    selectedSkuGroup.push(value);

    this.setState({
      selectedSkuGroup: selectedSkuGroup,
      skuGroupName: value,
    });
    if (this.state.selectedSkuGroup.length > 0) {
      this.setState({
        diableDelete: false,
      });
    }
  };
  tabHandler = (key) => {
    this.setState({
      key,
    });
    if (key === "home") {
      this.setState({
        showSkuGroup: false,
        isGroup: "no",
      });
    }
    if (key === "skugroup") {
      this.setState({
        showSkuGroup: true,
        isGroup: "yes",
      });
    }
  };
  onDeleteSkuGroup = () => {
    this.props.actionDeleteMarkdownGroup({
      skuid: this.state.selectedSkuGroup,
    });
    this.props.getMarkdownConfigureData();
    this.setState({
      showAlertSuccess: true,
    });
    setTimeout(() => {
      this.setState({
        showAlertSuccess: false,
      });
    }, 2500);
  };
  onFilterClick = (parms) => {
    this.setState({
      type: parms,
    });
    var category = this.state.selectedCategory
      ? this.state.selectedCategory.value
      : null;
    var sku = this.state.selectedSKU ? this.state.selectedSKU.value : null;
    var subCategory = this.state.selectedSubCategory
      ? this.state.selectedSubCategory.value
      : null;
    var brand = this.state.selectedBrand
      ? this.state.selectedBrand.value
      : null;

    var queryArray1 = null;
    var queryArray2 = null;
    var queryArray3 = null;

    if (category) {
      queryArray.push("categoryID=" + category);
      queryArray1 = category;
    }

    if (subCategory) {
      queryArray.push("subCategoryID=" + subCategory);
      queryArray2 = subCategory;
    }
    if (brand) {
      queryArray.push("brandID=" + brand);
      queryArray3 = brand;
    }

    this.props.getMarkdownConfigureData("?" + queryArray.join("&"));
    queryArray = [];
  };
  render() {
    let custGroupArray = [],
      brandArray = [],
      businessDescArray = [];
    if (
      this.props.markdownConfigData &&
      this.props.markdownConfigData.length > 0
    ) {
      var { skuData } = this.props.markdownConfigData.skuData;
    }

    if (this.props.markdownFilter && this.props.markdownFilter.data) {
      this.props.markdownFilter.data.category.map((filterData) => {
        custGroupArray.push({
          value: filterData.value,
          label: filterData.label,
        });
      });

      this.props.markdownFilter.data.subCategory.map((subcat) => {
        businessDescArray.push({
          value: subcat.value,
          label: subcat.label,
        });
      });
      this.props.markdownFilter.data.brand.map((brand) => {
        brandArray.push({
          value: brand.value,
          label: brand.label,
        });
      });
    }

    return (
      <div>
        <div className="alertDivs">
          {this.state.showAlertDanger ? (
            <AlertDanger
              message="Please select same brands!"
              show={this.state.showAlertDanger}
            />
          ) : null}

          {this.state.showAlertSuccess ? (
            <AlertSuccess
              message="Sub Category deleted successfully!"
              show={this.state.showAlertSuccess}
            />
          ) : null}
        </div>

        <div className="promo-setting-main card">
          <h4 className="font-size-14 setting-heading">
            <span className="font-bold color-blue">Configure</span>
          </h4>
          <hr className="mb-2" style={{ marginTop: "-4px" }} />
          <div className="row">
            <div className="col-md-6">
              <div className="row" style={{ padding: "1px 15px" }}>
                <div className="col-md-4">
                  <label className="font-size-13 font-bold mb-0">
                    Sub Department:
                  </label>
                  <Select
                    id="customerGroupSelect"
                    placeholder="Sub Department"
                    options={custGroupArray}
                    onChange={(e) => this.onDropdownchange("category", e)}
                    value={this.state.selectedCategory}
                  />
                </div>
                <div className="col-md-4">
                  <label className="font-size-13 font-bold mb-0">Class:</label>
                  <Select
                    id="businessDescSelect"
                    placeholder="Class"
                    options={businessDescArray}
                    value={this.state.selectedSubCategory}
                    onChange={(e) => this.onDropdownchange("subcategory", e)}
                    isDisabled={this.state.disableFlag}
                  />
                </div>
                <div className="col-md-4">
                  <label className="font-size-13 font-bold mb-0">Brand:</label>
                  <Select
                    id="brandSelect"
                    placeholder="Brand"
                    options={brandArray}
                    onChange={(e) => this.onDropdownchange("brand", e)}
                    value={this.state.selectedBrand}
                    isDisabled={this.state.disableFlag}
                  />
                </div>
              </div>
            </div>
            <div
              className="col-md-6 text-right"
              style={{ padding: "15px 44px 0px 0px" }}
            >
              <button
                className="btn btn-primary iconbuttonFilter"
                onClick={this.onFilterClick}
              >
                <span>
                  <i className="fa fa-filter" />
                  &nbsp; Filter
                </span>
              </button>
              <button
                onClick={() => this.onReset()}
                className="btn btn-primary iconbutton reset"
                style={{ marginLeft: "10px" }}
                disabled
              >
                <span>
                  <i className="fa fa-repeat" />
                  &nbsp; Reset
                </span>
              </button>
            </div>
          </div>
          <hr className="dotted-line mt-3" />
          <Tabs
            id="controlled-tab-example"
            activeKey={this.state.key}
            onSelect={(key) => this.tabHandler(key)}
          >
            <Tab eventKey="home" title="Sub Categories">
              <div className="row justify-content-center grid-padding">
                <div className="col-md-6">
                  <div className="input-group mb-3" style={{ width: "42%" }}>
                    <input
                      type="text"
                      class="form-control"
                      placeholder="Search"
                      onChange={(e) => this.onSearchSku(e.target.value)}
                    />
                    <div className="input-group-append">
                      <button
                        className="btn btn-side-percent-light"
                        type="button"
                      >
                        <i className="fa fa-search" />
                      </button>
                    </div>
                  </div>
                </div>
                <div className="col-md-6">
                  <button
                    className="btn btn-primary float-right"
                    onClick={() => this.onCreateSkuGroup()}
                    disabled={this.state.diableCreateGroup}
                  >
                    Create Sub category
                  </button>
                </div>
              </div>
              {_.isEmpty(
                this.props.markdownConfigData.skuData != undefined &&
                  this.props.markdownConfigData.skuData
              ) ? (
                <p className="text-center font-bold font-size-14 noDataMsg">
                  No data to show
                </p>
              ) : null}
              <div className="hrizntalascrlldiv">
                {this.props.markdownConfigData.skuData
                  ? this.props.markdownConfigData.skuData.map((item, index) => {
                      return (
                        <div className="grid-main-div">
                          <div className="gridBox">
                            <label className="font-size-13 label-gap ">
                              <input
                                type="checkbox"
                                value={item.skuid}
                                className="mr-2"
                                onChange={(e) =>
                                  this.onSelectSkus(e, item.brand_id)
                                }
                              />
                              {item.skuid}
                            </label>
                            <button
                              className="btn btn-primary btn-sm float-right gap-style"
                              onClick={() => this.showMessageModal(index)}
                            >
                              Configure
                            </button>
                            {item.lifecycle && item.lifecycle !== "null"
                              ? item.lifecycle.split(",").map((elem) => {
                                  return (
                                    <label
                                      style={{ backgroundColor: "#f8e0fe" }}
                                      className="tags-promo-setting"
                                    >
                                      {elem}
                                    </label>
                                  );
                                })
                              : ""}
                            {item.percentmax && item.percentmin ? (
                              <label
                                style={{ backgroundColor: "#E0F3FF" }}
                                className="tags-promo-setting "
                              >
                                {"(" +
                                  item.percentmin +
                                  "-" +
                                  item.percentmax +
                                  ")% off"}
                              </label>
                            ) : null}
                            {item.amtmax && item.amtmin ? (
                              <label
                                style={{ backgroundColor: "#B9EBF7" }}
                                className="tags-promo-setting"
                              >
                                {"$(" +
                                  item.amtmin +
                                  "-" +
                                  item.amtmax +
                                  ") off"}
                              </label>
                            ) : null}
                            {item.map ? (
                              <label
                                style={{ backgroundColor: "#FFE6B5" }}
                                className="tags-promo-setting"
                              >
                                {item.map + "%"}
                              </label>
                            ) : null}
                          </div>
                        </div>
                      );
                    })
                  : null}
              </div>
            </Tab>
            <Tab eventKey="skugroup" title="Sub category">
              <div className="">
                <div className="row justify-content-center grid-padding">
                  <div className="col-md-6">
                    <div className="input-group mb-3" style={{ width: "42%" }}>
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Search"
                        onChange={(e) => this.onSearchSkuGroup(e.target.value)}
                      />
                      <div className="input-group-append">
                        <button
                          className="btn btn-side-percent-light "
                          type="button"
                        >
                          <i className="fa fa-search" />
                        </button>
                      </div>
                    </div>
                  </div>
                  <div className="col-md-6">
                    <button
                      className="btn btn-primary float-right"
                      onClick={this.onDeleteSkuGroup}
                      disabled={this.state.diableDelete}
                    >
                      Delete
                    </button>
                  </div>
                </div>
                {_.isEmpty(
                  this.props.markdownConfigData.skuGroup != undefined &&
                    this.props.markdownConfigData.skuGroup
                ) ? (
                  <p className="text-center font-bold font-size-14 noDataMsg">
                    No data to show
                  </p>
                ) : null}
                <div className="hrizntalascrlldiv">
                  {this.props.markdownConfigData.skuGroup
                    ? this.props.markdownConfigData.skuGroup.map(
                        (item, index) => {
                          return (
                            <div className="grid-main-div" key={index}>
                              <div className="gridBox">
                                <label className="font-size-13 label-gap ">
                                  <input
                                    type="checkbox"
                                    value={item.id}
                                    className="mr-2"
                                    onChange={(e) =>
                                      this.onSelectSkuGroup(e.target.value)
                                    }
                                  />
                                  {item.groupname}
                                </label>
                                <button
                                  className="btn btn-primary btn-sm float-right gap-style"
                                  onClick={() =>
                                    this.onshowskuGroupModal(index)
                                  }
                                >
                                  Configure
                                </button>
                                {item.lifecycle && item.lifecycle !== "null"
                                  ? item.lifecycle.split(",").map((elem) => {
                                      return (
                                        <label
                                          style={{ backgroundColor: "#f8e0fe" }}
                                          className="tags-promo-setting"
                                        >
                                          {elem}
                                        </label>
                                      );
                                    })
                                  : ""}
                                {item.percentmax && item.percentmin ? (
                                  <label
                                    style={{ backgroundColor: "#E0F3FF" }}
                                    className="tags-promo-setting "
                                  >
                                    {"(" +
                                      item.percentmin +
                                      "-" +
                                      item.percentmax +
                                      ")% off"}
                                  </label>
                                ) : null}
                                {item.amtmax && item.amtmin ? (
                                  <label
                                    style={{ backgroundColor: "#B9EBF7" }}
                                    className="tags-promo-setting"
                                  >
                                    {"$(" +
                                      item.amtmin +
                                      "-" +
                                      item.amtmax +
                                      ") off"}
                                  </label>
                                ) : null}
                                {item.map ? (
                                  <label
                                    style={{ backgroundColor: "#FFE6B5" }}
                                    className="tags-promo-setting"
                                  >
                                    {item.map + "% "}
                                  </label>
                                ) : null}
                              </div>
                            </div>
                          );
                        }
                      )
                    : null}
                </div>
              </div>
            </Tab>
          </Tabs>
          {this.state.showModalFlag ? (
            <ConfigureModal
              showModal={this.state.showModalFlag}
              hideModal={() => this.hideModalFlag()}
              data={this.props.markdownConfigData}
              skuInd={this.state.ind}
              skuGroupName={this.state.skuGroupName}
              showSkuGroup={this.state.showSkuGroup}
              isSkuGroupFlag={this.state.isSkuGroupFlag}
              isGroup={this.state.isGroup}
              selectedskuid={this.state.selectedskuid}
            />
          ) : null}
          <CreateSKUModal
            showModal={this.state.showCreateModal}
            hideModal={() => this.hideModalCreate()}
            selectedskuid={this.state.selectedskuid}
            selectedSkuLength={this.state.selectedSkuLength}
            brandId={this.state.brandarr}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    markdownConfigData: state.markdown.markdownConfigData,
    markdownFilter: state.markDownFilter,
    markdownSkuGroupData: state.markdownSkuGroupData,
    deleteSkuGroupData: state.deleteSkuGroupData,
  };
};

const mapActionToProps = (dispatch) => {
  return {
    getAllFilters: (val) => dispatch(getAllFilters(val)),
    getMarkdownConfigureData: (val) => dispatch(getMarkdownConfigureData(val)),
    actionMarkdownSkuCreate: (val) => dispatch(actionMarkdownSkuCreate(val)),
    actionDeleteMarkdownGroup: (val) =>
      dispatch(actionDeleteMarkdownGroup(val)),
    actionMarkdownSKugroupGraphData: (val) =>
      dispatch(actionMarkdownSKugroupGraphData(val)),
  };
};

export default connect(
  mapStateToProps,
  mapActionToProps
)(withRouter(MarkdownConfiguration));
