import React, { Component } from 'react';
import { connect } from 'react-redux';
import Calendar from '../../lib/Calendar';

import CalendarWrapper from '../Content/Promotions/PromoCalendar/CalendarWrapper';
import { withRouter } from 'react-router-dom';
import 'react-datepicker/dist/react-datepicker.css';
import moment from 'moment';
import { isEqual } from 'lodash';
import PageLoader from '../../utils/Loader';
import {
  markdownCalendarDatePlot,
  markdownCalendarData,
  markdownCalendarGraphData,
} from '../Content/Promotions/PromoCalendar/calendarAction';
import AddSkuFilter from '../createMarkdown/AddSkuFilter';
import { getAllFilters } from '../../actions/markDownCommonActions';
import { getGlobalPromoPerMarkdown } from '../../actions/markdown_global_promo';
import _ from 'underscore';

import AlertDanger from '../../utils/AlertMessage/alertDanger';

import { Element, scroller } from 'react-scroll';

import MarkdownPart from './detailedMarkdown';

import GlobalPromosmark from './GlobalPromosMarkdown';

export class PromoCalendar extends Component {
  calendarData;

  constructor(props) {
    super(props);
    let matchQueryTimeSamp = document.location.search.match(/timeMonth=([^&]*)/i);
    let momentObj = matchQueryTimeSamp ? moment(matchQueryTimeSamp[1]) : null;
    let checkMatchQueryTimeSapm =
      matchQueryTimeSamp && matchQueryTimeSamp.length && matchQueryTimeSamp[1];

    this.state = {
      loader: false,
      showPromotionMain: false,
      showPromotionMainWithValues: false,
      startDate: checkMatchQueryTimeSapm ? momentObj.toDate() : new Date(),
      selectedYear: checkMatchQueryTimeSapm ? momentObj.year() : new Date().getFullYear(),
      selectedMonth: 'Sep',
      selectedView: 'Month',
      fiscal_monthnum: checkMatchQueryTimeSapm ? momentObj.month() : Number(new Date().getMonth()),
      fiscal_quarter: checkMatchQueryTimeSapm ? momentObj.format('Q') : 1,
      calendarWrapperArr: [],
      calendarWrapperArr1: [],

      historyStartDate: 'null',
      historyEndDate: 'null',
      historyQuarter: 'null',
      histortFiscalMonth: 'null',
      //moment(matchQueryTimeSt[1]).toDate()
      calendarDataArr: [],
      initialLoadFlag: true,
      selectedDate: checkMatchQueryTimeSapm ? momentObj.toDate() : null,

      selectedDepartment: null,
      selectedCategory: null,
      selectedSKU: null,
      selectedSubCategory: null,
      skuList: null,
      markdownId: null,
      markdownStartDate: null,
      markdownEndDate: null,
      markdownName: null,
      selectedMarkDownSku: {},
      showGraph: false,
      markdownlinkShow: 1,
      //filter states
      filterCategory: null,
      filterSubCategory: null,
      filterClass: null,
      filterSubClass: null,
      filterBrand: null,
      showPromoDetailTable: false,
      generateCadenceError: false,
      showToggleContinious: false,
    };

    this.calendarData = JSON.parse(localStorage.getItem('calendarData'));
  }
  componentWillMount() {
    this.setState({ loader: true });
    this.calendarData = JSON.parse(localStorage.getItem('calendarData'));
    let { markdownCalendarDatePlot } = this.props;
    let startDate = this.calendarData && this.calendarData.startDate,
      Quarter = this.calendarData && this.calendarData.quarter;

    let matchQueryTimeSt = document.location.search.match(/timeMonth=([^&]*)/i);

    let momentObjSt = matchQueryTimeSt ? moment(matchQueryTimeSt[1]) : null;

    if (matchQueryTimeSt && matchQueryTimeSt.length && matchQueryTimeSt[1]) {
      let year = momentObjSt.year() + 1;
      let selectedDate = momentObjSt.toDate();
      let yearMonth = momentObjSt.format('YYYY-MMM');
      let month = momentObjSt.month() + 1;
      let Quater = momentObjSt.format('Q');
      this.setState({
        fiscal_quarter: Quater,
        selectedDate: selectedDate,
        fiscal_monthnum: month,
        selectedYear: year,
        selectedMonth: yearMonth.slice(5, 8),
      });
    }

    let date = startDate && startDate !== 'null' ? startDate : new Date();
    let year =
        new Date(date).getMonth() !== 0
          ? new Date(date).getFullYear() + 1
          : new Date(date).getFullYear(),
      fiscalDate =
        new Date(date).getMonth() !== 0 ? new Date(moment(date).add(1, 'years')) : new Date(date);

    this.setState({
      selectedYear: year,
      selectedDate: fiscalDate,
    });
    if (Quarter) {
      this.setState({
        selectedView: 'Quarter',
      });
    }

    markdownCalendarDatePlot({
      fiscal_year: year,
    });

    // if (startDate && startDate !== null) {
    //   let year =
    //     new Date(startDate).getMonth() !== 0
    //       ? new Date(startDate).getFullYear() + 1
    //       : new Date(startDate).getFullYear();

    //   if (Quarter && Quarter !== null) {
    //     this.setState({
    //       selectedView: "Quarter"
    //     });
    //   }
    //   this.setState({
    //     selectedYear: year,
    //     selectedDate: new Date(startDate)
    //   });
    //   markdownCalendarDatePlot({
    //     fiscal_year: year
    //   });
    // } else {

    //   markdownCalendarDatePlot({
    //     fiscal_year: this.state.selectedYear
    //   });
    // }
  }
  //72 lines
  componentDidUpdate(oldProps, oldState) {
    this.calendarData = JSON.parse(localStorage.getItem('calendarData'));
    let {
      initialLoadFlag,
      selectedView,
      fiscal_quarter,
      fiscal_monthnum,
      selectedYear,
    } = this.state;
    let { calendarStrucure, calendarDataPerYear, markdownCalendarDatePlot } = this.props;

    let Quarter = this.calendarData && this.calendarData.quarter,
      fiscalMonth1 = this.calendarData && this.calendarData.fiscalMonth,
      fiscalMonth = 0,
      Month = new Date().getMonth();

    fiscalMonth = Month === 0 ? 1 : Math.ceil(Month / 3);

    //calling for 1st load from local storage
    if (oldProps.calendarStrucure !== calendarStrucure && initialLoadFlag) {
      let tempMonth =
        fiscalMonth1 && fiscalMonth1 !== 'null' ? fiscalMonth1 : new Date().getMonth();

      let fiscalQuarter = Quarter && Quarter !== 'null' ? Quarter : fiscalMonth;
      this.setState({
        fiscal_monthnum: tempMonth === 0 ? 12 : tempMonth,
        fiscal_quarter: fiscalQuarter,
        initialLoadFlag: false,
      });

      this.getCalendarWrapper(selectedView);
    }
    if (!isEqual(oldProps.calendarStrucure, calendarStrucure) && !initialLoadFlag) {
      // this.setState({ fiscal_monthnum: 1, fiscal_quarter: 1 });

      this.getCalendarWrapper(selectedView);
    }

    ///
    if (oldProps.calendarDataPerYear !== calendarDataPerYear) {
      /*------------------weekly navigation enable/disable function------------------ */

      /*-------------------------setting Calendar Data--------------------------*/
      this.setState({
        calendarDataArr: calendarDataPerYear,
        loader: false,
      });
    }

    ///

    // debugger
    if (
      oldState.fiscal_monthnum !== fiscal_monthnum ||
      (fiscal_quarter && oldState.fiscal_quarter !== fiscal_quarter)
    ) {
      // debugger
      if (oldState.selectedYear !== selectedYear) {
        markdownCalendarDatePlot({
          fiscal_year: selectedYear,
        });
      } else {
        this.getCalendarWrapper(selectedView);
      }
    }
  }
  componentWillUnmount() {
    localStorage.setItem(
      'calendarData',
      JSON.stringify({
        startDate: this.state.historyStartDate,
        endDate: this.state.historyEndDate,
        fiscalMonth: this.state.histortFiscalMonth,
      })
    );

    if (this.state.selectedView === 'Quarter') {
      localStorage.setItem(
        'calendarData',
        JSON.stringify({
          quarter: this.state.historyQuarter,
        })
      );
    }
  }
  //170 --- 141 lines
  getCalendarWrapper = (val) => {
    this.calendarData = JSON.parse(localStorage.getItem('calendarData'));
    let { calendarStrucure, markdownCalendarData } = this.props;
    let {
      fiscal_monthnum,
      selectedYear,
      fiscal_quarter,
      filterCategory,
      filterSubCategory,
      filterClass,
      filterSubClass,
      filterBrand,
    } = this.state;

    let calendarWrapperArr = [],
      start_date = null,
      end_date;

    let startDate = this.calendarData && this.calendarData.startDate,
      endDate = this.calendarData && this.calendarData.endDate;

    if (
      calendarStrucure !== undefined ||
      (calendarStrucure !== null && calendarStrucure.plot_data && calendarStrucure.plot_data.length)
    ) {
      var fiscal_week_52_patch = false;
      var curr_date = moment(new Date()).format('YYYY-MM-DD');
      var temp_fiscal_week = 0;
      var temp_fiscal_monthnum = 0;
      calendarStrucure.plot_data.forEach((ele) => {
        //fetching fiscal week and fiscal month
        if (
          ele.start_date <= curr_date &&
          ele.end_date >= curr_date &&
          fiscal_monthnum === ele.fiscal_monthnum &&
          ((fiscal_monthnum < 12 && new Date(ele.start_date).getFullYear() + 1 === selectedYear) ||
            (fiscal_monthnum === 12 && new Date(ele.start_date).getFullYear() === selectedYear))
        ) {
          temp_fiscal_week = Number(ele.fiscal_week);
          temp_fiscal_monthnum = Number(ele.fiscal_monthnum);
        }
      });
      //for month level view
      if (val === 'Month') {
        calendarStrucure.plot_data.forEach((ele) => {
          //checking for fiscal week which is belong to 2 diff. month
          if (
            temp_fiscal_week !== 0 &&
            Number(ele.fiscal_week) === temp_fiscal_week &&
            Number(ele.fiscal_monthnum) !== fiscal_monthnum &&
            fiscal_monthnum - temp_fiscal_monthnum === 1
          ) {
            calendarWrapperArr.push(ele);
          }
          //for checking fiscal week which belonging to fiscal month
          if (
            Number(ele.fiscal_monthnum) === fiscal_monthnum &&
            ((fiscal_monthnum < 12 &&
              new Date(ele.start_date).getFullYear() + 1 === selectedYear) ||
              (fiscal_monthnum < 12 &&
                new Date(ele.start_date).getFullYear() + 2 === selectedYear) ||
              (fiscal_monthnum === 12 && new Date(ele.start_date).getFullYear() === selectedYear))
          ) {
            // debugger

            calendarWrapperArr.push(ele);
          }
        });
      } else {
        //view for quater
        let tempArr = calendarStrucure.plot_data;
        for (let index = 0; index < tempArr.length; index++) {
          //for checking quater 1 starting from last week of year
          if (tempArr[index].fiscal_quarter === 1 && tempArr[index].fiscal_week === 52) {
            fiscal_week_52_patch = true;
          }
          //for cheking quaters
          if (
            Number(tempArr[index].fiscal_quarter) === fiscal_quarter &&
            (new Date(tempArr[index].start_date).getFullYear() + 1 === selectedYear ||
              new Date(tempArr[index].start_date).getFullYear() === selectedYear)
          ) {
            //for margin single day week to previous week which is having same fiscal week
            if (
              index < tempArr.length - 1 &&
              tempArr[index].fiscal_week === tempArr[index + 1].fiscal_week
            ) {
              tempArr[index].end_date = tempArr[index + 1].end_date;
              calendarWrapperArr.push(tempArr[index]);
              index++;
            } else {
              calendarWrapperArr.push(tempArr[index]);
            }
          }
        }
      }
      if (startDate && startDate !== 'null') {
        markdownCalendarData({
          start_date: startDate,
          end_date: endDate,
          sku: '',
          category: filterCategory,
          subcategory: filterSubCategory,
          classname: filterClass,
          subclass: filterSubClass,
          brand: filterBrand,
        });
        localStorage.setItem(
          'calendarData',
          JSON.stringify({
            startDate: null,
            endDate: null,
            fiscalMonth: null,
            // quarter: null
          })
        );
        if (this.state.selectedView === 'Quarter') {
          localStorage.setItem(
            'calendarData',
            JSON.stringify({
              quarter: null,
            })
          );
        }
      } else {
        start_date =
          calendarWrapperArr && calendarWrapperArr[0] ? calendarWrapperArr[0].start_date : null;

        if (fiscal_week_52_patch === true) {
          end_date =
            calendarWrapperArr && calendarWrapperArr.length
              ? calendarWrapperArr[calendarWrapperArr.length - 2].end_date
              : null;
        } else {
          end_date =
            calendarWrapperArr && calendarWrapperArr.length
              ? calendarWrapperArr[calendarWrapperArr.length - 1].end_date
              : null;
        }
        markdownCalendarData({
          start_date: start_date,
          end_date: end_date,
          sku: '',
          category: filterCategory,
          subcategory: filterSubCategory,
          classname: filterClass,
          subclass: filterSubClass,
          brand: filterBrand,
        });
      }
      // }
    }

    this.setState(
      (state) => {
        return {
          ...state,
          calendarWrapperArr,
          historyStartDate: start_date,
          historyEndDate: end_date,
          histortFiscalMonth: state.fiscal_monthnum,
          historyQuarter: state.fiscal_quarter,
        };
      },
      () => {
        //reload page and set the local storage

        window.onbeforeunload = (e) => {
          if (
            e.currentTarget.performance.navigation.type ===
            e.currentTarget.performance.navigation.TYPE_RELOAD
          ) {
            localStorage.setItem(
              'calendarData',
              JSON.stringify({
                startDate: start_date,
                endDate: end_date,
                fiscalMonth: this.state.histortFiscalMonth,
              })
            );
            if (this.state.selectedView === 'Quarter') {
              localStorage.setItem(
                'calendarData',
                JSON.stringify({
                  quarter: this.state.historyQuarter,
                })
              );
            }
          }
        };
      }
    );
  };

  toggleMonthQuarter = (val) => {
    if (this.state.selectedView !== val) {
      this.setState({ selectedView: val, loader: true });

      this.getCalendarWrapper(val);
    }
  };

  navCallBack = (payload) => {
    this.setState(payload);
  };

  onFilterClick = (data) => {
    this.props.markdownCalendarData({
      start_date: this.state.historyStartDate,
      end_date: this.state.historyEndDate,
      sku: '',
      category: data.SelectedCategory ? data.SelectedCategory.value : '',
      subcategory: data.SelectedSubCategory ? data.SelectedSubCategory.value : '',
      classname: data.SelectedClass ? data.SelectedClass.value : '',
      subclass: data.SelectedSubClass ? data.SelectedSubClass.value : '',
      brand: data.SelectedBrand ? data.SelectedBrand.value : '',
    });

    this.setState({
      filterCategory: data.SelectedCategory ? data.SelectedCategory.value : '',
      filterSubCategory: data.SelectedSubCategory ? data.SelectedSubCategory.value : '',
      filterClass: data.SelectedClass ? data.SelectedClass.value : '',
      filterSubClass: data.SelectedSubClass ? data.SelectedSubClass.value : '',
      filterBrand: data.SelectedBrand ? data.SelectedBrand.value : '',
    });
  };

  showMarkdown = (val) => {
    this.setState({
      // loader: true,
      skuList: val.sku_ids,
      markdownId: val.mrk_id,
      // loader: true,
      markdownStartDate: val.start_date,
      markdownEndDate: val.end_date,
      markdownName: val.name,
      selectedMarkDownSku: val.sku_ids[0],
      showGraph: !this.state.showGraph,
    });

    this.props.markdownCalendarGraphData({
      mrk_id: val.mrk_id,
      sku_ids: val.sku_ids[0],
    });

    scroller.scrollTo('showPromotionTag', {
      duration: 800,
      delay: 0,
      smooth: 'easeInOutQuart',
    });
  };

  promoButtonClick = (val) => {
    this.setState({
      markdownId: val.mrk_id,
      markdownStartDate: val.start_date,
      markdownEndDate: val.end_date,
      markdownName: val.name,
      // loader:true
    });
    let markdownid = val.mrk_id;
    this.props.getGlobalPromoPerMarkdown(markdownid);
  };

  call_edit_adgroup = (data) => {
    this.props.history.push(
      `/createpromotion?copyCampaign=true&campaignId=${data.campaignId}&adgroupIds=${data.adgroupId}&promoIds=${data.promoId}`
    );
  };

  onDropdownchange(tag, value) {
    if (tag === 'department') {
      this.setState({
        selectedDepartment: value,
      });
    }
    if (tag === 'category') {
      this.setState({
        selectedCategory: value,
      });
    }

    if (tag === 'subCategory') {
      this.setState({
        selectedSubCategory: value,
      });
    }

    if (tag === 'sku') {
      this.setState({
        selectedSKU: value,
      });
    }
  }

  onDropdownSkuchange = (val) => {
    let skuvalue = val.value;
    let markdownID = this.state.markdownId;

    this.setState({
      selectedMarkDownSku: skuvalue,
    });

    this.props.markdownCalendarGraphData({
      mrk_id: markdownID,
      sku_ids: skuvalue,
    });
  };

  promoDetailsHandler = (active) => {
    this.setState({
      showPromoDetailTable: active,
      generateCadenceError: true,
    });
  };

  onCalEventClick = (event) => {
    console.log(event.title);
  };

  render() {
    let generateCadenceError =
      !!this.state.showPromoDetailTable && this.props.promoDataForMarkdown.length == 0;

    let custGroupArray = [],
      businessDescArray = [],
      shopArray = [],
      skuArray = [],
      brandArray = [];
    if (this.props.markdownFilter && this.props.markdownFilter.data) {
      this.props.markdownFilter.data.category.map((filterData) => {
        custGroupArray.push({
          value: filterData.id,
          label: filterData.category,
        });
      });

      this.props.markdownFilter.data.category.map((filterData) => {
        businessDescArray.push({
          value: filterData.id,
          label: filterData.category,
        });
      });

      this.props.markdownFilter.data.subCategory.map((filterData) => {
        shopArray.push({
          value: filterData.id,
          label: filterData.subcategory,
        });
      });

      this.props.markdownFilter.data.skuGroup.map((filterData) => {
        skuArray.push({
          value: filterData.id,
          label: filterData.sku_group,
        });
      });

      this.props.markdownFilter.data.brand.map((filterData) => {
        brandArray.push({
          value: filterData.id,
          label: filterData.class,
        });
      });
    }

    return (
      <div>
        <PageLoader loader={this.props.loader || this.state.loader}>
          {!this.state.showPromotionMain ? (
            <div className="calendarContainer" id="main-calendar-container">
              <div className="promo">
                <div
                  className="card CalendarMain promoCalendarDiv1"
                  id="promocalendar-maincontainer">
                  <h4 className="promoCal-brdcrm mb-10">
                    Markdown Optimization / <span className="font-bold color-blue">Calendar</span>
                  </h4>
                  <hr className="mb-2" style={{ marginTop: '-3px' }} />
                  <div className="row promocal-subdiv">
                    <div className="col-md-12">
                      <AddSkuFilter
                        subClassID={this.props.subClassID}
                        brandID={this.props.brandID}
                        onFilterClick={this.onFilterClick}
                      />
                    </div>
                  </div>
                  <hr className="dotted-line" />
                  <Calendar fstDaOfWk={0} fstMoOfYr={1} onCalEventClick={this.onCalEventClick} />
                </div>
              </div>

              <Element name="showPromotionTag" className="element" style={{ marginTop: '2%' }}>
                {this.props.calendarGraphData && !this.state.showPromoDetailTable && (
                  <MarkdownPart
                    selectedCadenceSet={this.props.calendarGraphData}
                    markdownName={this.state.markdownName}
                    markdownStartDate={this.state.markdownStartDate}
                    markdownEndDate={this.state.markdownEndDate}
                    markdownId={this.state.markdownId}
                    skuList={this.state.skuList}
                    selectedSku={this.state.selectedMarkDownSku}
                    onChangeSku={this.onDropdownSkuchange}
                    showGraph={this.state.showGraph}
                  />
                )}
              </Element>

              <Element name="showPromotionTag" className="element">
                {this.props.promoDataForMarkdown &&
                  this.props.promoDataForMarkdown.length > 0 &&
                  this.state.showPromoDetailTable && (
                    <GlobalPromosmark
                      markdownName={this.state.markdownName}
                      markdownStartDate={this.state.markdownStartDate}
                      markdownEndDate={this.state.markdownEndDate}
                      markdownId={this.state.markdownId}
                      promoData={this.props.promoDataForMarkdown}
                      loader={this.state.loader}
                    />
                  )}
              </Element>
              <div className="alertDivs">
                {this.state.generateCadenceError &&
                  generateCadenceError &&
                  !this.props.loader &&
                  this.props.promoDataForMarkdown.length === 0 && (
                    <AlertDanger
                      message="No Data Found"
                      show={(() => {
                        setTimeout(() => {
                          this.setState({
                            generateCadenceError: false,
                          });
                        }, 5000);
                        return this.state.generateCadenceError;
                      })()}
                    />
                  )}
              </div>
            </div>
          ) : (
            {}
          )}
        </PageLoader>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    view: state.calendar.view,
    calendarStrucure: state.calendar.markdownCalendarPlotData,
    calendarDataPerYear: state.calendar.markdownCalendarData,
    loader: state.markdown.loader,
    validationLoader: state,
    // summaryCumulativeFilters: state,
    markdownFilter: state.markDownFilter.data,
    calendarGraphData: state.calendar.markdownCalendarGraphData,
    // markdownFilter: state.markDownFilter,
    promoDataForMarkdown: state.globalPromos.globalPromoPerMarkDownData,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    markdownCalendarDatePlot: (val) => dispatch(markdownCalendarDatePlot(val)),
    markdownCalendarData: (val) => dispatch(markdownCalendarData(val)),
    markdownCalendarGraphData: (val) => dispatch(markdownCalendarGraphData(val)),
    getAllFilters: (val) => dispatch(getAllFilters(val)),
    getGlobalPromoPerMarkdown: (payload) => dispatch(getGlobalPromoPerMarkdown(payload)),

    // getFilterData: val => dispatch(getFilterData(val))
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(withRouter(PromoCalendar));
