import React from "react";
import { withRouter } from "react-router-dom";
import Select from "../../../utils/reactDropDownSelect";
import "../markdowncss.css";
import styled from "styled-components";

function columnSelection(props) {
  return (
    <Styles>
      {props.attributes &&
        props.attributes.length &&
        props.attributes.map((att, i) => {
          return (
            <div className="custom-control custom-checkbox">
              <input
                type="checkbox"
                className="custom-control-input"
                id={props.tab === "status" ? i : props.tab + i}
              />
              <label
                className="custom-control-label"
                htmlFor={props.tab === "status" ? i : props.tab + i}
              >
                {att}
              </label>
            </div>
          );
        })}
      <label className="ml-4"> Add Metric</label>

      <div className=" ml-2">
        <Select
          multi={true}
          // clearable={true}
          onHandleSelect={props.onHandleSelect}
          options={props.options}
          contentRenderer={props.contentRenderer}
          selectedItems={props.selectedItems}
        />
      </div>
    </Styles>
  );
}

export default withRouter(columnSelection);

const Styles = styled.div`
  padding: 1rem;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;

  .custom-control {
    line-height: 1.5rem;
    margin: 0 1rem;
  }

  .custom-control-label::before {
    top: 0.5rem;
  }
  .custom-control-label::after {
    top: 0.5rem;
  }
  .custom-checkbox label {
    vertical-align: -webkit-baseline-middle;
  }
`;
