import React, { Component } from 'react';
import '../../../index.css';
import { connect } from 'react-redux';
import Filters from '../../../services/filters';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import './StyleConfigFilter.css';
import StyleConfigActions from '../../../actions/styleConfigActions';
import FilterActions from '../../../actions/filtersAction';
import _ from 'lodash';
import { toast } from 'react-toastify';

export class StyleConfigFilter extends Component {
  state = {
    filterReset: false,
  };
  validateFilters = () => {
    return (
      this.props.selectedHierarchy1 &&
      this.props.selectedHierarchy2.length &&
      this.props.selectedHierarchy3.length &&
      this.props.selectedHierarchy4.length
    );
  };
  onFilter = ({ customStyleGroups }) => {
    // if (!this.validateFilters()) {
    //   toast.error(`Please select mandatory filters`, {
    //     autoClose: 15000,
    //     hideProgressBar: true,
    //   });
    //   return;
    // }
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
      },
    };
    this.props.fetchStyleGroups(req).then((res) => {
      let tableData = [];
      _.map(res, (item) => {
        tableData.push({
          style_group: item.value,
          subRows: _.map(item.style_colors, (color) => ({ id: color, name: color })),
        });
      });
      this.props.setStyleConfigTableData(tableData);
      this.props.onFilterHandler();
    });

    // let style_config_id = customStyleGroups && customStyleGroups[0] && customStyleGroups[0].value;
    // this.props
    //   .getStyleConfigTableData({
    //     // style_type: 'group',
    //     style_config_id,
    //   })
    // .then((res) => {
    //   this.props.onFilterHandler();
    //   this.setState({ setHierarchy8: true });
    // });
  };
  filterResetHandler = (val) => {
    this.setState({
      filterReset: val,
    });
  };

  render() {
    return (
      <>
        <div>
          <Filters
            hideCustomStyleGroup={true}
            hideCustomStores={true}
            // hideStoreClusterFilter
            // hidefilterButton

            filtersOptions={[
              { type: 'country', isMandatory: true, isMultiple: false },
              { type: 'channel', isMandatory: true, isMultiple: false },
              { type: 'storeCluster', isMandatory: false, isMultiple: true },
              { type: 'customStoreGroup', isMandatory: false, isMultiple: true },
              { type: 'store', isMandatory: false, isMultiple: true },
              {
                type: 'hierarchy1',
                isMandatory: true,
                isMultiple: false,
                isHierarchy: true,
              },
              {
                type: 'hierarchy2',
                isMandatory: true,
                isMultiple: false,
                isHierarchy: true,
              },
              {
                type: 'hierarchy3',
                isMandatory: true,
                isMultiple: true,
                isHierarchy: true,
              },
              {
                type: 'hierarchy4',
                isMandatory: true,
                isMultiple: true,
                isHierarchy: true,
              },
              {
                type: 'hierarchy5',
                isMandatory: true,
                isMultiple: true,
                isHierarchy: true,
              },
            ]}
            customStyleGrpMandatory={true}
            hideStoreCluster={true}
            onFilter={this.onFilter}
            from={this.props.from}
            filterReset={this.state.filterReset}
            onReset={this.filterResetHandler}
          />
        </div>
      </>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    selectedHierarchy7: store.filtersReducer.selectedHierarchy7,
    selectedHierarchy8: store.filtersReducer.selectedHierarchy8,
    styleGroups: store.styleReducer.styleGroups,
    selectedCustomStyleGroup: store.filtersReducer.selectedCustomStyleGroup,
    style: store.styleReducer.style,
  };
};
const mapActionsToProps = {
  getStyleConfigTableData: StyleConfigActions.getStyleConfigTableData,
  setStyleConfigTableData: StyleConfigActions.setStyleConfigTableData,
  fetchStyleGroups: FilterActions.fetchStyleGroups,
};
export default connect(mapStateToProps, mapActionsToProps)(StyleConfigFilter);
