import React, { Component } from 'react';
import styled from 'styled-components';
class ComparisonTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: this.props.data,
      selected: '',
      optimalDisable: false,
    };
  }
  componentWillReceiveProps(props) {
    this.setState({
      data: this.props.data,
    });
  }
  componentDidMount() {
    this.setState({
      data: this.props.data,
    });
  }
  getHeader() {
    if (this.state.data && this.state.data.length) {
      let headers = Object.keys(this.state.data[0]);
      headers.unshift('');
      return headers.map((header, index) => {
        return <th key={index}>{header ? header[0].toUpperCase() + header.slice(1) : ''}</th>;
      });
    }
  }
  onCadenceValue = (col, row) => {
    if (row.scenarios == 'Optimal') {
      this.setState({ optimalDisable: true });
    }
  };
  onCheckboxClick = (col, row) => {
    let comparisonRowVal = row;
    console.log(comparisonRowVal, 'abc');
    this.props.compariosnCheckbox(comparisonRowVal);
  };
  getInputWithPercentageTag(col, row, i, index) {
    return (
      <div className="col" key={i + 1} style={{ width: '200px' }}>
        <div className="input-group" style={{ width: '90%' }}>
          <input
            type="text"
            className="form-control border-style"
            value={row[col]}
            disabled={() => this.onCheonCadenceValueckboxClick(col, row)}
          />
          <div className="input-group-append">
            <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
              <i className="fa fa-percent" />
            </button>
          </div>
        </div>
      </div>
    );
  }

  getTags(col, row) {
    console.log(row, 'rowcom');
    if (col.toLowerCase().startsWith('w')) {
      return this.getInputWithPercentageTag(col, row);
    } else if (col.toLowerCase().startsWith('scenarios')) {
      return row[col];
    } else {
      return <input type="checkbox" onChange={() => this.onCheckboxClick(col, row)}></input>;
    }
  }

  render() {
    return (
      <>
        <div className="tableScroll">
          <Styles>
            <tbody>
              <tr>{this.getHeader()}</tr>
              {this.state.data.map((row) => {
                let rows = Object.keys(row);
                rows.unshift('');
                return (
                  <tr>
                    {rows.map((col) => {
                      return <td>{this.getTags(col, row)}</td>;
                    })}
                  </tr>
                );
              })}
            </tbody>
          </Styles>
        </div>
      </>
    );
  }
}

export default ComparisonTable;

const Styles = styled.table`
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;

  td,
  th {
    border: 1px solid #dddddd;
    text-align: center;
    padding: 8px;
  }
`;
