import React, { Component } from "react";
import { withRouter, Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/auth";
import { clearCurrentChartdata } from "../../actions/chart";
import { someAction } from "../../actions/auth";
import "./sideBarStyle.css";

const isLocalNode = function (node, target) {
  // In Internet Explorer (IE), Node.contains() only works on Element nodes,
  // not Text nodes. As such, let's travel up to the nearest Element node.
  // --
  // NOTE: This doesn't necessarily apply to this demo, which only reacts to
  // Element node interactions. However, I'm adding it here as a mental note.
  do {
    if (node === target) {
      return true;
    }

    target = target.parentNode;
  } while (target);

  return false;
};

const findUpTag = function (el, tag) {
  while (el.parentNode) {
    el = el.parentNode;
    if (el === tag) return el;
  }
  return null;
};

const pageLinks = {
  createPromo: [
    "/vis/auto-create-promo",
    "/vis/history-promo",
    "/vis/create-promo",
  ],
  planningPromo: [
    "/vis/budgetplanning",
    "/vis/Promo",
    "/vis/planned-promo-pipeline",
    "/vis/finalize-promo",
    "/vis/promosettings",
  ],
  analysisPromos: [
    "/vis/EDA",
    "/vis/PromoView",
    "/vis/analyze-promo",
    "/vis/analyze-campaign",
  ],
  createPreSeason: [
    "/vis/Markdown/calender",
    "/vis/Markdown/reporting",
    "/vis/Markdown/actual-planned-reports",
    "/vis/Markdown/global-promo",
    "/vis/Markdown/create-markdown",
    "/vis/Markdown/draftStatus",
    "/vis/Markdown/finalize",
  ],
  createPreSeason: [
    "/vis/Markdown/AI-assisted-pre-season",
    "/vis/manual-pre-season",
    "/vis/Markdown/history?preseason=1",
  ],
  createInSeason: [
    "/vis/Markdown/AI-assisted-In-season",
    "/vis/manual-inseason",
    "/vis/Markdown/history?preseason=0",
  ],
  questionsPromo: ["/vis/QuIQ"],
  pricing: [
    "/vis/pricing/pricing-revision",
    "/vis/pricing/new-product-pricing",
    "/vis/pricing/recommended-testing",
    "/vis/pricing/pipeline",
    "/vis/pricing/early-warning-system",
  ],
};

class SideBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      class: "",
      display: "none",
    };
  }

  onHover = () => {
    this.setState({
      class: "active",
      display: "inline-block",
    });
  };

  onLeaveHover = () => {
    this.setState({
      class: "",
      display: "",
    });
  };

  handleClickOutside = (event) => {
    if (!findUpTag(event.target, this.wrapperRef)) {
      this.onLeaveHover();
    }
  };

  componentDidMount() {
    document.addEventListener("mousedown", this.handleClickOutside);
  }

  componentWillUnmount() {
    document.removeEventListener("mousedown", this.handleClickOutside);
  }

  toggleSideBarExpansionHandler = (expand) => {
    if (expand === true || this.state.class !== "active") {
      this.setState({
        class: "active",
        display: "inline-block",
      });
      return;
    }
    if (this.state.class === "active") {
      this.setState({
        class: "",
        display: "",
      });
    }
  };

  onLogoutClick() {
    this.props.history.push("/login");
  }

  handleResize = (event) => {
    setTimeout(() => {
      if (this.wrapperRef && !isLocalNode(this.wrapperRef, event.target)) {
        this.toggleSideBarExpansionHandler();
      }
    }, 300);
  };

  /**
   * Set the wrapper ref
   */
  setWrapperRef = (node) => {
    this.wrapperRef = node;
  };

  redirectToCreatePromo() {
    document.location.href = pageLinks.createPromo[2];
  }
  render() {
    let pathname =
      this.props.location && this.props.location.pathname
        ? this.props.location.pathname
        : document.location.pathname;

    return (
      <nav id="sidebar" className={this.state.class} ref={this.setWrapperRef}>
        <div className="sidebar-toggle-arrow">
          {this.state.class === "active" ? (
            <i
              className="fa fa-angle-double-left sidebar-toggle-arrow-icon sidebar-toggle-arrow-left-icon"
              aria-hidden="true"
              onClick={this.toggleSideBarExpansionHandler}
            />
          ) : (
            <i
              className="fa fa-angle-double-right sidebar-toggle-arrow-icon sidebar-toggle-arrow-right-icon"
              aria-hidden="true"
              onClick={this.toggleSideBarExpansionHandler}
            />
          )}
        </div>
        <div className="sidebar-header" />

        <ul className="list-unstyled components">
          <li>
            <Link
              to="#"
              onClick={() => this.toggleSideBarExpansionHandler(true)}
              className={
                pageLinks.planningPromo.includes(pathname) ||
                pageLinks.createPromo.includes(pathname) ||
                pageLinks.analysisPromos.includes(pathname)
                  ? "active collapsed"
                  : "collapsed"
              }
              data-toggle="collapse"
              data-target="#promoPlanning"
            >
              <img
                className="imgIcons"
                src={require("./promotion-planning.png")}
                alt="Promotion Planning"
                style={{ height: "24px" }}
              />
              <span
                className="hidetext fontstyling"
                style={{ display: this.state.display }}
              >
                Promo Planning
              </span>
              {this.state.display === "inline-block" && (
                <div
                  className="sidebar-accordion-icon icon-stylingArrow ml-2"
                  aria-hidden="true"
                />
              )}
            </Link>
            {this.state.display === "inline-block" && (
              <ul
                className={`sub-ul  
                ${
                  pageLinks.createPromo.includes(pathname) ||
                  pageLinks.planningPromo.includes(pathname) ||
                  pageLinks.analysisPromos.includes(pathname)
                    ? "show"
                    : "collapse"
                }
              `}
                id="promoPlanning"
              >
                <li>
                  <Link
                    to="#"
                    onClick={() => this.toggleSideBarExpansionHandler(true)}
                    className={
                      pageLinks.planningPromo.includes(pathname)
                        ? "active"
                        : "collapsed"
                    }
                    data-toggle="collapse"
                    data-target="#promotionPlanningOptions"
                  >
                    <img
                      className="imgIcons"
                      src={require("./Group78.svg")}
                      alt="Promotion Planning"
                    />
                    <span
                      className="hidetext fontstyling"
                      style={{ display: this.state.display }}
                    >
                      Promotion Planning
                    </span>

                    <div
                      className="sidebar-accordion-icon icon-stylingArrow ml-2"
                      aria-hidden="true"
                    />
                  </Link>
                  {this.state.display === "inline-block" && (
                    <ul
                      className={`sub-ul  
                ${
                  pageLinks.planningPromo.includes(pathname)
                    ? "show"
                    : "collapse"
                }
              `}
                      id="promotionPlanningOptions"
                    >
                      <li
                        onClick={() => this.toggleSideBarExpansionHandler()}
                        className={
                          pathname === pageLinks.planningPromo[0]
                            ? "active"
                            : ""
                        }
                      >
                        <Link to={pageLinks.planningPromo[0]}>
                          <i className="fa fa-circle icon-stylingDot" />
                          <span
                            className="hidetext fontstyling"
                            style={{ display: this.state.display }}
                          >
                            Budget Allocation
                          </span>
                        </Link>
                      </li>
                      <li
                        onClick={() => this.toggleSideBarExpansionHandler()}
                        className={
                          pathname === pageLinks.planningPromo[1]
                            ? "active"
                            : ""
                        }
                      >
                        <Link to={pageLinks.planningPromo[1]}>
                          <i className="fa fa-circle icon-stylingDot" />
                          <span
                            className="hidetext fontstyling"
                            style={{ display: this.state.display }}
                          >
                            Promotion Calendar
                          </span>
                        </Link>
                      </li>

                      <li
                        onClick={() => this.toggleSideBarExpansionHandler()}
                        className={
                          pathname === pageLinks.planningPromo[3]
                            ? "active"
                            : ""
                        }
                      >
                        <Link to={pageLinks.planningPromo[3]}>
                          <i className="fa fa-circle icon-stylingDot" />
                          <span
                            className="hidetext fontstyling"
                            style={{ display: this.state.display }}
                          >
                            Finalize Promotion
                          </span>
                        </Link>
                      </li>
                      <li
