import React, { Component } from 'react';
import Filters from '../../../services/filters';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { cloneDeep, differenceBy } from 'lodash';
import _ from 'lodash';

import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import DataTable from '../../../services/reactTable';
import StyleConfigFilter from '../StyleConfigFilter/StyleConfigFilter';
import excelIcon from '../../../images/leftPanel/status.svg';

const tableData = [
  { style_id: '690-1-2-1-REF CIRCUIT', style_desc: 'REFLECTIVE CIRCUIT TRAIN CAP' },
  { style_id: '230-1-2-1-SOLDIER', style_desc: 'SOLDIER STRETCHFIT' },
  { style_id: '690-1-2-1-STREAM', style_desc: 'STREAMLINE SNAPBACK' },
  { style_id: '640-1-2-1-CIRCUIT', style_desc: 'REFLECTIVE CIRCUIT CAP' },
  { style_id: '567-1-2-1-SOLD', style_desc: 'SOLDIER STRETCHFIT AAA' },
  { style_id: '123-1-2-1-TEAM', style_desc: 'STREAMLINE SNAPBACK BBB' },
];

export class CreateStyleGroup extends Component {
  state = {
    groupName: '',
    allStyles: [],
    currentStyles: [],
    selectedAllStyles: [],
    selectedCurrentStyles: [],
    showEDLP: true,
    showGeo: false,
    header: [
      {
        Header: 'Styles ID',
        accessor: 'style_id',
      },
      {
        Header: 'Styles Description',
        accessor: 'style_desc',
      },
    ],
  };

  async componentDidMount() {
    // this.props.fetchStoreGroups();
    // let response = await this.props.getStoreGroupTableData();
    // let { match } = this.props;
    // if (match && match.params && match.params.id) {
    //   let { id } = match.params;
    //   let res = await this.props.getStoreGroupTableData({ Stores_groups_Id: [id] });
    //   this.setState({ fromEdit: true, storeGroupId: id, currentStores: res });
    // }
    this.setState({ allStyles: tableData });
  }

  componentDidUpdate(prevProps, prevState) {
    // if (this.props.styleGroupEdit && !this.state.storeGroupName && this.props.storeGroups) {
    //   let groupName = _.find(this.props.storeGroups, { value: parseInt(this.state.storeGroupId) });
    //   console.log(this.props.storeGroups, this.state.storeGroupId, 'Verlotppppppp', groupName);
    //   if (groupName) {
    //     this.setState({ storeGroupName: groupName.label });
    //   }
    // }
  }
  onGroupNameChange = (e) => {
    this.setState({
      groupName: e.target.value,
    });
  };
  onFilter = () => {
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
      },
    };
    console.log(req, 'request');
  };
  onAddClickHandler = () => {
    const allStyles = cloneDeep(this.state.allStyles);
    const currentStyles = cloneDeep(this.state.currentStyles);
    let newcurrentStyles = _.unionWith(currentStyles, this.state.selectedAllStyles, _.isEqual);

    let mainArray = allStyles,
      childArray = this.state.selectedAllStyles;

    if (allStyles.length < this.state.selectedAllStyles.length) {
      mainArray = this.state.selectedAllStyles;
      childArray = allStyles;
    }
    let newAllStyles = differenceBy(mainArray, childArray, 'style_id');
    console.log('asdflkasmdfa', newAllStyles, this.state.allStyles, this.state.selectedAllStyles);
    this.setState({
      allStyles: newAllStyles,
      currentStyles: newcurrentStyles,
      selectedAllStyles: [],
    });
  };

  onRemoveClickHandler = () => {
    const currentStyles = cloneDeep(this.state.currentStyles);
    const allStyles = cloneDeep(this.state.allStyles);
    let mainArray = currentStyles,
      childArray = this.state.selectedCurrentStyles;

    if (currentStyles.length < this.state.selectedCurrentStyles.length) {
      mainArray = this.state.selectedCurrentStyles;
      childArray = currentStyles;
    }
    const newCurrentStyles = differenceBy(mainArray, childArray, 'style_id');
    // allStyles.push(...this.state.selectedCurrentStyles);
    let newallStyles = _.unionWith(allStyles, this.state.selectedCurrentStyles, _.isEqual);

    this.setState({
      allStyles: newallStyles,
      currentStyles: newCurrentStyles,
      selectedCurrentStyles: [],
    });
  };

  onAllStyleSelectionChangeHandler = (rowData) => {
    let { selectedAllStyles } = this.state;
    let foundEle = _.find(selectedAllStyles, { style_id: rowData.original.style_id });
    if (foundEle) {
      _.remove(selectedAllStyles, (item) => item.style_id === rowData.original.style_id);
    } else {
      selectedAllStyles.push(rowData.original);
    }
    console.log(foundEle, 'asdfmasdf', selectedAllStyles, selectedAllStyles.length);

    this.setState({
      selectedAllStyles,
    });
  };

  onCurrentStylesSelectionChangeHandler = (rowData) => {
    let { selectedCurrentStyles } = this.state;
    let foundEle = _.find(selectedCurrentStyles, {
      style_id: rowData.original.style_id,
    });
    if (foundEle) {
      _.remove(selectedCurrentStyles, (item) => item.style_id === rowData.original.style_id);
    } else {
      selectedCurrentStyles.push(rowData.original);
    }
    console.log(foundEle, 'asdfmasdf', selectedCurrentStyles, selectedCurrentStyles.length);

    this.setState({
      selectedCurrentStyles,
    });
  };

  onCreateStyleGroup = () => {
    if (this.props.styleGroupEdit) {
      // let { storeGroupId, storeGroupName, currentStores } = this.state;
      // let storeCodes = [];
      // currentStores.map((ele) => storeCodes.push(ele.store_code));
      // this.props.editStoreGroup({
      //   Stores_groups_NAME: storeGroupName,
      //   Stores_Codes: storeCodes,
      //   Stores_groups_Id: storeGroupId,
      // });
    } else {
      // let storeCodes = this.state.currentStores.map((ele) => ele.store_code);
      // let { storeGroupName } = this.state;
      // this.props.createStoreGroup({
      //   Stores_groups_NAME: storeGroupName,
      //   Stores_Codes: storeCodes,
      // });
    }
  };

  onCancelButtonHandler = () => {
    // this.setState({ allStores: [], editAllstoreData: [] });
    if (this.props.styleGroupEdit) {
      this.props.styleGroupEditHandler();
      return;
    }
    this.props.history.goBack();
  };

  render() {
    let { groupName, currentStyles, allStyles, header } = this.state;

    return (
      <div className="card p-2">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h4 className="font-size-14  pad-10 markdown-heading">
            Store Configuration/
            <span className="font-bold color-blue">
              {this.props.styleGroupEdit ? 'Edit Style' : 'Create Style'} Group{' '}
            </span>
          </h4>

          <div>
            <button className="btn btn-primary" onClick={this.onCancelButtonHandler}>
              Back
            </button>
          </div>
        </div>
        <hr
          className="mb-2"
          style={{
            marginTop: '-4px',
          }}
        />

        <div className="row justify-content-between">
          <div className="col-md-6">
            <label className="mr-1">
