import React from 'react'
import { Modal } from 'react-bootstrap'
import PromoAnalysis from '../analyzePromo/promoAnalysis'
import './CustomModal.css'
import { OFFER_TYPE_OPTIONS } from '../../container/createNewPromo/constants'
export default class CustomModal extends React.Component {
  render() {
    console.log('CustomModal props', this.props)
    let type =
      this.props.currentPromo && this.props.currentPromo.promoType
        ? OFFER_TYPE_OPTIONS.filter(
            type => type.value === this.props.currentPromo.promoType
          )[0].label
        : 'Null'
    let value =
      this.props.currentPromo && this.props.currentPromo.promoValue
        ? this.props.currentPromo.promoValue.toFixed(2)
        : ''
    const prefix = '$'
    const postfix = '%'
    return (
      <div>
        <Modal size="lg" show={this.props.showModal}>
          {/* <Modal.Header>
                <Modal.Title>Modal heading</Modal.Title>
            </Modal.Header> */}
          <Modal.Body>
            <span
              onClick={this.props.closeModal}
              className="pull-right"
              style={{ cursor: 'pointer' }}
            >
              <i class="fa fa-times" aria-hidden="true" />
            </span>
            <div className="modal-htitle" style={{ display: 'flex' }}>
              <div>
                Promotion type: &nbsp;
                <span style={{ fontWeight: 'bolder' }}>{type}</span>
              </div>
              <div style={{ marginLeft: '20px' }}>
                Promotion value: &nbsp;
                <span className="font-bold font-size-13">
                  {(this.props.currentPromo.promoType === 'A' ||
                    this.props.currentPromo.promoType === 'F') &&
                    prefix}
                  {Math.ceil(value)}
                  {(this.props.currentPromo.promoType === 'BxGy' ||
                    this.props.currentPromo.promoType === 'P') &&
                    postfix}
                </span>
              </div>
            </div>
            <PromoAnalysis {...this.props} analysisType={false} />
          </Modal.Body>
          {/* <Modal.Footer>
            <Link
              to={{
                pathname: '/vis/analyze-promo',
                state: { id: this.props.currentPromo.id }
              }}
            >
              <button className="btn btn-primary">Analyse Promotion</button>
            </Link>
          </Modal.Footer> */}
        </Modal>
      </div>
    )
  }
}
