import React, { Component } from 'react';
import Filters from '../../services/filters';
import MarkdownAnalysis from '../MarkdownAnalysis';
import InventoryInfo from './InventoryInfo';
import Constraints from './Constraints';
import CreateCadence from './CreateCadence';
import Modal from 'react-awesome-modal';
import { withRouter } from 'react-router-dom';
import Select from 'react-select';
import { connect } from 'react-redux';
import Download from '../../services/download';
import LoadingOverlay from '../../utils/Loader';
import _ from 'lodash';
import GetMarkdownPercentageHeader from './GetMarkdownPercentageHeader';
import manuallyCreateMdActions from '../../actions/manuallyCreateMdActions.js';
import './index.css';
import moment from 'moment';
import { Button, Tabs, Tab } from 'react-bootstrap';
import MarkdownDetailedTable from './MardownDetailedTable';
import cloneDeep from 'clone-deep';
import ComparisonTable from './ComparisonTable';
import ComparisonMetricTable from './ComparisonMetricTable';
import classNames from 'classnames';
import ExcludeStoreTable from './excludeStoreTable';
// import config from '../../utils/config';
import PlanSavePopup from './planSavePopup';
import { toast } from 'react-toastify';
// import FormikFilter from './FormikFilter';

class ManuallyCreateMD extends Component {
  constructor(props) {
    super(props);
    this.state = {
      stores: [],
      selectedStyle: 'all',
      saveCadenceRequest: {},
      selectedSkuId: null,
      targetSellThroughVal: null,
      markdownTypeVal: false,
      cadenceConstraintsVal: 'overall',
      marginVal: null,
      overallCadenceData: [],
      skuLabelCadenceData: [],
      offset: 10,
      fileName: 'Comparison Table',
      minTS: 0,
      maxTS: 100,
      minMargin: -100,
      maxMargin: 100,
      totalInDc: 0,
      totalOnHand: 0,
      totalOnOrder: 0,
      totalInTransit: 0,
      cadenceData: ['40', '45', '50', '55', '60', '65', '70', '75', '80'],
      currentPage: 1,
      filters: [
        {
          type: 'hierarchy1',
          isMandatory: true,
          isMultiple: false,
        },
        {
          type: 'hierarchy2',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy3',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy4',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy5',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy6',
          isMandatory: false,
          isMultiple: true,
        },
        {
          type: 'hierarchy7',
          isMandatory: false,
          isMultiple: true,
        },
        {
          type: 'hierarchy8',
          isMandatory: false,
          isMultiple: true,
        },
      ],
      // filteredData: {},
      inventoryDc: 0,
      onHandInventory: 0,
      onOrderInventory: 0,
      inTransitInventory: 0,
      totalInventory: 0,
      currentInventory: 0,
      totalStores: 0,
      selectedStores: 0,
      disableSimBtn: true,
      selectedView: 'Overview',
      selectedSku: [],
      showExcludeStorePopup: false,
      showPlanSavePopup: false,
      TabTitles: ['Optimal'],
      defaultActiveKey: 'Optimal',
      comparisonData: [],
      showCadence: false,
      simulatedData: false,
      comparisonRow: [],
      status: 'pending',
      md_level: 'week',
      isDraftDisabled: false,
    };
  }
  selectedSkuHandler = (val) => {
    this.setState({
      selectedStyle: val ? val.value : 'all',
      selectedSkuId: val,
    });
    val && val.value ? this.callAPISimulate(val.value) : this.callAPISimulate();
  };

  comparisonCheckbox = (comparisonRowVal) => {
    this.setState({ comparisonRow: comparisonRowVal });
    // this.setState({ disableSimBtn: false });
  };
  onComparisonInputChange = (data) => {
    let cadence = cloneDeep(this.state.overallCadenceData);
    cadence[this.state.TabTitles.indexOf('Comparison')] = data;
    this.setState({
      overallCadenceData: cadence,
    });
    this.setState({ disableSimBtn: false });
  };
  onUpdateMarkdownPercentage = (data, index) => {
    if (this.state.cadenceConstraintsVal === 'overall') {
      let data1 = cloneDeep(this.state.overallCadenceData);
      data1[index] = data;
      this.setState({
        overallCadenceData: data1,
        disableSimBtn: false,
      });
    } else {
      let data1 = cloneDeep(this.state.skuLabelCadenceData);
      data1[index] = data;
      this.setState({
        skuLabelCadenceData: data1,
      });
    }
    // this.setState({ disableSimBtn: false });
  };
  getComparsionHeader = (header) => {
    if (this.state.cadenceConstraintsVal === 'sku' && header === 'Comparison') {
      let optimalData = this.state.skuLabelCadenceData[0];
      let options = optimalData.map((data) => {
        return { label: data.SKU_Id, value: data.SKU_Id };
      });
      return (
        <div className="row skuLabelStyle">
          <label className="skuLabel">SKU/SKU Group</label>
          <div className="col-md-2">
            <Select name="category" options={options} />
          </div>
        </div>
      );
    }
    return (
      <div>
        <div className="row justify-content-center skuLabelStyle">
          <label className="font-size-14 markdown-percentage-heading ">Markdown Percentages</label>
        </div>
        <div className="abc">
          <button className="btn btn-primary mr-3 mb-3 mt-4" onClick={this.addScenario}>
            Add Scenario
          </button>
        </div>
      </div>
    );
  };
  getCadenceData = (index) => {
    if (this.state.cadenceConstraintsVal === 'overall') return this.state.overallCadenceData[index];
    return this.state.skuLabelCadenceData[index];
  };
  // getTabTag = (tabTitle, index) => {
  //   console.log('usedgettag');
  //   if (tabTitle === 'Comparison') {
  //     if (this.state.cadenceConstraintsVal === 'sku') {
  //       return (
  //         <div>
  //           <ComparisonMetricTable
  //             showCheckBoxComparsionTable={true}
  //             data={this.props.comparisonData}
  //           />
  //         </div>
  //       );
  //     } else {
  //       return (
  //         <div>
  //           <Download
  //             data={this.state.overallCadenceData[this.state.overallCadenceData.length - 1]}
  //           />
  //           <ComparisonTable
  //             comparisonCheckbox={(comparisonRowVal, index) =>
  //               this.comparisonCheckbox(comparisonRowVal, index)
  //             }
  //             data={this.state.overallCadenceData[index]}
  //             updatedData={this.onComparisonInputChange}
  //           />
  //         </div>
  //       );
  //     }
  //   } else {
  //     return (
  //       <div className="cadencetable">
  //         <CreateCadence
  //           overallCadenceData={this.getCadenceData(index)}
  //           onUpdateMarkdownPercentage={this.onUpdateMarkdownPercentage}
  //           addScenario={this.addScenario}
  //           selectedSku={this.state.selectedSku}
  //           key={tabTitle}
  //           index={index}
  //           activeTab={this.state.defaultActiveKey}
  //         />
  //       </div>
  //     );
  //   }
  // };
  checkTabIsCompasionAndSkuType = (tabTitle) => {
    return this.state.cadenceConstraintsVal === 'sku' && tabTitle === 'Comparison';
  };
  isSKUType = (header) => {
    return this.state.cadenceConstraintsVal === 'sku';
  };
  isNotComparsionTab = (tabTitle) => {
    return tabTitle !== 'Comparison';
  };
  getTabTable = (activeTab, index) => {
    if (this.isNotComparsionTab()) {
      return (
        <>
          <div className="anu">
            <button className="btn btn-primary mr-3 mb-3 mt-4" onClick={this.addScenario}>
              Add Scenario
            </button>
          </div>
          <GetMarkdownPercentageHeader />
          <div className="cadencetable">
            <CreateCadence
              overallCadenceData={this.getCadenceData(index)}
              onUpdateMarkdownPercentage={this.onUpdateMarkdownPercentage}
              addScenario={this.addScenario}
              selectedSku={this.state.selectedSku}
              key={activeTab}
              index={index}
              activeTab={this.state.defaultActiveKey}
            />
          </div>
        </>
      );
    } else {
      if (this.isSKUType(activeTab)) {
        let optimalData = this.state.skuLabelCadenceData[0];
        let options = optimalData.map((data) => {
          return { label: data.SKU_Id, value: data.SKU_Id };
        });
        return (
          <>
            <div className="row skuLabelStyle">
              <label className="skuLabel">SKU/SKU Group</label>
              <div className="col-md-2">
                <Select name="category" options={options} />
              </div>
            </div>
            <ComparisonMetricTable
              showCheckBoxComparsionTable={true}
              data={this.props.comparisonData}
              fileName={this.state.fileName}
            />
          </>
        );
      } else {
        return (
          <div>
            <ComparisonTable
              comparisonCheckbox={(comparisonRowVal, index) =>
                this.comparisonCheckbox(comparisonRowVal, index)
              }
              data={this.state.overallCadenceData[index]}
              updatedData={this.onComparisonInputChange}
            />
          </div>
        );
      }
    }
  };
  // getTab() {
  //   return this.state.TabTitles.map((tabTitle, index) => {
  //     return (
  //       <Tab eventKey={tabTitle} title={tabTitle} key={tabTitle} style={{ textAlign: 'center' }}>
  //         {this.getTabTable(tabTitle, index)}
  //       </Tab>
  //     );
  //   });
  // }
  getComparsionHeader = (header) => {
    if (this.state.cadenceConstraintsVal === 'sku' && header === 'Comparison') {
      let optimalData = this.state.skuLabelCadenceData[0];
      let options = optimalData.map((data) => {
        return { label: data.SKU_Id, value: data.SKU_Id };
      });
      return (
        <div className="row skuLabelStyle">
          <label className="skuLabel">SKU/SKU Group</label>
          <div className="col-md-2">
            <Select name="category" options={options} />
          </div>
        </div>
      );
    }
    return (
      <div>
        <div className="row justify-content-center skuLabelStyle">
          <label className="font-size-14 markdown-percentage-heading ">Markdown Percentages</label>
        </div>
        <div className="addScenario">
          <button className="btn btn-primary mr-3 mb-3 mt-4" onClick={this.addScenario}>
            Add Scenario
          </button>
          {this.state.defaultActiveKey === 'Comparison' ? (
            <Download
              data={this.state.overallCadenceData[this.state.overallCadenceData.length - 1]}
            />
          ) : null}
        </div>
      </div>
    );
  };
  getTabTag = (tabTitle, index) => {
    if (tabTitle === 'Comparison') {
      if (this.state.cadenceConstraintsVal === 'sku') {
        return (
          <div>
            <ComparisonMetricTable
              showCheckBoxComparsionTable={true}
              data={this.props.comparisonData}
              fileName={this.state.fileName}
            />
          </div>
        );
      } else {
        return (
          <div>
            <ComparisonTable
              comparisonCheckbox={(comparisonRowVal, index) =>
                this.comparisonCheckbox(comparisonRowVal, index)
              }
              data={this.state.overallCadenceData[index]}
              updatedData={this.onComparisonInputChange}
            />
          </div>
        );
      }
    } else {
      return (
        <div className="cadencetable">
          <CreateCadence
            overallCadenceData={
              this.state.cadenceConstraintsVal === 'overall'
                ? this.state.overallCadenceData[index]
                : this.state.skuLabelCadenceData[index]
            }
            onUpdateMarkdownPercentage={this.onUpdateMarkdownPercentage}
            addScenario={this.addScenario}
            selectedSku={this.state.selectedSku}
            key={tabTitle}
            index={index}
            activeTab={this.state.defaultActiveKey}
          />
        </div>
      );
    }
  };
  getTab() {
    return this.state.TabTitles.map((tabTitle, index) => {
      return (
        <Tab eventKey={tabTitle} title={tabTitle} key={tabTitle} style={{ textAlign: 'center' }}>
          {this.getComparsionHeader(tabTitle)}
          <div>{this.getTabTag(tabTitle, index)}</div>
        </Tab>
      );
    });
  }
  onSelectTabs = (key) => {
    this.setState(
      {
        defaultActiveKey: key,
        disableSimBtn: true,
        isDraftDisabled: true,
        selectedStyle: 'all',
        selectedSkuId: null,
      },
      () => {
        if (this.state.defaultActiveKey !== 'Comparison') {
          this.props.updateAnalysisData(
            this.props.analysisData[this.state.defaultActiveKey],
            this.state.defaultActiveKey
          );
          // this.callAPISimulate();
        }
      }
    );
  };
  addTitleInTabs = () => {
    //checl comparison is there or not if yes -1
    let TabTitles = this.state.TabTitles;
    let comparisonTitleIndex = TabTitles.indexOf('Comparison');
    let newSceniosIndex = TabTitles.length;
    let defaultSceniosTitle = `Scenario 0${TabTitles.length}`;
    if (comparisonTitleIndex !== -1) {
      //comparsion present
      newSceniosIndex = comparisonTitleIndex;
      defaultSceniosTitle = `Scenario 0${newSceniosIndex}`;
      TabTitles.splice(newSceniosIndex, 0, defaultSceniosTitle);
    } else {
      TabTitles.splice(newSceniosIndex, 0, defaultSceniosTitle);
    }
    this.setState({
      TabTitles,
      defaultActiveKey: defaultSceniosTitle,
    });
    this.addContentsInTabs(newSceniosIndex, defaultSceniosTitle);
  };
  addContentsInTabs = (newSceniosIndex, defaultSceniosTitle) => {
    let data =
      this.state.cadenceConstraintsVal === 'overall'
        ? this.state.overallCadenceData
        : this.state.skuLabelCadenceData;
    // );
    data.splice(newSceniosIndex, 0, cloneDeep(data[0]));
    let obj = {
      skuLabelCadenceData: data,
    };
    if (this.state.cadenceConstraintsVal === 'overall') {
      obj = {
        overallCadenceData: data,
      };
    }
    console.log('data:', data, this.props.analysisData['Optimal']);
    this.props.updateAnalysisData(
      this.props.analysisData['Optimal'].simulateData,
      defaultSceniosTitle
    );
    this.setState(obj);
  };
  addScenario = () => {
    if (this.isTotalSceniosCreatedFive()) {
      this.setState({
        selectedSkuId: null,
        selectedStyle: 'all',
      });
      this.addTitleInTabs();
      this.setState({ disableSimBtn: true, isDraftDisabled: true });
      return;
    }
    toast.error(`Max 5 Scenarios can be created`, {
      autoClose: 15000,
      hideProgressBar: true,
    });
  };
  isTotalSceniosCreatedFive = () => {
    let totalScenariosCreated = this.state.TabTitles.filter((title) =>
      title.startsWith('Scenario')
    );
    return totalScenariosCreated.length < 5;
  };

  resetCadence = () => {
    this.setState({
      TabTitles: ['Optimal'],
      overallCadenceData: [],
      simulatedData: false,
      showCadence: false,
      defaultActiveKey: 'Optimal',
    });
    this.resetComp();
  };

  checkDateRange = () => {
    if (
      moment(new Date()).week() + 1 <= this.state.startDate.week() &&
      moment(new Date()).add(13, 'weeks') >= this.state.endDate.week()
    ) {
      return true;
    }
    toast.error(`Start week should be greater than current week and end date less than 13 weeks`, {
      autoClose: 15000,
      hideProgressBar: true,
    });
    return false;
  };

  generateCadence = () => {
    if (!this.validateSellThrough() || !this.validateMargin() || !this.checkDateRange()) {
      return;
    }
    this.resetCadence();
    this.setState({
      showCadence: false,
      simulatedData: true,
    });
    let startWeek = this.state.startDate.week();
    let endWeek = this.state.endDate.week();
    let weeks = this.getWeekRange(startWeek, endWeek);
    let request = cloneDeep(this.state.filteredData);
    let inputData = {
      cadenceData: this.state.cadenceData,
      inventorySelected: '',
      storeSelected: '',
      excludeStore: this.state.excludedStores,
      sellThrough: this.state.targetSellThroughVal || '',
      margin: this.state.marginVal || '',
      markdownType: this.state.markdownTypeVal || '',
      cadenceType: this.state.cadenceConstraintsVal || 'overall',
      startDate: this.state.startDate.format('YYYY-MM-DD'),
      endDate: this.state.endDate.format('YYYY-MM-DD'),
      week: weeks,
      totalInDc: this.state.totalInDc,
      totalOnHand: this.state.totalOnHand,
      totalOnOrder: this.state.totalOnOrder,
      totalInTransit: this.state.totalInTransit,
      stores: this.state.stores,
      md_level: this.state.md_level,
      md_level_no_range: weeks,
      startWeek,
      endWeek,
    };

    let saveCadenceRequest = { ...request, ...inputData };
    this.setState({
      saveCadenceRequest,
    });
    this.props.fetchCadence(saveCadenceRequest);
  };
  checkComparsionDataPresent = (data) => {
    return data.indexOf('Comparison') !== -1 ? true : false;
  };
  updateComparsionDataInTab = (tabTitles) => {
    let comparsionData = [];

    // this.state.TabTitles.indexOf('Comparsion')
    for (let i = 0; i < tabTitles.length - 1; i++) {
      if (this.state.cadenceConstraintsVal === 'overall') {
        comparsionData.push({
          scenarios: tabTitles[i],
          ...cloneDeep(this.state.overallCadenceData[i][0]),
        });
      } else {
        let rows = cloneDeep(this.state.skuLabelCadenceData[i][0]);
        delete rows['SKU ID'];
        delete rows['MD Status'];
        comparsionData.push({
          scenarios: tabTitles[i],
          ...rows,
        });
      }
    }
    let data = [];
    let minusIndex = 0;
    if (this.checkComparsionDataPresent(this.state.TabTitles)) {
      minusIndex = 1;
    }
    if (this.state.cadenceConstraintsVal === 'overall') {
      data = cloneDeep(this.state.overallCadenceData);
      data.splice(data.length - minusIndex, 1, comparsionData);
      this.setState({
        overallCadenceData: data,
        simulatedData: true,
      });
    } else {
      data = cloneDeep(this.state.skuLabelCadenceData);
      data.splice(data.length - minusIndex, 1, comparsionData);
      this.setState({
        skuLabelCadenceData: data,
        simulatedData: true,
      });
    }
  };
  resetComp = () => {
    this.setState({
      comparisonData: {},
    });
  };
  onSimulate = () => {
    //adding comparsion title
    let tabTitles = cloneDeep(this.state.TabTitles);
    this.setState({
      isDraftDisabled: false,
      selectedSkuId: null,
    });
    if (this.state.TabTitles.indexOf('Comparison') === -1) {
      let array = [...this.state.TabTitles, 'Comparison'];
      tabTitles = array;
      this.setState({
        TabTitles: array,
        selectedStyle: 'all',
        selectedSkuId: null,
        // isDraftDisabled: false,
      });
    }
    this.updateComparsionDataInTab(tabTitles);
    this.callAPISimulate();
    this.setState({ disableSimBtn: true });
    this.resetComp();
  };
  callAPISimulate = (skus = 'all') => {
    let index = this.state.TabTitles.indexOf(this.state.defaultActiveKey);
    let array = [];

    if (this.state.defaultActiveKey === 'Comparison') {
      let cadence =
        this.state.cadenceConstraintsVal === 'overall'
          ? cloneDeep(this.state.overallCadenceData)
          : cloneDeep(this.state.skuLabelCadenceData);
      let lastComparisonArray = cadence[cadence.length - 1];
      for (let i = 1; i < lastComparisonArray.length; i++) {
        let scenios = cloneDeep(lastComparisonArray[i]);
        delete scenios.scenarios;
        cadence[i] = [scenios];
      }
      this.setState({
        overallCadenceData: cadence,
      });
      let data = cadence[this.state.TabTitles.indexOf('Comparison')];
      for (let i = 1; i < data.length; i++) {
        let row = cloneDeep(data[i]);
        delete row.scenarios;
        this.props.simulate(
          {
            csv_names: this.props.csv_names,
            skus: 'all',
            cadenceType: this.state.cadenceConstraintsVal,
            scenario: 'true',
            scenario_data: [row],
          },
          this.state.defaultActiveKey
        );
      }
    } else {
      array = this.getCadenceData(index);
      this.props.simulate(
        {
          csv_names: this.props.csv_names,
          skus,
          cadenceType: this.state.cadenceConstraintsVal,
          scenario: 'true',
          scenario_data: array,
        },
        this.state.defaultActiveKey
      );
    }
  };
