import React from 'react';
import {
  toPercentage,
  toPercentageWithDecimalDraft,
  toUnit,
  toDollar,
} from '../../helper/formatter';
import _ from 'lodash';
import moment from 'moment';
import numeral from 'numeral';
//cellClassName must be same for aggColDef, binToWeekColDef and weekToBinColDef
/********************************************************************* */
//*******WHILE ADDING NEW COLUMNS ADD A UNIQUE CELLCLASSNAME************/
/********ADD THE COLUMN TO ALL OF THREE COLDEFS*************************/
/********************************************************************* */
//aggColDef is aggregate level parent table coldef,
//in this level user can expand both dateRange and Bins cols(when one is expanded the other will disable)
export const aggColDef = [
  {
    Header: 'MD Plan',
    accessor: 'md_plan',
    cellClassName: 'summary_md_plan',
  },
  {
    Header: 'Scenario',
    accessor: 'scenario',
    cellClassName: 'summary_scenario',
  },
  {
    Header: 'Plan Level',
    accessor: 'cadence_type',
    cellClassName: 'summary_cadence_type',
  },
  {
    id: 'expander',
    cellClassName: 'summary_expander',
    Header: 'Date Range',
    Cell: ({ row }) => {
      return row.original.md_plan ? (
        <span
          className="summary-table-icon"
          {...row.getToggleRowExpandedProps({
            style: {
              paddingLeft: `${row.depth * 2}rem`,
            },
          })}>
          {moment(row.original.start_date).format('YYYY/MM/DD')} -{' '}
          {moment(row.original.end_date).format('YYYY/MM/DD')}
          &nbsp;&nbsp;&nbsp;
          {row.isExpanded ? (
            <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
          ) : (
            <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
          )}
          &nbsp;&nbsp;&nbsp;
        </span>
      ) : null;
    },
  },

  {
    id: 'expander1',
    cellClassName: 'summary_expander1',
    Header: 'Bins',
    Cell: ({ row }) => {
      return row.original.md_plan ? (
        <span
          className="summary-table-icon"
          {...row.getToggleRowExpandedProps({
            style: {
              paddingLeft: `${row.depth * 2}rem`,
            },
          })}>
          {row.original.bin_level_data
            ? row.original.bin_level_data.length
            : row.original.week_level_data &&
              row.original.week_level_data[0] &&
              row.original.week_level_data[0].bin_level_data
            ? row.original.week_level_data[0].bin_level_data.length
            : row.original.week_level_data &&
              row.original.week_level_data[0] &&
              row.original.week_level_data[0].subRows
            ? row.original.week_level_data[0].subRows.length
            : '-'}
          &nbsp;&nbsp;&nbsp;
          {row.isExpanded ? (
            <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
          ) : (
            <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
          )}
          &nbsp;&nbsp;&nbsp;
        </span>
      ) : null;
    },
  },

  {
    Header: '#Products',
    accessor: 'product_code',
    cellClassName: 'summary_product_code',
  },
  {
    Header: 'Next week MD',
    accessor: 'overall_next_week',
    cellClassName: 'summary_next_week',
  },
  {
    Header: 'Current week MD',
    accessor: 'overall_current_week',
    cellClassName: 'summary_current_week',
  },
  {
    Header: 'MD Difference',
    accessor: 'overall_md_diff',
    cellClassName: 'summary_difference',
  },
  {
    Header: 'Sell through %',
    accessor: 'overall_sell_through',
    cellClassName: 'summary_sell_through',
    Cell: (props) => <div> {toPercentageWithDecimalDraft(props)} </div>,
  },

  {
    Header: 'Markdown %',
    accessor: 'overall_markdown_perc',
    cellClassName: 'summary_markdown_perc',
    Cell: (props) => <div> {toPercentage(props)} </div>,
  },
  {
    Header: 'Margin %',
    accessor: 'overall_margin_perc',
    cellClassName: 'summary_margin_perc',
    metric: true,
    Cell: (props) => <div> {toPercentage(props)} </div>,
  },
  {
    Header: 'Margin $',
    accessor: 'overall_margin$',
    cellClassName: 'summary_margin',
    hideColumn: true,
    Cell: (props) => <div> {toDollar(props)} </div>,
  },
  {
    Header: 'AUM $',
    accessor: 'overall_aum',
    cellClassName: 'summary_aum',
    hideColumn: true,
    Cell: (props) => <div> {toDollar(props)} </div>,
  },
  {
    Header: 'Revenue $',
    accessor: 'overall_revenue$',
    cellClassName: 'summary_revenue',
    hideColumn: true,
    Cell: (props) => <div> {toDollar(props)} </div>,
  },
  {
    Header: 'PVH Planned units',
    accessor: 'overall_pvh_units',
    cellClassName: 'summary_pvh_units',
    hideColumn: true,
    Cell: (props) => <div> {toUnit(props)} </div>,
  },
  {
    Header: 'Markdown units',
    accessor: 'overall_markdown_units',
    cellClassName: 'summary_markdown_units',
    hideColumn: true,
    Cell: (props) => <div> {toUnit(props)} </div>,
  },
  {
    Header: 'AUR $',
    accessor: 'overall_aur',
    cellClassName: 'summary_aur',
    hideColumn: true,
    Cell: (props) => <div> {toDollar(props)} </div>,
  },
];
//weekToBinColDef contains accessor in granular level,
//in Cell method have added a condition to display aggregated level data
//if u add a new column make sure to copy the same
//only bins will be expandable
export const weekToBinColDef = [
