      return <div> {toPercentage({ value: value })} </div>;
    },
  },
  {
    Header: 'Markdown %',
    accessor: 'markdown_perc',
    cellClassName: 'summary_markdown_perc',
    Cell: (props) => {
      let value = props.row.original.subRows
        ? props.row.original.overall_week_markdown_perc
        : props.value;
      return <div> {toPercentage({ value })} </div>;
    },
  },

  {
    Header: 'Margin %',
    accessor: 'margin_perc',
    cellClassName: 'summary_margin_perc',
    metric: true,
    Cell: (props) => {
      let value = props.row.original.subRows
        ? props.row.original.overall_week_margin_perc
        : props.value;
      return <div> {toPercentage({ value })} </div>;
    },
  },
  {
    Header: 'Margin $',
    accessor: 'margin$',
    cellClassName: 'summary_margin',
    hideColumn: true,
    Cell: (props) => {
      let value = props.row.original.subRows
        ? props.row.original.overall_week_margin$
        : props.value;
      return <div> {toDollar(value)} </div>;
    },
  },
  {
    Header: 'AUM $',
    accessor: 'aum',
    cellClassName: 'summary_aum',
    hideColumn: true,
    Cell: (props) => {
      let value = props.row.original.subRows ? props.row.original.overall_week_aum : props.value;
      return <div> {toDollar({ value })} </div>;
    },
  },
  {
    Header: 'Revenue $',
    accessor: 'revenue$',
    cellClassName: 'summary_revenue',
    hideColumn: true,
    Cell: (props) => {
      let value = props.row.original.subRows
        ? props.row.original.overall_week_revenue$
        : props.value;
      return <div> {toDollar({ value })} </div>;
    },
  },
  {
    Header: 'PVH Planned units',
    accessor: 'pvh_units',
    cellClassName: 'summary_pvh_units',
    hideColumn: true,
    Cell: (props) => {
      let value = props.row.original.subRows
        ? props.row.original.overall_week_pvh_units
        : props.value;
      return <div> {toUnit({ value })} </div>;
    },
  },
  {
    Header: 'Markdown units',
    accessor: 'markdown_units',
    cellClassName: 'summary_markdown_units',
    hideColumn: true,
    Cell: (props) => {
      let value = props.row.original.subRows
        ? props.row.original.overall_week_markdown_units
        : props.value;
      return <div> {toUnit({ value })} </div>;
    },
  },
  {
    Header: 'AUR $',
    accessor: 'aur',
    cellClassName: 'summary_aur',
    hideColumn: true,
    Cell: (props) => {
      let value = props.row.original.subRows ? props.row.original.overall_week_aur : props.value;
      return <div> {toDollar({ value })} </div>;
    },
  },
];
