import React, { Component } from 'react';
import Select from '../../../services/select';
import Filters from '../../../services/filters';
import '../../../index.css';
import DataTable from '../../../services/reactTable';
import { connect } from 'react-redux';
import colDefs from '../StoreConfigContainers/StoreConfigColDefs';
import StoreConfigRowData from '../StoreConfigContainers/StoreConfigRowData';
const deptData = [
  { label: 'Group 1', value: 'Group 1' },
  { label: 'Group 2', value: 'Group 2' },
  { label: 'Group 3', value: 'Group 3' },
];

export class StoreConfig extends Component {
  state = {
    storeGroup: '',
  };
  header = [
    {
      Header: 'Store Number',
      accessor: 'store_number',
    },
    {
      Header: 'Store Name',
      accessor: 'store_name',
    },
    {
      Header: 'Chain Description',
      accessor: 'chain_description',
    },
    {
      Header: 'Region Description',
      accessor: 'region_description',
    },
  ];
  onStoreGroupChangeHandler = (value) => {
    this.setState({
      storeGroup: value,
    });
  };

  clearStoreFilter = () => {
    this.setState({
      storeGroup: '',
    });
  };

  render() {
    console.log(StoreConfigRowData, 'StoreConfigColDefs');
    return (
      <div className="card p-2 mt-4">
        <div className="row justify-content-between mt-3 mb-2">
          <div className="row">
            <div className="col-md-6">
              <label className="mr-1">Store group</label>
              <Select
                id="store-group-select"
                placeholder="Select Store Group"
                options={deptData}
                value={this.state.storeGroup}
                onChange={this.onStoreGroupChangeHandler}
              />
            </div>
            <div className="col-md-4 pt-1">
              <button className="btn btn-primary">
                <span>
                  <i className="fa fa-filter"></i>&nbsp; Filter
                </span>
              </button>
              <button
                onClick={this.clearStoreFilter}
                className="btn btn-primary  btn-filter-reset ml-2">
                <span>
                  <i className="fa fa-repeat"></i>&nbsp; Reset
                </span>
              </button>
            </div>
          </div>
        </div>
        {this.props.userRole !== 'Admin' && (
          <div className="col-md-2 pt-1 mr-3 text-right">
            <button
              className="btn btn-primary"
              onClick={() => this.props.onCreateStoreGroupHandler()}>
              Create store group
            </button>
          </div>
        )}
        <hr />
        <Filters
          hierarchy={['hierarchy1', 'hierarchy2', 'hierarchy3', 'hierarchy4']}
          // onFilter={this.onFilter}
        />
        <hr />
        <div className="row">
          <div className="col-md-2 offset-md-2">No of stores: 190</div>
          {/* <div className="col-md-2">Overall QC: 0.23</div> */}
          {this.props.userRole !== 'Admin' && (
            <div className="col-md-7 text-right">
              <button
                className="btn btn-primary"
                onClick={() => this.props.onCreateStoreGroupHandler(true)}>
                Edit store group
              </button>
            </div>
          )}
        </div>
        <div style={{ height: '370px', width: '65%', margin: '1.5rem auto' }}>
          <DataTable columns={this.header} data={StoreConfigRowData} />
        </div>
      </div>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    // selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    // selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    // selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    // selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    // selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
  };
};
const mapActionsToProps = {
  // getReportingData: reportingActions.getReportingData,
};
export default connect(mapStateToProps, mapActionsToProps)(StoreConfig);
