      selectedSKU: null,
      selectedSubCategory: null,
      selectedBrand: null,
      loader: false,
      disableFlag: true,
      tableData: [],
      showCadenceData: false,
      frameworkComponents: {
        childMessageRenderer: ChildMessageRenderer,
        ChildActionRenderer: ChildActionRenderer,
      },
    };
  }

  componentDidMount() {
    this.props.getCockpitData();
    this.props.getAllFilters();
    if (this.props.cascadingFilterData) {
      this.setState({
        disableFlag: false,
        selectedCategory: this.props.cascadingFilterData.SelectedCategory,
        selectedSubCategory: this.props.cascadingFilterData.SelectedSubCategory,
        selectedBrand: this.props.cascadingFilterData.SelectedBrand,
      });
    }
    rowData.map((items) => {
      items.getfun = this.showCadenceData;
      return items;
    });
    this.setState({
      tableData: rowData,
    });
  }

  onRefresh = () => {
    this.reloadData();
  };
  reloadData = () => {
    this.setState({
      loader: false,
      selectedCategory: null,
      selectedDepartment: null,
      selectedSKU: null,
      selectedSubCategory: null,
      selectedBrand: null,
    });
  };

  filterData() {
    var department = this.state.selectedDepartment
      ? this.state.selectedDepartment.value
      : null;
    var category = this.state.selectedCategory
      ? this.state.selectedCategory.value
      : null;
    var sku = this.state.selectedSKU ? this.state.selectedSKU.value : null;
    var subCategory = this.state.selectedSubCategory
      ? this.state.selectedSubCategory.value
      : null;

    var queryArray = null;
    var queryArray1 = null;
    var queryArray2 = null;
    var queryArray3 = null;
    var queryArray4 = null;
    if (department) {
      queryArray = "department=" + department;
      queryArray1 = department;
    }
    if (category) {
      queryArray = "category=" + category;
      queryArray2 = category;
    }
    if (sku) {
      queryArray = "sku=" + sku;
      queryArray4 = sku;
    }
    if (subCategory) {
      queryArray = "subCategory=" + subCategory;
      queryArray3 = subCategory;
    }
  }

  onDropdownchange(tag, value) {
    if (tag === "department") {
      this.setState({
        selectedDepartment: value,
      });
    }
    if (tag === "category") {
      this.setState({
        selectedCategory: value,
        disableFlag: false,
      });
    }

    if (tag === "subCategory") {
      this.setState({
        selectedSubCategory: value,
      });
    }

    if (tag === "sku") {
      this.setState({
        selectedSKU: value,
      });
    }
    if (tag === "brand") {
      this.setState({
        selectedBrand: value,
      });
    }
  }

  onGridReady = (params) => {
    this.gridApi = params.api;
    this.gridColumnApi = params.columnApi;
    if (this.props.autoSizeCol) {
      params.api.sizeColumnsToFit();
    }
    this.gridApi.forEachLeafNode((node) => {
      if (node.data.indicator === "URGENT") {
        node.setSelected(true);
      }
    });
  };
  showCadenceData = () => {
    this.setState(
      {
        showCadenceData: false,
      },
      () => {
        setTimeout(() => {
          this.setState({
            showCadenceData: true,
          });
        }, 200);
      }
    );
  };
  render() {
    let custGroupArray = [],
      businessDescArray = [],
      shopArray = [],
      skuArray = [],
      brandArray = [];
    if (this.props.markdownFilter && this.props.markdownFilter.data) {
      this.props.markdownFilter.data.category.map((filterData) => {
        custGroupArray.push({
          value: filterData.value,
          label: filterData.label,
        });
      });

      this.props.markdownFilter.data.category.map((filterData) => {
        businessDescArray.push({
          value: filterData.value,
          label: filterData.label,
        });
      });

      this.props.markdownFilter.data.subCategory.map((filterData) => {
        shopArray.push({
          value: filterData.value,
          label: filterData.label,
        });
      });

      this.props.markdownFilter.data.skuGroup.map((filterData) => {
        skuArray.push({
          value: filterData.id,
          label: filterData.sku_group_id,
        });
      });

      this.props.markdownFilter.data.brand.map((filterData) => {
        brandArray.push({
          value: filterData.value,
          label: filterData.label,
        });
      });
    }

    return (
      <div>
        {/* <PageLoader loader={this.props.loader || this.state.loader}> */}
        <div className="">
          <div
            className="card CalendarMain promoCalendarDiv1"
            id="promocalendar-maincontainer"
          >
            <h4 className="promoCal-brdcrm mb-10">
              Markdown Optimization /Calendar/{" "}
              <span className="font-bold color-blue">Recommended</span>
            </h4>
            <hr style={{ marginBottom: "1px" }} />
            <div className="row promocal-subdiv">
              <div className="col-md-10 SelectRow">
                <div className="row">
                  <div className="col-md-2">
                    <label className="input-label">Sub Department</label>
                    <Select
                      id="businessDescSelect"
                      placeholder="Sub Department"
                      options={businessDescArray}
                      onChange={(e) => this.onDropdownchange("category", e)}
                      value={this.state.selectedCategory}
                      isDisabled={this.state.disableFlag}
                    />
                  </div>
                  <div className="col-md-2">
                    <label className="input-label">Class</label>
                    <Select
                      id="shopSelect"
                      placeholder="Class"
                      options={shopArray}
                      onChange={(e) => this.onDropdownchange("subCategory", e)}
                      value={this.state.selectedSubCategory}
                      isDisabled={this.state.disableFlag}
                    />
                  </div>
                  <div className="col-md-2">
                    <label className="input-label">Brand</label>
                    <Select
                      id="brandSelect"
                      placeholder="Brand"
                      options={brandArray}
                      onChange={(e) => this.onDropdownchange("brand", e)}
                      value={this.state.selectedBrand}
                      isDisabled={this.state.disableFlag}
                    />
                  </div>
                </div>
              </div>
              <div
                className="col-md-2 text-right"
                style={{ marginTop: "25px" }}
              >
                <button
                  className="btn btn-primary filterBtn"
                  onClick={() => this.filterData()}
                >
                  <span>
                    <i class="fa fa-filter" />
                    &nbsp; Filter
                  </span>
                </button>
                <button
                  className="btn btn-primary resetButton"
                  onClick={() => this.onRefresh()}
                >
                  <span>
                    <i class="fa fa-repeat" />
                    &nbsp; Reset
                  </span>
                </button>
              </div>
            </div>
            <hr className="dotted-line" />
            <div className="" style={{ padding: "10px 9px" }}>
              <div className="promolist-header">
                <label className="pipelineLabel">Sub Category List</label>
              </div>
              <button className="btn btn-primary iconbutton download-btn">
                <span>
                  <i class="fa fa-download" aria-hidden="true" />
                </span>
              </button>

              <a href="/vis/Markdown/calender">
                {" "}
                <button type="button" class="btn btn-primary pull-right">
                  Add Markdown
                </button>
              </a>
            </div>

            <div className="pipelineGrid" style={{ padding: "0px 16px" }}>
              <div
                id="myGridForPipeline"
                style={{
                  height: "250px",
                  width: "100%",
                }}
                className="ag-theme-balham"
              >
                <AgGridReact
                  columnDefs={RecommendedColdef.columnDefs}
                  rowData={this.state.tableData}
                  frameworkComponents={this.state.frameworkComponents}
                  onGridReady={this.onGridReady}
                  rowSelection="multiple"
                  paginationAutoPageSize={true}
                  pagination={true}
                />
              </div>
            </div>
          </div>
        </div>
        {this.state.showCadenceData ? (
          <div className="card">
            <CadenceChartComp selectedCadenceSet={sampleData} />
            <div className="mt-20">
              <div className="row mt-20 j-c-even">
                <div className="col-md-3">
                  <AutopromoCharts
                    title="Quanity"
                    width="16.5rem"
                    height="328px"
                    data={sampleData.overall.inventory}
                  />
                </div>
                <div className="col-md-3">
                  <AutopromoCharts
                    title="Revenue"
                    width="16.5rem"
                    height="328px"
                    data={sampleData.overall.revenue}
                  />
                </div>
                <div className="col-md-3">
                  <AutopromoCharts
                    title="Margin"
                    width="16.5rem"
                    height="328px"
                    data={sampleData.overall.margin}
                  />
                </div>
              </div>
            </div>
          </div>
        ) : null}
        {/* </PageLoader> */}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    // authentication: state.userLoginDetails,
    loader: state.calendar.loader,
    markdownFilter: state.markDownFilter,
    cascadingFilterData: state.markDownFilter.cascadingFilters,
    cockpitData: state.cockpitData,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getCockpitData: (val) => dispatch(getCockpitData(val)),
    getAllFilters: (val) => dispatch(getAllFilters(val)),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
  // mapActionToProps
)(withRouter(Recommended));
