  comparisonDataHandler = () => {
    let { overallCadenceData } = this.state;
    let comparisonTabs = _.map(
      overallCadenceData[overallCadenceData.length - 1],
      (item) => item.scenarios
    );
    if (this.state.cadenceConstraintsVal.value === 'sku') {
      let { skuLabelCadenceData } = this.state;
      comparisonTabs = _.map(
        skuLabelCadenceData[skuLabelCadenceData.length - 1],
        (item) => item.scenarios
      );
    }
    let comparisonData = _.cloneDeep(this.props.comparisonData);

    _.map(Object.keys(comparisonData), (item) => {
      if (!comparisonTabs.includes(item)) {
        delete comparisonData[item];
      }
    });
    return comparisonData;
  };
  getTabTag = (tabTitle, index) => {
    if (tabTitle === 'Comparison') {
      if (this.state.cadenceConstraintsVal.value === 'sku') {
        let optimalData = this.state.skuLabelCadenceData[0];
        let options =
          optimalData &&
          optimalData.map((data) => {
            return { label: data.SKU_Id, value: data.SKU_Id };
          });
        let rowSpan =
          this.state.overallCadenceData[this.state.overallCadenceData.length - 1].length /
          this.state.overallCadenceData[0].length;
        return (
          <div>
            <ComparisonTable
              cadenceConstraintsVal={this.state.cadenceConstraintsVal.value}
              comparisonCheckbox={(comparisonRowVal, index) =>
                this.comparisonCheckbox(comparisonRowVal, index)
              }
              data={this.state.skuLabelCadenceData[index]}
              updatedData={this.onComparisonInputChange}
              offerType={this.state.offerType.value}
              skuDropDownOptions={options}
              rowSpan={rowSpan}
              daterangeOrWeek={this.props.daterangeOrWeek}
            />
          </div>
        );
      } else {
        return (
          <ComparisonTable
            daterangeOrWeek={this.props.daterangeOrWeek}
            cadenceConstraintsVal={this.state.cadenceConstraintsVal.value}
            comparisonCheckbox={(comparisonRowVal, index) =>
              this.comparisonCheckbox(comparisonRowVal, index)
